        role: "admin",
      }).returning();

      const [supervisor] = await db.insert(users).values({
        username: "maria.santos",
        password: "senha123",
        name: "Maria Santos",
        email: "maria.santos@empresa.com",
        role: "supervisor",
      }).returning();

      const [colaborador1] = await db.insert(users).values({
        username: "carlos.oliveira",
        password: "senha123",
