export default function KanbanBoard() {
  const [draggedTicket, setDraggedTicket] = useState<any>(null);
  const [finalizationModal, setFinalizationModal] = useState<{
    isOpen: boolean;
    ticket: any | null;
  }>({ isOpen: false, ticket: null });
  const [searchTerm, setSearchTerm] = useState('');
  const [filterBy, setFilterBy] = useState('all');
  const [viewMode, setViewMode] = useState<'kanban' | 'list'>('kanban');
  const [statusFilter, setStatusFilter] = useState('all');
  const [priorityFilter, setPriorityFilter] = useState('all');
  const [departmentFilter, setDepartmentFilter] = useState('all');
  const [assigneeFilter, setAssigneeFilter] = useState('all');
  const [showAdvancedFilters, setShowAdvancedFilters] = useState(false);

  // Fetch real tickets from API
  const { data: tickets = [], refetch: refetchTickets } = useQuery<any[]>({
    queryKey: ['/api/tickets'],
  });

  // Fetch current user for role checking
  const { data: users } = useQuery<any[]>({
    queryKey: ['/api/users'],
  });
  const currentUser = users?.find(u => u.role === 'admin') || users?.[0];

  // Buscar configura√ß√µes de status e prioridade
  const { data: statusConfigs } = useQuery<any[]>({
    queryKey: ['/api/config/status'],
  });

  const { data: priorityConfigs } = useQuery<any[]>({
    queryKey: ['/api/config/priority'],
  });

  // Define columns with dynamic counts using database configurations
  const columns = statusConfigs?.map(status => ({
    id: status.value,
    title: status.name.toUpperCase(),
    color: status.color === '#dc2626' ? 'bg-red-500' : 
           status.color === '#f59e0b' ? 'bg-yellow-500' :
           status.color === '#10b981' ? 'bg-green-500' : 'bg-gray-500',
    headerColor: status.color === '#dc2626' ? 'bg-red-500' : 
                 status.color === '#f59e0b' ? 'bg-yellow-500' :
                 status.color === '#10b981' ? 'bg-green-500' : 'bg-gray-500',
    count: tickets.filter(t => t.status === status.value).length
  })) || [
    { id: 'open', title: 'ABERTO', color: 'bg-red-500', headerColor: 'bg-red-500', count: 0 },
    { id: 'in_progress', title: 'EM PROGRESSO', color: 'bg-green-500', headerColor: 'bg-green-500', count: 0 },
    { id: 'on_hold', title: 'PAUSADO', color: 'bg-yellow-500', headerColor: 'bg-yellow-500', count: 0 },
    { id: 'resolved', title: 'RESOLVIDO', color: 'bg-gray-500', headerColor: 'bg-gray-500', count: 0 }
  ];

  // Check for URL parameters on component mount
  React.useEffect(() => {
    const urlParams = new URLSearchParams(window.location.search);
    const filter = urlParams.get('filter');

    if (filter) {
      switch (filter) {
        case 'open':
          setStatusFilter('Aberto');
          setShowAdvancedFilters(true);
          break;
        case 'resolved':
          setStatusFilter('Resolvido');
          setShowAdvancedFilters(true);
          break;
        case 'critical':
          setPriorityFilter('Alta');
          setShowAdvancedFilters(true);
          break;
        case 'all':
        default:
          // Keep all filters as 'all'
          break;
      }
    }
  }, []);

  const handleDragStart = (e: any, ticket: any) => {
    setDraggedTicket(ticket);
    e.dataTransfer.effectAllowed = 'move';
  };

  const handleDragOver = (e: any) => {
    e.preventDefault();
    e.dataTransfer.dropEffect = 'move';
  };

  const handleDrop = (e: any, status: string) => {
    e.preventDefault();
    if (draggedTicket && draggedTicket.status !== status) {
      // Se estiver movendo para "Resolvido", mostrar modal de finaliza√ß√£o
      if (status === 'resolved') {
        setFinalizationModal({ isOpen: true, ticket: draggedTicket });
        setDraggedTicket(null);
        return;
      }

      // Para outros status, fazer update direto via API
      fetch(`/api/tickets/${draggedTicket.id}`, {
        method: 'PATCH',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify({ status }),
      }).then(() => {
        refetchTickets();
      }).catch(error => {
        console.error('Erro ao atualizar status:', error);
      });
      setDraggedTicket(null);
    }
  };

  const getPriorityColor = (priority: string) => {
    const config = priorityConfigs?.find(p => p.value === priority || p.name === priority);
    if (config?.color) {
      const colorMap: Record<string, string> = {
        '#dc2626': 'bg-red-100 text-red-800 border-red-200',
        '#f59e0b': 'bg-orange-100 text-orange-800 border-orange-200',
        '#3b82f6': 'bg-blue-100 text-blue-800 border-blue-200',
        '#10b981': 'bg-green-100 text-green-800 border-green-200',
      };
      return colorMap[config.color] || 'bg-gray-100 text-gray-800 border-gray-200';
    }
    return 'bg-gray-100 text-gray-800 border-gray-200';
  };

  const getProgressColor = (progress: number, status: string) => {
    if (status === 'Resolvido') return 'bg-green-500';
    if (status === 'Atrasado') return 'bg-red-500';
    if (status === 'Pausado') return 'bg-yellow-500';
    return 'bg-blue-500';
  };

  // Fun√ß√µes SLA
  const getSLAStatusColor = (ticket: any) => {
    const slaTime = getSLATimeInHours(ticket);
    const timeElapsed = getTimeElapsedInHours(ticket.createdAt);
    const percentageUsed = (timeElapsed / slaTime) * 100;

    if (percentageUsed >= 100) return 'bg-red-500'; // Vencido
    if (percentageUsed >= 80) return 'bg-orange-500'; // Cr√≠tico
    if (percentageUsed >= 60) return 'bg-yellow-500'; // Aten√ß√£o
    return 'bg-green-500'; // Ok
  };

  const getSLATimeInHours = (ticket: any) => {
    // SLA baseado na prioridade
    switch (ticket.priority?.toLowerCase()) {
      case 'cr√≠tica': return 4; // 4 horas
      case 'alta': return 24; // 1 dia
      case 'm√©dia': return 72; // 3 dias
      case 'baixa': return 168; // 7 dias
      default: return 24;
    }
  };

  const getTimeElapsedInHours = (createdAt: string) => {
    const now = new Date();
    const created = new Date(createdAt);
    return Math.abs(now.getTime() - created.getTime()) / (1000 * 60 * 60);
  };

  const getSLATimeRemaining = (ticket: any) => {
    const slaTime = getSLATimeInHours(ticket);
    const timeElapsed = getTimeElapsedInHours(ticket.createdAt);
    const remaining = slaTime - timeElapsed;

    if (remaining <= 0) return 'Vencido';
    if (remaining < 1) return `${Math.ceil(remaining * 60)}min`;
    if (remaining < 24) return `${Math.ceil(remaining)}h`;
    return `${Math.ceil(remaining / 24)}d`;
  };

  const getSLAStatus = (ticket: any) => {
    const slaTime = getSLATimeInHours(ticket);
    const timeElapsed = getTimeElapsedInHours(ticket.createdAt);
    const percentageUsed = (timeElapsed / slaTime) * 100;

    if (percentageUsed >= 100) return 'Vencido';
    if (percentageUsed >= 80) return 'Cr√≠tico';
    if (percentageUsed >= 60) return 'Aten√ß√£o';
    return 'Ok';
  };

  const filteredTickets = tickets.filter(ticket => {
    const matchesSearch = (ticket.subject || '').toLowerCase().includes(searchTerm.toLowerCase()) ||
                         (ticket.ticketNumber || '').toLowerCase().includes(searchTerm.toLowerCase()) ||
                         (ticket.assignedToUser?.name || '').toLowerCase().includes(searchTerm.toLowerCase()) ||
                         (ticket.createdByUser?.name || '').toLowerCase().includes(searchTerm.toLowerCase()) ||
                         (ticket.department?.name || '').toLowerCase().includes(searchTerm.toLowerCase());

    const matchesStatus = statusFilter === 'all' || ticket.status === statusFilter;
    const matchesPriority = priorityFilter === 'all' || ticket.priority === priorityFilter;
    const matchesDepartment = departmentFilter === 'all' || ticket.department?.name === departmentFilter;
    const matchesAssignee = assigneeFilter === 'all' || ticket.assignedToUser?.name === assigneeFilter;

    // Legacy filter compatibility
    const matchesLegacyFilter = filterBy === 'all' || 
      (filterBy === 'alta' && ticket.priority === 'Alta') ||
      (filterBy === 'atrasado' && ticket.status === 'Atrasado');

    return matchesSearch && matchesStatus && matchesPriority && matchesDepartment && matchesAssignee && matchesLegacyFilter;
  });

  // Get unique values for filter options
  const uniqueStatuses = Array.from(new Set(tickets.map(t => t.status).filter(Boolean)));
  const uniquePriorities = Array.from(new Set(tickets.map(t => t.priority).filter(Boolean)));
  const uniqueDepartments = Array.from(new Set(tickets.map(t => t.department?.name).filter(Boolean)));
  const uniqueAssignees = Array.from(new Set(tickets.map(t => t.assignedToUser?.name).filter(Boolean)));

  const clearAllFilters = () => {
    setSearchTerm('');
    setFilterBy('all');
    setStatusFilter('all');
    setPriorityFilter('all');
    setDepartmentFilter('all');
    setAssigneeFilter('all');
  };

  const handleDeleteTicket = async (ticketId: string) => {
    if (!currentUser || currentUser.role !== 'admin') {
      console.warn('Apenas administradores podem excluir tickets');
      return;
    }

    if (window.confirm('Tem certeza que deseja excluir este ticket? Esta a√ß√£o n√£o pode ser desfeita.')) {
      try {
        const response = await fetch(`/api/tickets/${ticketId}`, {
          method: 'DELETE',
          headers: {
            'Content-Type': 'application/json',
          },
        });

        if (response.ok) {
          // Refetch dos tickets para atualizar lista
          refetchTickets();
          console.log('Ticket exclu√≠do com sucesso');
        } else {
          console.error('Erro ao excluir ticket');
        }
      } catch (error) {
        console.error('Erro na requisi√ß√£o:', error);
      }
    }
  };

  const handleFinalizeTicket = (ticket: any) => {
    setFinalizationModal({ isOpen: true, ticket });
  };

  const handleFinalizationConfirm = async (finalizationData: any) => {
    if (!finalizationModal.ticket) return;

    try {
      // Fazer chamada √† API para finalizar o ticket
      const response = await fetch(`/api/tickets/${finalizationModal.ticket.id}/finalize`, {
        method: 'PATCH',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify({
          status: 'Resolvido',
          finalizationData: finalizationData,
          progress: 100
        }),
      });

      if (response.ok) {
        // Refetch dos tickets para atualizar com dados reais do banco
        refetchTickets();

        console.log('Ticket finalizado com sucesso');
      } else {
        console.error('Erro ao finalizar ticket no servidor');
      }

      setFinalizationModal({ isOpen: false, ticket: null });
    } catch (error) {
      console.error('Erro ao finalizar ticket:', error);
    }
  };

  return (
    <div className="p-6 space-y-6 bg-gray-50 min-h-screen">
      {/* Header */}
      <div className="bg-white rounded-lg shadow-sm p-4">
        <div className="flex items-center justify-between mb-4">
          <div>
            <h1 className="text-2xl font-bold text-gray-900">Listagem de Tickets</h1>
            <p className="text-gray-600">
              {viewMode === 'kanban' ? 'Gerencie tickets em formato Kanban' : 'Visualize tickets em formato de lista'}
            </p>
          </div>
          <div className="flex items-center space-x-2">
            {/* View Mode Toggle */}
            <div className="flex bg-gray-100 rounded-lg p-1">
              <Button
                variant={viewMode === 'kanban' ? 'default' : 'ghost'}
                size="sm"
                onClick={() => setViewMode('kanban')}
                className="px-3"
              >
                <Grid3X3 className="w-4 h-4 mr-2" />
                Kanban
              </Button>
              <Button
                variant={viewMode === 'list' ? 'default' : 'ghost'}
                size="sm"
                onClick={() => setViewMode('list')}
                className="px-3"
              >
                <List className="w-4 h-4 mr-2" />
                Lista
              </Button>
            </div>
            <Button variant="outline" size="sm">
              <Filter className="w-4 h-4 mr-2" />
              Filtro Avan√ßado
            </Button>
          </div>
        </div>

        {/* Search and Quick Filters */}
        <div className="space-y-4">
          <div className="flex items-center space-x-4">
            <div className="relative flex-1 max-w-md">
              <Search className="absolute left-3 top-1/2 transform -translate-y-1/2 text-gray-400 w-4 h-4" />
              <Input 
                placeholder="Buscar por ticket, t√≠tulo, respons√°vel, solicitante..." 
                className="pl-10"
                value={searchTerm}
                onChange={(e) => setSearchTerm(e.target.value)}
              />
            </div>

            <Select value={statusFilter} onValueChange={setStatusFilter}>
              <SelectTrigger className="w-40">
                <SelectValue placeholder="Status" />
              </SelectTrigger>
              <SelectContent>
                <SelectItem value="all">Todos os Status</SelectItem>
                {uniqueStatuses.map(status => (
                  <SelectItem key={status} value={status}>{status}</SelectItem>
                ))}
              </SelectContent>
            </Select>

            <Select value={priorityFilter} onValueChange={setPriorityFilter}>
              <SelectTrigger className="w-40">
                <SelectValue placeholder="Prioridade" />
              </SelectTrigger>
              <SelectContent>
                <SelectItem value="all">Todas as Prioridades</SelectItem>
                {uniquePriorities.map(priority => (
                  <SelectItem key={priority} value={priority}>{priority}</SelectItem>
                ))}
              </SelectContent>
            </Select>

            <Button 
              variant="outline" 
              size="sm"
              onClick={() => setShowAdvancedFilters(!showAdvancedFilters)}
            >
              <Filter className="w-4 h-4 mr-2" />
              Filtros Avan√ßados
              <ChevronDown className={`w-4 h-4 ml-1 transition-transform ${showAdvancedFilters ? 'rotate-180' : ''}`} />
            </Button>

            {(searchTerm || statusFilter !== 'all' || priorityFilter !== 'all' || departmentFilter !== 'all' || assigneeFilter !== 'all') && (
              <Button variant="ghost" size="sm" onClick={clearAllFilters}>
                Limpar Filtros
              </Button>
            )}
          </div>

          {/* Advanced Filters */}
          {showAdvancedFilters && (
            <div className="bg-gray-50 p-4 rounded-lg border border-gray-200">
              <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-4 gap-4">
                <div>
                  <Label className="text-sm font-medium text-gray-700 mb-2 block">Departamento</Label>
                  <Select value={departmentFilter} onValueChange={setDepartmentFilter}>
                    <SelectTrigger>
                      <SelectValue placeholder="Todos os Departamentos" />
                    </SelectTrigger>
                    <SelectContent>
                      <SelectItem value="all">Todos os Departamentos</SelectItem>
                      {uniqueDepartments.map(dept => (
                        <SelectItem key={dept} value={dept}>{dept}</SelectItem>
                      ))}
                    </SelectContent>
                  </Select>
                </div>

                <div>
                  <Label className="text-sm font-medium text-gray-700 mb-2 block">Respons√°vel</Label>
                  <Select value={assigneeFilter} onValueChange={setAssigneeFilter}>
                    <SelectTrigger>
                      <SelectValue placeholder="Todos os Respons√°veis" />
                    </SelectTrigger>
                    <SelectContent>
                      <SelectItem value="all">Todos os Respons√°veis</SelectItem>
                      {uniqueAssignees.map(assignee => (
                        <SelectItem key={assignee} value={assignee}>{assignee}</SelectItem>
                      ))}
                    </SelectContent>
                  </Select>
                </div>

                <div>
                  <Label className="text-sm font-medium text-gray-700 mb-2 block">Filtros R√°pidos</Label>
                  <Select value={filterBy} onValueChange={setFilterBy}>
                    <SelectTrigger>
                      <SelectValue placeholder="Filtros Especiais" />
                    </SelectTrigger>
                    <SelectContent>
                      <SelectItem value="all">Todos</SelectItem>
                      <SelectItem value="alta">Alta Prioridade</SelectItem>
                      <SelectItem value="atrasado">Atrasados</SelectItem>
                    </SelectContent>
                  </Select>
                </div>

                <div className="flex flex-col justify-end">
                  <div className="text-sm text-gray-600 bg-white p-3 rounded border">
                    <strong>{filteredTickets.length}</strong> de <strong>{tickets.length}</strong> tickets
                  </div>
                </div>
              </div>
            </div>
          )}
        </div>
      </div>

      {/* Content Based on View Mode */}
      {viewMode === 'kanban' ? (
        /* Kanban Board */
        <div className="grid grid-cols-1 lg:grid-cols-4 gap-6">
          {columns.map((column) => (
            <div key={column.id} className="space-y-4">
              {/* Column Header */}
              <div className={`${column.headerColor} text-white p-4 rounded-lg flex items-center justify-between shadow-md`}>
                <span className="font-bold text-sm tracking-wide">{column.title}</span>
                <div className="bg-white bg-opacity-20 rounded-full w-8 h-8 flex items-center justify-center">
                  <span className="text-sm font-bold">
                    {filteredTickets.filter(t => t.status === column.id).length}
                  </span>
                </div>
              </div>

              {/* Drop Zone */}
              <div 
                className="space-y-3 min-h-[600px] p-2 rounded-lg"
                onDragOver={handleDragOver}
                onDrop={(e) => handleDrop(e, column.id)}
              >
                {filteredTickets
                  .filter(ticket => ticket.status === column.id)
                  .map((ticket) => (
                    <TicketModal key={ticket.id} ticket={ticket} onUpdate={(updatedTicket: any) => {
                      // Atualizar lista ser√° feita pela revalida√ß√£o de query
                    }}>
                      <Card 
                        className="cursor-pointer hover:shadow-lg transition-all duration-200 border-0 shadow-sm bg-white"
                        draggable
                        onDragStart={(e) => handleDragStart(e, ticket)}
                      >
                      <CardContent className="p-4">
                        <div className="space-y-3">
                          {/* Header */}
                          <div className="flex items-center justify-between">
                            <span className="text-xs font-bold text-gray-600">{ticket.ticketNumber}</span>
                            <div className="flex items-center space-x-1">
                              {/* Bot√£o Finalizar (apenas para tickets n√£o resolvidos) */}
                              {ticket.status !== 'resolved' && (
                                <Button 
                                  variant="ghost" 
                                  size="sm" 
                                  className="w-6 h-6 p-0 text-green-600 hover:text-green-700 hover:bg-green-50"
                                  onClick={(e) => {
                                    e.preventDefault();
                                    e.stopPropagation();
                                    handleFinalizeTicket(ticket);
                                  }}
                                  title="Finalizar Ticket"
                                >
                                  <svg className="w-4 h-4" fill="none" stroke="currentColor" viewBox="0 0 24 24">
                                    <path strokeLinecap="round" strokeLinejoin="round" strokeWidth="2" d="M5 13l4 4L19 7"></path>
                                  </svg>
                                </Button>
                              )}
                              <Button variant="ghost" size="sm" className="w-6 h-6 p-0">
                                <MoreHorizontal className="w-4 h-4 text-gray-400" />
                              </Button>
                            </div>
                          </div>

                          {/* Title */}
                          <div>
                            <h3 className="font-bold text-sm text-gray-900 leading-tight mb-1">
                              {ticket.subject}
                            </h3>
                            <p className="text-xs text-gray-600 line-clamp-2">
                              {ticket.description}
                            </p>
                          </div>

                          {/* Assignee and Date */}
                          <div className="flex items-center justify-between">
                            <div className="flex items-center space-x-2">
                              <Avatar className="w-6 h-6">
                                <AvatarFallback className="text-xs bg-blue-100 text-blue-600">
                                  {ticket.assignedToUser?.name?.charAt(0) || 'U'}
                                </AvatarFallback>
                              </Avatar>
                              <span className="text-xs text-gray-600">{ticket.assignedToUser?.name || 'N√£o atribu√≠do'}</span>
                            </div>
                            <div className="text-xs text-gray-500">
                              {new Date(ticket.createdAt).toLocaleDateString('pt-BR')}
                            </div>
                          </div>

                          {/* Priority Badge and SLA */}
