          <div className="flex items-center space-x-3">
            <Icon name="XCircle" size={20} className="text-red-600 dark:text-red-400" />
            <div>
              <div className="text-2xl font-bold text-slate-900 dark:text-slate-100">
                {stats.inactive}
              </div>
              <div className="text-sm text-slate-600 dark:text-slate-400">
                Inativos
              </div>
            </div>
          </div>
        </div>

        <div className="bg-white dark:bg-slate-800 rounded-lg border border-slate-200 dark:border-slate-700 p-4">
          <div className="flex items-center space-x-3">
            <Icon name="Shield" size={20} className="text-purple-600 dark:text-purple-400" />
            <div>
              <div className="text-2xl font-bold text-slate-900 dark:text-slate-100">
                {stats.admins}
              </div>
              <div className="text-sm text-slate-600 dark:text-slate-400">
                Admins
              </div>
            </div>
          </div>
        </div>

        <div className="bg-white dark:bg-slate-800 rounded-lg border border-slate-200 dark:border-slate-700 p-4">
          <div className="flex items-center space-x-3">
            <Icon name="Headphones" size={20} className="text-indigo-600 dark:text-indigo-400" />
            <div>
              <div className="text-2xl font-bold text-slate-900 dark:text-slate-100">
                {stats.operators}
              </div>
              <div className="text-sm text-slate-600 dark:text-slate-400">
                Operadores
              </div>
            </div>
          </div>
        </div>

        <div className="bg-white dark:bg-slate-800 rounded-lg border border-slate-200 dark:border-slate-700 p-4">
          <div className="flex items-center space-x-3">
            <Icon name="User" size={20} className="text-slate-600 dark:text-slate-400" />
            <div>
              <div className="text-2xl font-bold text-slate-900 dark:text-slate-100">
                {stats.regularUsers}
              </div>
              <div className="text-sm text-slate-600 dark:text-slate-400">
                Usu√°rios
              </div>
            </div>
          </div>
        </div>
      </div>

      {/* Toolbar */}
      <div className="bg-white dark:bg-slate-800 rounded-lg border border-slate-200 dark:border-slate-700 p-4">
        <div className="flex items-center justify-between">
          <div className="flex items-center space-x-3">
            <Button
              variant={showFilters ? "default" : "outline"}
              size="sm"
              onClick={() => setShowFilters(!showFilters)}
              iconName="Filter"
              iconPosition="left"
            >
              Filtros
            </Button>

            <div className="w-px h-6 bg-slate-300 dark:bg-slate-600"></div>

            <div className="flex items-center space-x-2">
              <Button
                variant={viewMode === 'grid' ? "default" : "outline"}
                size="sm"
                onClick={() => setViewMode('grid')}
                iconName="Grid3X3"
              />
              <Button
                variant={viewMode === 'list' ? "default" : "outline"}
                size="sm"
                onClick={() => setViewMode('list')}
                iconName="List"
              />
            </div>
          </div>

          <div className="flex items-center space-x-3 text-sm text-slate-600 dark:text-slate-400">
            <span>{filteredUsers.length} de {users.length} usu√°rios</span>
            {selectedUsers.length > 0 && (
              <span className="text-blue-600 dark:text-blue-400">
                {selectedUsers.length} selecionado(s)
              </span>
            )}
          </div>
        </div>
      </div>

      <div className="grid grid-cols-1 lg:grid-cols-4 gap-6">
        {/* Filters Panel */}
        {showFilters && (
          <div className="lg:col-span-1">
            <FilterPanel
              filters={filters}
              onFiltersChange={handleFilterChange}
              departments={departments}
              roles={roles}
              userStats={stats}
            />
          </div>
        )}

        {/* Main Content */}
        <div className={`${showFilters ? 'lg:col-span-3' : 'lg:col-span-4'} space-y-6`}>
          {/* Bulk Actions */}
          {showBulkActions && (
            <BulkActionsPanel
              selectedUsers={selectedUsers}
              onBulkAction={handleBulkAction}
              onClear={() => {
                setSelectedUsers([]);
                setShowBulkActions(false);
              }}
            />
          )}

          {/* User Data Grid */}
          <UserDataGrid
            users={filteredUsers}
            selectedUsers={selectedUsers}
            onUserSelect={handleUserSelect}
            onUserMultiSelect={handleUserMultiSelect}
            onUserEdit={handleUserEdit}
            viewMode={viewMode}
            departments={departments}
            roles={roles}
          />
        </div>
      </div>

      {/* User Details Panel */}
      {selectedUser && (
        <UserDetailsPanel
          userId={selectedUser}
          onClose={() => {
            setSelectedUser(null);
            setEditingUser(null);
          }}
          user={users.find(u => u.id === selectedUser)}
          departments={departments}
          roles={roles}
          isEditing={editingUser === selectedUser}
        />
      )}

      {/* Activity Monitor */}
      {showActivityMonitor && (
        <ActivityMonitor
          onClose={() => setShowActivityMonitor(false)}
          users={users}
        />
      )}

      {/* Create User Modal */}
      <Dialog open={showCreateUserModal} onOpenChange={setShowCreateUserModal}>
        <DialogContent className="sm:max-w-[425px]">
          <DialogHeader>
            <DialogTitle>Criar Novo Usu√°rio</DialogTitle>
          </DialogHeader>
          <div className="grid gap-4 py-4">
            <div className="grid grid-cols-4 items-center gap-4">
              <Label htmlFor="name" className="text-right">
                Nome *
              </Label>
              <Input
                id="name"
                value={newUser.name}
                onChange={(e) => setNewUser({ ...newUser, name: e.target.value })}
                className="col-span-3"
                placeholder="Nome completo"
              />
            </div>
            <div className="grid grid-cols-4 items-center gap-4">
              <Label htmlFor="email" className="text-right">
                Email *
              </Label>
              <Input
                id="email"
                type="email"
                value={newUser.email}
                onChange={(e) => setNewUser({ ...newUser, email: e.target.value })}
                className="col-span-3"
                placeholder="usuario@empresa.com"
              />
            </div>
            <div className="grid grid-cols-4 items-center gap-4">
              <Label htmlFor="role" className="text-right">
                Fun√ß√£o *
              </Label>
              <Select value={newUser.role} onValueChange={(value) => setNewUser({ ...newUser, role: value })}>
                <SelectTrigger className="col-span-3">
                  <SelectValue placeholder="Selecione uma fun√ß√£o" />
                </SelectTrigger>
                <SelectContent>
                  {roles.map((role) => (
                    <SelectItem key={role.id} value={role.id}>
                      {role.name}
                    </SelectItem>
                  ))}
                </SelectContent>
              </Select>
            </div>
            <div className="grid grid-cols-4 items-center gap-4">
              <Label htmlFor="department" className="text-right">
                Departamento *
              </Label>
              <Select value={newUser.department} onValueChange={(value) => setNewUser({ ...newUser, department: value })}>
                <SelectTrigger className="col-span-3">
                  <SelectValue placeholder="Selecione um departamento" />
                </SelectTrigger>
                <SelectContent>
                  {departments.map((dept) => (
                    <SelectItem key={dept.id} value={dept.name}>
                      {dept.name}
                    </SelectItem>
                  ))}
                </SelectContent>
              </Select>
            </div>
            <div className="grid grid-cols-4 items-center gap-4">
              <Label htmlFor="phone" className="text-right">
                Telefone
              </Label>
              <Input
                id="phone"
                value={newUser.phone}
                onChange={(e) => setNewUser({ ...newUser, phone: e.target.value })}
                className="col-span-3"
                placeholder="(11) 99999-9999"
              />
            </div>
            <div className="grid grid-cols-4 items-center gap-4">
              <Label htmlFor="extension" className="text-right">
                Ramal
              </Label>
              <Input
                id="extension"
                value={newUser.extension}
                onChange={(e) => setNewUser({ ...newUser, extension: e.target.value })}
                className="col-span-3"
                placeholder="1001"
              />
            </div>
            <div className="grid grid-cols-4 items-center gap-4">
              <Label htmlFor="location" className="text-right">
                Localiza√ß√£o
              </Label>
              <Input
                id="location"
                value={newUser.location}
                onChange={(e) => setNewUser({ ...newUser, location: e.target.value })}
                className="col-span-3"
                placeholder="S√£o Paulo - SP"
              />
            </div>
          </div>
          <DialogFooter>
            <Button 
              variant="outline" 
              onClick={() => setShowCreateUserModal(false)}
            >
              Cancelar
            </Button>
            <Button onClick={handleSaveUser}>
              Criar Usu√°rio
            </Button>
          </DialogFooter>
        </DialogContent>
      </Dialog>
    </div>
  );
}

