import React from 'react';
import UserCard from '@/components/users/UserCard';
import { Checkbox } from '@/components/ui/checkbox';
import Button from '@/components/Button';
import Icon from '@/components/AppIcon';

interface User {
  id: string;
  name: string;
  email: string;
  role: string;
  status: string;
  department: string;
  lastLogin: string;
  createdAt: string;
  avatar?: string | null;
  phone?: string;
  extension?: string;
  location?: string;
  manager?: string | null;
  permissions?: string[];
  ticketsAssigned?: number;
  ticketsResolved?: number;
  averageResolutionTime?: string;
  satisfactionRating?: number;
}

interface Department {
  id: string;
  name: string;
  userCount: number;
}

interface Role {
  id: string;
  name: string;
  description: string;
}

interface UserDataGridProps {
  users: User[];
  selectedUsers: string[];
  onUserSelect: (userId: string) => void;
  onUserMultiSelect: (userIds: string[]) => void;
  onUserEdit: (userId: string) => void;
  viewMode: 'grid' | 'list';
  departments: Department[];
  roles: Role[];
}

export default function UserDataGrid({
  users,
  selectedUsers,
  onUserSelect,
  onUserMultiSelect,
  onUserEdit,
  viewMode,
  departments,
  roles
}: UserDataGridProps) {
  const handleSelectAll = () => {
    if (selectedUsers.length === users.length) {
      onUserMultiSelect([]);
    } else {
      onUserMultiSelect(users.map(u => u.id));
    }
  };

  const handleUserCheck = (userId: string) => {
    const newSelection = selectedUsers.includes(userId)
      ? selectedUsers.filter(id => id !== userId)
      : [...selectedUsers, userId];
    onUserMultiSelect(newSelection);
  };

  const formatDate = (dateString: string) => {
    return new Date(dateString).toLocaleDateString('pt-BR', {
      day: '2-digit',
      month: '2-digit',
      year: 'numeric',
      hour: '2-digit',
      minute: '2-digit'
    });
  };

  const getRoleName = (roleId: string) => {
    return roles.find(r => r.id === roleId)?.name || roleId;
  };

  const getRoleColor = (role: string) => {
    switch (role) {
      case 'admin': return 'bg-purple-100 text-purple-800 dark:bg-purple-900/20 dark:text-purple-400';
      case 'manager': return 'bg-blue-100 text-blue-800 dark:bg-blue-900/20 dark:text-blue-400';
      case 'operator': return 'bg-green-100 text-green-800 dark:bg-green-900/20 dark:text-green-400';
      case 'user': return 'bg-slate-100 text-slate-800 dark:bg-slate-700 dark:text-slate-300';
      default: return 'bg-slate-100 text-slate-800 dark:bg-slate-700 dark:text-slate-300';
    }
  };

  const getStatusColor = (status: string) => {
    switch (status) {
      case 'active': return 'bg-green-100 text-green-800 dark:bg-green-900/20 dark:text-green-400';
      case 'inactive': return 'bg-red-100 text-red-800 dark:bg-red-900/20 dark:text-red-400';
      case 'pending': return 'bg-yellow-100 text-yellow-800 dark:bg-yellow-900/20 dark:text-yellow-400';
      default: return 'bg-slate-100 text-slate-800 dark:bg-slate-700 dark:text-slate-300';
    }
  };

  const getStatusLabel = (status: string) => {
    switch (status) {
      case 'active': return 'Ativo';
      case 'inactive': return 'Inativo';
      case 'pending': return 'Pendente';
      default: return status;
    }
  };

  if (users.length === 0) {
    return (
      <div className="bg-white dark:bg-slate-800 rounded-lg border border-slate-200 dark:border-slate-700 p-12 text-center">
        <Icon name="Users" size={48} className="text-slate-400 dark:text-slate-600 mx-auto mb-4" />
        <h3 className="text-lg font-semibold text-slate-900 dark:text-slate-100 mb-2">
          Nenhum usu√°rio encontrado
        </h3>
        <p className="text-slate-600 dark:text-slate-400 mb-6">
          N√£o h√° usu√°rios que correspondam aos filtros aplicados.
        </p>
        <Button iconName="Plus" iconPosition="left">
          Criar Primeiro Usu√°rio
        </Button>
      </div>
    );
  }

  if (viewMode === 'grid') {
    return (
      <div className="space-y-4">
        {/* Selection Header */}
        {users.length > 0 && (
          <div className="bg-white dark:bg-slate-800 rounded-lg border border-slate-200 dark:border-slate-700 p-4">
            <div className="flex items-center space-x-3">
              <Checkbox
                checked={selectedUsers.length === users.length && users.length > 0}
                onCheckedChange={handleSelectAll}
              />
              <span className="text-sm text-slate-600 dark:text-slate-400">
                {selectedUsers.length > 0 
                  ? `${selectedUsers.length} de ${users.length} selecionado(s)`
