            <div className="col-span-2 flex items-center">
              <span className={`inline-flex items-center px-2.5 py-0.5 rounded-full text-xs font-medium ${getRoleColor(user.role)}`}>
                {getRoleName(user.role)}
              </span>
            </div>

            <div className="col-span-2 flex items-center">
              <span className="text-sm text-slate-900 dark:text-slate-100">
                {user.department}
              </span>
            </div>

            <div className="col-span-1 flex items-center">
              <span className={`inline-flex items-center px-2.5 py-0.5 rounded-full text-xs font-medium ${getStatusColor(user.status)}`}>
                {getStatusLabel(user.status)}
              </span>
            </div>

            <div className="col-span-2 flex items-center">
              <span className="text-sm text-slate-600 dark:text-slate-400">
                {formatDate(user.lastLogin)}
              </span>
            </div>

            <div className="col-span-1 flex items-center space-x-1">
              <Button
                size="sm"
                variant="ghost"
                onClick={() => onUserSelect(user.id)}
                iconName="Eye"
                title="Visualizar usu√°rio"
              />
              <Button
                size="sm"
                variant="ghost"
                onClick={() => onUserEdit(user.id)}
                iconName="Edit"
                title="Editar usu√°rio"
              />
            </div>
          </div>
        ))}
      </div>

      {/* Table Footer */}
      <div className="border-t border-slate-200 dark:border-slate-700 bg-slate-50 dark:bg-slate-700 p-4">
        <div className="flex items-center justify-between text-sm text-slate-600 dark:text-slate-400">
          <span>
            {selectedUsers.length > 0 && `${selectedUsers.length} selecionado(s) ‚Ä¢ `}
            Mostrando {users.length} usu√°rio(s)
          </span>
          <div className="flex items-center space-x-2">
            <Button size="sm" variant="outline" disabled iconName="ChevronLeft">
              Anterior
            </Button>
            <span className="px-3 py-1 text-xs">P√°gina 1 de 1</span>
            <Button size="sm" variant="outline" disabled iconName="ChevronRight">
              Pr√≥xima
            </Button>
          </div>
        </div>
      </div>
    </div>
  );
}

