import React, { useState } from 'react';
import { useQuery, useMutation, useQueryClient } from '@tanstack/react-query';
import { Dialog, DialogContent, DialogHeader, DialogTitle, DialogTrigger } from '@/components/ui/dialog';
import { Tabs, TabsContent, TabsList, TabsTrigger } from '@/components/ui/tabs';
import { Badge } from '@/components/ui/badge';
import { Button } from '@/components/ui/button';
import { Input } from '@/components/ui/input';
import { Textarea } from '@/components/ui/textarea';
import { Avatar, AvatarFallback } from '@/components/ui/avatar';
import { Card, CardContent, CardHeader, CardTitle } from '@/components/ui/card';
import { Label } from '@/components/ui/label';
import { Select, SelectContent, SelectItem, SelectTrigger, SelectValue } from '@/components/ui/select';
import { 
  Eye, Edit, Save, X, Paperclip, MessageCircle, Clock, User, 
  FileText, Image, Download, Upload, Calendar, AlertCircle,
  CheckCircle, Pause, Play, MoreHorizontal, Send
} from 'lucide-react';
import { format } from 'date-fns';
import { ptBR } from 'date-fns/locale';
import { apiRequest } from '@/lib/queryClient';
import { useToast } from '@/hooks/use-toast';


interface TicketModalProps {
  ticket: any;
  children: React.ReactNode;
  onUpdate?: (ticket: any) => void;
}

export function TicketModal({ ticket, children, onUpdate }: TicketModalProps) {
  const [isOpen, setIsOpen] = useState(false);
  const [isEditing, setIsEditing] = useState(false);
  const [editedTicket, setEditedTicket] = useState(ticket);
  const [newComment, setNewComment] = useState('');
  const [newTag, setNewTag] = useState('');
  const { toast } = useToast();
  const queryClient = useQueryClient();

  // Buscar coment√°rios do ticket
  const { data: comments, isLoading: commentsLoading } = useQuery<any[]>({
    queryKey: ['/api/tickets', ticket.id, 'comments'],
    enabled: isOpen && !!ticket.id,
  });

  // Buscar usu√°rios para saber quem est√° logado
  const { data: users } = useQuery<any[]>({
    queryKey: ['/api/users'],
    enabled: isOpen,
  });

  // Buscar configura√ß√µes de status e prioridade
  const { data: statusConfigs } = useQuery<any[]>({
    queryKey: ['/api/config/status'],
    enabled: isOpen,
  });

  const { data: priorityConfigs } = useQuery<any[]>({
    queryKey: ['/api/config/priority'],
    enabled: isOpen,
  });

  // Usu√°rio atual (assumindo que √© o primeiro admin para demo)
  const currentUser = users?.find(u => u.role === 'admin') || users?.[0];

  // Mutation para criar coment√°rio
  const createCommentMutation = useMutation({
    mutationFn: async (content: string) => {
      if (!currentUser) throw new Error('Usu√°rio n√£o encontrado');

      return apiRequest(`/api/tickets/${ticket.id}/comments`, 'POST', {
        content,
        userId: currentUser.id,
        ticketId: ticket.id,
      });
    },
    onSuccess: () => {
      queryClient.invalidateQueries({ queryKey: ['/api/tickets', ticket.id, 'comments'] });
      setNewComment('');
      toast({
        title: 'Coment√°rio adicionado',
        description: 'Seu coment√°rio foi adicionado com sucesso.',
      });
    },
    onError: (error: any) => {
      toast({
        title: 'Erro',
        description: error.message || 'Erro ao adicionar coment√°rio',
        variant: 'destructive',
      });
    },
  });

  const [attachments, setAttachments] = useState([
    { id: 1, name: 'screenshot.png', type: 'image', size: '2.4 MB', uploadedAt: new Date(), uploadedBy: 'Jo√£o Silva' },
    { id: 2, name: 'log_error.txt', type: 'text', size: '156 KB', uploadedAt: new Date(), uploadedBy: 'Ana Santos' }
  ]);

  // Mutation para salvar ticket
  const saveTicketMutation = useMutation({
    mutationFn: async (ticketData: any) => {
      return apiRequest(`/api/tickets/${ticket.id}`, 'PATCH', ticketData);
    },
    onSuccess: (updatedTicket) => {
      queryClient.invalidateQueries({ queryKey: ['/api/tickets'] });
      onUpdate?.(updatedTicket);
      setIsEditing(false);
      toast({
        title: 'Ticket atualizado',
        description: 'As altera√ß√µes foram salvas com sucesso.',
      });
    },
    onError: (error: any) => {
      toast({
        title: 'Erro ao salvar',
        description: error.message || 'Erro ao salvar as altera√ß√µes',
        variant: 'destructive',
      });
    },
  });

  const handleSave = () => {
    saveTicketMutation.mutate(editedTicket);
  };

  const handleCancel = () => {
    setEditedTicket(ticket);
    setNewTag(''); // Limpar tag input
    setIsEditing(false);
  };

  const handleAddComment = () => {
    if (!newComment.trim()) return;
    createCommentMutation.mutate(newComment);
  };

  const handleDownloadAttachment = (attachment: any) => {
    // Simular download do anexo
    const link = document.createElement('a');
    link.href = attachment.url || '#';
    link.download = attachment.name;
    document.body.appendChild(link);
    link.click();
    document.body.removeChild(link);

    // Toast de confirma√ß√£o
    toast({
      title: 'Download iniciado',
      description: `Fazendo download de ${attachment.name}`,
    });
  };

  const getPriorityColor = (priority: string) => {
    const config = priorityConfigs?.find(p => p.value === priority || p.name === priority);
    if (config?.color) {
      // Convert hex to Tailwind classes
      const colorMap: Record<string, string> = {
        '#dc2626': 'bg-red-100 text-red-800 border-red-200',
        '#f59e0b': 'bg-orange-100 text-orange-800 border-orange-200',
        '#3b82f6': 'bg-blue-100 text-blue-800 border-blue-200',
        '#10b981': 'bg-green-100 text-green-800 border-green-200',
      };
      return colorMap[config.color] || 'bg-gray-100 text-gray-800 border-gray-200';
    }
    return 'bg-gray-100 text-gray-800 border-gray-200';
  };

  const getStatusColor = (status: string) => {
    const config = statusConfigs?.find(s => s.value === status || s.name === status);
    if (config?.color) {
      // Convert hex to Tailwind classes  
      const colorMap: Record<string, string> = {
        '#3b82f6': 'border-blue-200 text-blue-800 bg-blue-50',
        '#f59e0b': 'border-yellow-200 text-yellow-800 bg-yellow-50',
        '#10b981': 'border-green-200 text-green-800 bg-green-50',
        '#6b7280': 'border-gray-200 text-gray-800 bg-gray-50',
      };
      return colorMap[config.color] || 'border-gray-200 text-gray-800 bg-gray-50';
    }
    return 'border-gray-200 text-gray-800 bg-gray-50';
  };

  const handleFileUpload = (event: React.ChangeEvent<HTMLInputElement>) => {
    const files = event.target.files;
    if (files) {
      // Aqui voc√™ adicionaria a l√≥gica para upload dos arquivos
      console.log('Uploading files:', files);
    }
  };

  // Fun√ß√£o para adicionar nova tag
  const handleAddTag = () => {
    if (!newTag.trim()) return;

    const currentTags = editedTicket.tags || [];

    // Verificar se a tag j√° existe (n√£o case-sensitive)
    if (currentTags.some((tag: string) => tag.toLowerCase() === newTag.toLowerCase())) {
      toast({
        title: 'Tag j√° existe',
        description: 'Esta tag j√° foi adicionada ao ticket.',
        variant: 'destructive',
      });
      return;
    }

    // Adicionar nova tag
    setEditedTicket({
      ...editedTicket,
      tags: [...currentTags, newTag.trim()]
    });

    setNewTag('');

    toast({
      title: 'Tag adicionada',
      description: `A tag "${newTag.trim()}" foi adicionada com sucesso.`,
    });
  };

  // Fun√ß√£o para remover tag
  const handleRemoveTag = (indexToRemove: number) => {
    const currentTags = editedTicket.tags || [];
    const tagToRemove = currentTags[indexToRemove];

    const updatedTags = currentTags.filter((_: string, index: number) => index !== indexToRemove);

    setEditedTicket({
      ...editedTicket,
      tags: updatedTags
    });

    toast({
      title: 'Tag removida',
      description: `A tag "${tagToRemove}" foi removida com sucesso.`,
    });
  };

  return (
    <Dialog open={isOpen} onOpenChange={setIsOpen}>
      <DialogTrigger asChild>
        {children}
      </DialogTrigger>
      <DialogContent className="max-w-4xl max-h-[90vh] overflow-y-auto">
        <DialogHeader className="flex flex-row items-center justify-between">
          <DialogTitle className="flex items-center space-x-3">
            <span className="text-xl font-bold">Ticket {ticket.ticketNumber}</span>
            <Badge variant="outline" className={`${getStatusColor(editedTicket.status)}`}>
              {editedTicket.status}
            </Badge>
            <Badge variant="outline" className={`${getPriorityColor(editedTicket.priority)}`}>
              {editedTicket.priority}
            </Badge>
          </DialogTitle>
          <div className="flex items-center space-x-2">
            {isEditing ? (
              <>
                <Button size="sm" onClick={handleSave} disabled={saveTicketMutation.isPending}>
                  <Save className="w-4 h-4 mr-2" />
                  {saveTicketMutation.isPending ? 'Salvando...' : 'Salvar'}
                </Button>
                <Button variant="outline" size="sm" onClick={handleCancel}>
                  <X className="w-4 h-4 mr-2" />
                  Cancelar
                </Button>
              </>
            ) : (
              <Button variant="outline" size="sm" onClick={() => setIsEditing(true)}>
                <Edit className="w-4 h-4 mr-2" />
                Editar
              </Button>
            )}
          </div>
        </DialogHeader>

        <Tabs defaultValue="details" className="w-full">
          <TabsList className="grid w-full grid-cols-4">
            <TabsTrigger value="details">Detalhes</TabsTrigger>
            <TabsTrigger value="comments">Coment√°rios</TabsTrigger>
            <TabsTrigger value="attachments">Anexos</TabsTrigger>
            <TabsTrigger value="history">Hist√≥rico</TabsTrigger>
          </TabsList>

          <TabsContent value="details" className="space-y-6">
            <div className="grid grid-cols-1 md:grid-cols-2 gap-6">
              {/* Informa√ß√µes Principais */}
              <Card>
                <CardHeader>
                  <CardTitle className="text-lg">Informa√ß√µes Principais</CardTitle>
                </CardHeader>
                <CardContent className="space-y-4">
                  <div>
                    <Label className="text-sm font-medium">T√≠tulo</Label>
                    {isEditing ? (
                      <Input
                        value={editedTicket.subject}
                        onChange={(e) => setEditedTicket({...editedTicket, subject: e.target.value})}
                        className="mt-1"
                      />
                    ) : (
                      <p className="mt-1 text-sm text-gray-900">{editedTicket.subject}</p>
                    )}
                  </div>

                  <div>
                    <Label className="text-sm font-medium">Descri√ß√£o</Label>
                    {isEditing ? (
                      <Textarea
                        value={editedTicket.description || ''}
                        onChange={(e) => setEditedTicket({...editedTicket, description: e.target.value})}
                        className="mt-1"
                        rows={3}
                      />
                    ) : (
                      <p className="mt-1 text-sm text-gray-700">{editedTicket.description}</p>
                    )}
                  </div>

                  <div className="grid grid-cols-2 gap-4">
                    <div>
                      <Label className="text-sm font-medium">Status</Label>
                      {isEditing ? (
                        <Select 
                          value={editedTicket.status} 
                          onValueChange={(value) => setEditedTicket({...editedTicket, status: value})}
                        >
                          <SelectTrigger className="mt-1">
                            <SelectValue />
                          </SelectTrigger>
                          <SelectContent>
                            {statusConfigs?.map((status) => (
                              <SelectItem key={status.id} value={status.value}>
                                {status.name}
                              </SelectItem>
                            ))}
                          </SelectContent>
                        </Select>
                      ) : (
                        <Badge variant="outline" className={`mt-1 ${getStatusColor(editedTicket.status)}`}>
                          {editedTicket.status}
                        </Badge>
                      )}
                    </div>

                    <div>
                      <Label className="text-sm font-medium">Prioridade</Label>
                      {isEditing ? (
                        <Select 
                          value={editedTicket.priority} 
                          onValueChange={(value) => setEditedTicket({...editedTicket, priority: value})}
                        >
                          <SelectTrigger className="mt-1">
                            <SelectValue />
                          </SelectTrigger>
                          <SelectContent>
                            {priorityConfigs?.map((priority) => (
                              <SelectItem key={priority.id} value={priority.value}>
                                {priority.name}
                              </SelectItem>
                            ))}
                          </SelectContent>
                        </Select>
                      ) : (
                        <Badge variant="outline" className={`mt-1 ${getPriorityColor(editedTicket.priority)}`}>
                          {editedTicket.priority}
                        </Badge>
                      )}
                    </div>
                  </div>

                  <div>
                    <Label className="text-sm font-medium">Progresso</Label>
                    <div className="mt-2 space-y-2">
                      <div className="flex justify-between text-sm">
                        <span>Conclu√≠do</span>
                        <span>{editedTicket.progress}%</span>
                      </div>
                      <div className="w-full bg-gray-200 rounded-full h-2">
                        <div 
                          className="bg-blue-500 h-2 rounded-full transition-all"
                          style={{ width: `${editedTicket.progress}%` }}
                        />
                      </div>
                    </div>
                  </div>
                </CardContent>
              </Card>

              {/* Atribui√ß√µes */}
              <Card>
                <CardHeader>
                  <CardTitle className="text-lg">Atribui√ß√µes</CardTitle>
                </CardHeader>
                <CardContent className="space-y-4">
                  <div>
                    <Label className="text-sm font-medium">Respons√°vel</Label>
                    <div className="mt-2 flex items-center space-x-3">
                      <Avatar className="w-8 h-8">
                        <AvatarFallback className="bg-blue-100 text-blue-600 text-xs">
                          {editedTicket.assignedToUser?.name?.charAt(0) || 'U'}
                        </AvatarFallback>
                      </Avatar>
                      <div>
                        <p className="text-sm font-medium">{editedTicket.assignedToUser?.name || 'N√£o atribu√≠do'}</p>
                        <p className="text-xs text-gray-500">{editedTicket.department?.name || 'Sem departamento'}</p>
                      </div>
                    </div>
                  </div>

                  <div>
                    <Label className="text-sm font-medium">Solicitante</Label>
                    <p className="mt-1 text-sm text-gray-700">{editedTicket.createdByUser?.name || 'Desconhecido'}</p>
                  </div>

                  <div>
                    <Label className="text-sm font-medium">Departamento</Label>
                    <p className="mt-1 text-sm text-gray-700">{editedTicket.department?.name || 'Sem departamento'}</p>
                  </div>

                  <div>
                    <Label className="text-sm font-medium">Data de Cria√ß√£o</Label>
                    <div className="mt-1 flex items-center space-x-2">
                      <Calendar className="w-4 h-4 text-gray-400" />
                      <span className="text-sm text-gray-700">{new Date(editedTicket.createdAt).toLocaleDateString('pt-BR')}</span>
                    </div>
                  </div>

                  <div>
                    <Label className="text-sm font-medium">Tags</Label>
                    {isEditing ? (
                      <div className="mt-2 space-y-2">
                        {/* Tags existentes - edit√°veis */}
                        <div className="flex flex-wrap gap-1">
                          {editedTicket.tags?.map((tag: string, index: number) => (
                            <Badge 
                              key={index} 
                              variant="secondary" 
                              className="text-xs flex items-center gap-1 pr-1"
                            >
                              {tag}
                              <button
                                type="button"
                                onClick={() => handleRemoveTag(index)}
                                className="ml-1 text-gray-500 hover:text-red-500 transition-colors"
                                title="Remover tag"
                              >
                                <X className="w-3 h-3" />
                              </button>
                            </Badge>
                          )) || []}
                        </div>

                        {/* Input para adicionar nova tag */}
                        <div className="flex items-center space-x-2">
                          <Input
                            type="text"
                            placeholder="Adicionar nova tag..."
                            value={newTag}
                            onChange={(e) => setNewTag(e.target.value)}
                            onKeyPress={(e) => {
                              if (e.key === 'Enter') {
                                e.preventDefault();
                                handleAddTag();
                              }
                            }}
                            className="flex-1"
                          />
                          <Button
                            type="button"
                            size="sm"
                            onClick={handleAddTag}
                            disabled={!newTag.trim()}
                            className="px-3"
                          >
                            Adicionar
                          </Button>
                        </div>
                      </div>
                    ) : (
                      /* Modo visualiza√ß√£o - somente leitura */
                      <div className="mt-1 flex flex-wrap gap-1">
                        {editedTicket.tags?.map((tag: string, index: number) => (
                          <Badge key={index} variant="secondary" className="text-xs">
                            {tag}
                          </Badge>
                        )) || <span className="text-sm text-gray-500">Nenhuma tag</span>}
                      </div>
                    )}
                  </div>
                </CardContent>
              </Card>
            </div>
          </TabsContent>

          <TabsContent value="comments" className="space-y-4">
            {/* Adicionar Coment√°rio */}
            <Card>
              <CardHeader>
                <CardTitle className="text-lg">Adicionar Coment√°rio</CardTitle>
              </CardHeader>
              <CardContent className="space-y-4">
                <Textarea
                  placeholder="Escreva seu coment√°rio..."
                  value={newComment}
                  onChange={(e) => setNewComment(e.target.value)}
                  rows={3}
                />
                <div className="flex justify-between items-center">
                  <div className="flex items-center space-x-2">
                    <input type="checkbox" id="internal" className="rounded" />
                    <Label htmlFor="internal" className="text-sm">Coment√°rio interno</Label>
                  </div>
                  <Button 
                    onClick={handleAddComment} 
                    disabled={!newComment.trim() || createCommentMutation.isPending}
                  >
                    <Send className="w-4 h-4 mr-2" />
                    {createCommentMutation.isPending ? 'Adicionando...' : 'Adicionar Coment√°rio'}
                  </Button>
                </div>
              </CardContent>
            </Card>

            {/* Lista de Coment√°rios */}
            <div className="space-y-4">
              {commentsLoading ? (
                <div className="text-center py-4">
                  <div className="animate-spin rounded-full h-8 w-8 border-b-2 border-blue-600 mx-auto"></div>
                  <p className="mt-2 text-sm text-gray-500">Carregando coment√°rios...</p>
                </div>
              ) : comments && comments.length > 0 ? (
                comments.map((comment) => (
                  <Card key={comment.id}>
                    <CardContent className="p-4">
                      <div className="flex items-start space-x-3">
                        <Avatar className="w-8 h-8">
                          <AvatarFallback className="bg-blue-100 text-blue-600 text-xs">
                            {comment.user?.name?.split(' ').map((n: string) => n[0]).join('').toUpperCase() || 'U'}
                          </AvatarFallback>
                        </Avatar>
                        <div className="flex-1">
                          <div className="flex items-center space-x-2 mb-1">
                            <span className="text-sm font-medium">{comment.user?.name || 'Usu√°rio'}</span>
                            <span className="text-xs text-gray-500">
                              {format(new Date(comment.createdAt), "dd 'de' MMMM '√†s' HH:mm", { locale: ptBR })}
                            </span>
                          </div>
                          <p className="text-sm text-gray-700">{comment.content}</p>
                        </div>
                      </div>
                    </CardContent>
                  </Card>
                ))
              ) : (
                <div className="text-center py-8">
                  <MessageCircle className="mx-auto h-12 w-12 text-gray-400" />
                  <p className="mt-2 text-sm text-gray-500">Nenhum coment√°rio ainda</p>
                  <p className="text-xs text-gray-400">Seja o primeiro a comentar neste ticket</p>
                </div>
              )}
            </div>
          </TabsContent>

          <TabsContent value="attachments" className="space-y-4">
            {/* Upload de Anexos */}
            <Card>
              <CardHeader>
                <CardTitle className="text-lg">Adicionar Anexos</CardTitle>
              </CardHeader>
              <CardContent>
                <div className="border-2 border-dashed border-gray-300 rounded-lg p-6 text-center">
                  <Upload className="mx-auto h-12 w-12 text-gray-400" />
                  <div className="mt-2">
                    <label htmlFor="file-upload" className="cursor-pointer">
                      <span className="text-sm font-medium text-blue-600 hover:text-blue-500">
                        Clique para fazer upload
                      </span>
                      <span className="text-sm text-gray-500"> ou arraste arquivos aqui</span>
                    </label>
                    <input
                      id="file-upload"
                      type="file"
                      multiple
                      className="hidden"
                      onChange={handleFileUpload}
                    />
                  </div>
                  <p className="text-xs text-gray-500 mt-1">
                    PNG, JPG, PDF at√© 10MB
                  </p>
                </div>
              </CardContent>
            </Card>

            {/* Lista de Anexos */}
            <div className="space-y-3">
              {attachments.map((attachment) => (
                <Card key={attachment.id}>
                  <CardContent className="p-4">
                    <div className="flex items-center justify-between">
                      <div className="flex items-center space-x-3">
                        <div className="p-2 bg-gray-100 rounded">
                          {attachment.type === 'image' ? (
                            <Image className="w-5 h-5 text-blue-600" />
                          ) : (
                            <FileText className="w-5 h-5 text-gray-600" />
                          )}
                        </div>
                        <div>
                          <p className="text-sm font-medium">{attachment.name}</p>
                          <p className="text-xs text-gray-500">
                            {attachment.size} ‚Ä¢ {attachment.uploadedBy} ‚Ä¢ 
                            {format(attachment.uploadedAt, "dd/MM/yyyy HH:mm")}
                          </p>
                        </div>
                      </div>
                      <Button 
                        variant="ghost" 
                        size="sm"
                        onClick={() => handleDownloadAttachment(attachment)}
                      >
                        <Download className="w-4 h-4" />
                      </Button>
                    </div>
                  </CardContent>
                </Card>
              ))}
            </div>
          </TabsContent>

          <TabsContent value="history" className="space-y-4">
            <div className="space-y-4">
              <div className="flex items-center space-x-3 p-3 bg-gray-50 rounded-lg">
                <div className="p-2 bg-green-100 rounded-full">
                  <CheckCircle className="w-4 h-4 text-green-600" />
                </div>
                <div className="flex-1">
                  <p className="text-sm font-medium">Ticket criado</p>
                  <p className="text-xs text-gray-500">Por Cliente Opus ‚Ä¢ 17/12/2024 10:30</p>
                </div>
              </div>

              <div className="flex items-center space-x-3 p-3 bg-gray-50 rounded-lg">
                <div className="p-2 bg-blue-100 rounded-full">
                  <User className="w-4 h-4 text-blue-600" />
                </div>
                <div className="flex-1">
                  <p className="text-sm font-medium">Ticket atribu√≠do para Jo√£o Silva</p>
                  <p className="text-xs text-gray-500">Por Sistema ‚Ä¢ 17/12/2024 10:35</p>
                </div>
              </div>

              <div className="flex items-center space-x-3 p-3 bg-gray-50 rounded-lg">
                <div className="p-2 bg-yellow-100 rounded-full">
                  <Play className="w-4 h-4 text-yellow-600" />
                </div>
                <div className="flex-1">
                  <p className="text-sm font-medium">Status alterado para "Atendendo"</p>
                  <p className="text-xs text-gray-500">Por Jo√£o Silva ‚Ä¢ 17/12/2024 11:00</p>
                </div>
              </div>

              <div className="flex items-center space-x-3 p-3 bg-gray-50 rounded-lg">
                <div className="p-2 bg-purple-100 rounded-full">
                  <Paperclip className="w-4 h-4 text-purple-600" />
                </div>
                <div className="flex-1">
                  <p className="text-sm font-medium">Anexo adicionado: screenshot.png</p>
                  <p className="text-xs text-gray-500">Por Jo√£o Silva ‚Ä¢ 17/12/2024 14:20</p>
                </div>
              </div>
            </div>
          </TabsContent>
        </Tabs>
      </DialogContent>
    </Dialog>
  );
}

