import React, { useState, useEffect } from 'react';
import { useQuery, useMutation, useQueryClient } from '@tanstack/react-query';
import { Dialog, DialogContent, DialogHeader, DialogTitle } from '@/components/ui/dialog';
import { Button } from '@/components/ui/button';
import { Input } from '@/components/ui/input';
import { Textarea } from '@/components/ui/textarea';
import { Label } from '@/components/ui/label';
import { Select, SelectContent, SelectItem, SelectTrigger, SelectValue } from '@/components/ui/select';
import { Badge } from '@/components/ui/badge';
import { Paperclip, X, Upload } from 'lucide-react';
import { apiRequest } from '@/lib/queryClient';
import { useToast } from '@/hooks/use-toast';

interface CreateTicketModalNewProps {
  isOpen: boolean;
  onClose: () => void;
}

interface DynamicField {
  id: string;
  label: string;
  type: 'text' | 'email' | 'select' | 'textarea' | 'number' | 'tel';
  placeholder?: string;
  required?: boolean;
  options?: string[];
}

// Configura√ß√µes de campos din√¢micos por categoria
const DYNAMIC_FIELDS_CONFIG: Record<string, DynamicField[]> = {
  'Suporte T√©cnico': [
    { id: 'tipo_ticket', label: 'Tipo de Ticket', type: 'select', required: true, 
      options: ['Instala√ß√£o de Software', 'Problemas de Hardware', 'Problemas de Rede', 'Outros'] },
    { id: 'equipamento', label: 'Equipamento/Sistema', type: 'text', placeholder: 'Ex: Notebook Dell, Sistema XYZ', required: true },
    { id: 'urgencia_negocio', label: 'Urg√™ncia do Neg√≥cio', type: 'select', required: true,
      options: ['Baixa', 'M√©dia', 'Alta', 'Cr√≠tica'] }
  ],
  'Recursos Humanos': [
    { id: 'tipo_ticket', label: 'Tipo de Ticket', type: 'select', required: true,
      options: ['F√©rias', 'Licen√ßa', 'Benef√≠cios', 'Folha de Pagamento', 'Outros'] },
    { id: 'cpf_solicitante', label: 'Qual CPF do solicitante?', type: 'text', placeholder: '000.000.000-00', required: true },
    { id: 'email_solicitante', label: 'Qual e-mail?', type: 'email', placeholder: '(Email) Pressione Enter ou Tab', required: true },
    { id: 'celular_solicitante', label: 'N√∫mero do Celular do solicitante', type: 'tel', required: true },
    { id: 'centro_custo', label: 'Qual Centro de Custo do solicitante?', type: 'text', required: true },
    { id: 'superior_direto', label: 'Superior Direto', type: 'text', required: true }
  ],
  'Financeiro': [
    { id: 'tipo_ticket', label: 'Tipo de Ticket', type: 'select', required: true,
      options: ['Contas a Pagar', 'Contas a Receber', 'Reembolso', 'Or√ßamento', 'Outros'] },
    { id: 'valor_envolvido', label: 'Valor Envolvido', type: 'number', placeholder: 'R$ 0,00', required: true },
    { id: 'centro_custo', label: 'Centro de Custo', type: 'text', required: true },
    { id: 'aprovador', label: 'Aprovador Respons√°vel', type: 'text', required: true }
  ],
  'Jur√≠dico': [
    { id: 'tipo_ticket', label: 'Tipo de Ticket', type: 'select', required: true,
      options: ['Contrato', 'Assessoria Legal', 'Compliance', 'Outros'] },
    { id: 'prazo_resposta', label: 'Prazo para Resposta', type: 'select', required: true,
      options: ['24 horas', '48 horas', '1 semana', '2 semanas', 'N√£o urgente'] },
    { id: 'area_direito', label: '√Årea do Direito', type: 'select', required: true,
      options: ['Trabalhista', 'Tribut√°rio', 'Civil', 'Empresarial', 'Outros'] }
  ],
  'Marketing': [
    { id: 'tipo_ticket', label: 'Tipo de Ticket', type: 'select', required: true,
      options: ['Campanha', 'Material Gr√°fico', 'Evento', 'M√≠dias Sociais', 'Outros'] },
    { id: 'prazo_entrega', label: 'Prazo de Entrega', type: 'text', placeholder: 'Ex: 15 dias', required: true },
    { id: 'publico_alvo', label: 'P√∫blico Alvo', type: 'text', required: true },
    { id: 'orcamento_estimado', label: 'Or√ßamento Estimado', type: 'number', placeholder: 'R$ 0,00' }
  ]
};

export default function CreateTicketModalNew({ isOpen, onClose }: CreateTicketModalNewProps) {
  const { toast } = useToast();
  const queryClient = useQueryClient();

  // Estados do formul√°rio
  const [formData, setFormData] = useState({
    subject: '',
    description: '',
    requesterDepartment: '',
    primaryCategory: '',
    secondaryCategory: '',
    requesterName: '',
    requesterEmail: '',
    priority: 'medium' as 'low' | 'medium' | 'high' | 'critical',
    dynamicFields: {} as Record<string, string>
  });

  const [selectedFiles, setSelectedFiles] = useState<File[]>([]);
  const [subcategories, setSubcategories] = useState<string[]>([]);

  // Buscar dados necess√°rios
  const { data: users } = useQuery<any[]>({
    queryKey: ['/api/users'],
    enabled: isOpen,
  });

  const { data: departments } = useQuery<any[]>({
    queryKey: ['/api/departments'],
    enabled: isOpen,
  });

  const { data: categories } = useQuery<any[]>({
    queryKey: ['/api/categories'],
    enabled: isOpen,
  });

  // Usu√°rio atual
  const currentUser = users?.find(u => u.role === 'admin') || users?.[0];

  // Atualizar subcategorias quando categoria prim√°ria muda
  useEffect(() => {
    if (formData.primaryCategory) {
      // Simular subcategorias baseadas na categoria prim√°ria
      const mockSubcategories: Record<string, string[]> = {
        'Tecnologia': ['Suporte T√©cnico', 'Desenvolvimento', 'Infraestrutura'],
        'Custos': ['Cart√£o Clara', 'Reembolso Protheus'],
        'Departamento Pessoal': ['Folha de Pagamento', 'Benef√≠cios', 'Admiss√£o/Demiss√£o'],
        'Jur√≠dico': ['Contratos', 'Assessoria Legal', 'Compliance'],
        'Marketing': ['Campanhas', 'Material Gr√°fico', 'Eventos'],
        'Qualidade': ['Auditoria', 'Processos', 'Melhorias']
      };

      setSubcategories(mockSubcategories[formData.primaryCategory] || []);
      setFormData(prev => ({ 
        ...prev, 
        secondaryCategory: '',
        dynamicFields: {} // Reset dynamic fields when category changes
      }));
    }
  }, [formData.primaryCategory]);

  // Obter campos din√¢micos baseados na categoria secund√°ria
  const getDynamicFields = (): DynamicField[] => {
    if (!formData.secondaryCategory) return [];
    return DYNAMIC_FIELDS_CONFIG[formData.secondaryCategory] || [];
  };

  // Mutation para criar ticket
  const createTicketMutation = useMutation({
    mutationFn: async (data: any) => {
      if (!currentUser) {
        throw new Error('Usu√°rio n√£o encontrado');
      }

      const ticketData = {
        subject: data.subject,
        description: data.description,
        priority: data.priority,
        category: data.secondaryCategory,
        responsibleDepartmentId: departments?.find(d => d.name === data.primaryCategory)?.id || null,
        requesterDepartmentId: currentUser.departmentId || null,
        createdBy: currentUser.id,
        assignedTo: null,
        customFields: {
          requesterName: data.requesterName,
          requesterEmail: data.requesterEmail,
          ...data.dynamicFields
        }
      };

      return apiRequest('/api/tickets', 'POST', ticketData);
    },
    onSuccess: () => {
      toast({
        title: 'Sucesso',
        description: 'Ticket criado com sucesso',
      });
      queryClient.invalidateQueries({ queryKey: ['/api/tickets'] });
      queryClient.invalidateQueries({ queryKey: ['/api/dashboard'] });
      handleReset();
      onClose();
    },
    onError: (error: any) => {
      toast({
        title: 'Erro',
        description: error.message || 'Erro ao criar ticket',
        variant: 'destructive',
      });
    },
  });

  const handleReset = () => {
    setFormData({
      subject: '',
      description: '',
      requesterDepartment: '',
      primaryCategory: '',
      secondaryCategory: '',
      requesterName: '',
      requesterEmail: '',
      priority: 'medium',
      dynamicFields: {}
    });
    setSelectedFiles([]);
    setSubcategories([]);
  };

  const handleSubmit = (e: React.FormEvent) => {
    e.preventDefault();

    // Valida√ß√µes b√°sicas
    if (!formData.subject.trim()) {
      toast({ title: 'Erro', description: 'Assunto √© obrigat√≥rio', variant: 'destructive' });
      return;
    }

    if (!formData.description.trim()) {
      toast({ title: 'Erro', description: 'Descri√ß√£o √© obrigat√≥ria', variant: 'destructive' });
      return;
    }

    if (!formData.primaryCategory) {
      toast({ title: 'Erro', description: 'Categoria Prim√°ria √© obrigat√≥ria', variant: 'destructive' });
      return;
    }

    if (!formData.secondaryCategory) {
      toast({ title: 'Erro', description: 'Categoria Secund√°ria √© obrigat√≥ria', variant: 'destructive' });
      return;
    }

    // Validar campos din√¢micos obrigat√≥rios
    const dynamicFields = getDynamicFields();
    for (const field of dynamicFields) {
      if (field.required && !formData.dynamicFields[field.id]) {
        toast({ 
          title: 'Erro', 
          description: `${field.label} √© obrigat√≥rio`, 
          variant: 'destructive' 
        });
        return;
      }
    }

    createTicketMutation.mutate(formData);
  };

  const handleFileSelect = (e: React.ChangeEvent<HTMLInputElement>) => {
    const files = Array.from(e.target.files || []);
    setSelectedFiles(prev => [...prev, ...files]);
  };

  const removeFile = (index: number) => {
    setSelectedFiles(prev => prev.filter((_, i) => i !== index));
  };

  const handleDynamicFieldChange = (fieldId: string, value: string) => {
    setFormData(prev => ({
      ...prev,
      dynamicFields: {
        ...prev.dynamicFields,
        [fieldId]: value
      }
    }));
  };

  const primaryCategories = ['Custos', 'Departamento Pessoal', 'Jur√≠dico', 'Marketing', 'Qualidade', 'Tecnologia'];

  return (
    <Dialog open={isOpen} onOpenChange={onClose}>
      <DialogContent className="max-w-2xl max-h-[90vh] overflow-y-auto">
        <DialogHeader>
          <DialogTitle className="flex items-center justify-between">
            Novo Ticket
            <Button variant="ghost" size="icon" onClick={onClose}>
              <X className="h-4 w-4" />
            </Button>
          </DialogTitle>
        </DialogHeader>

        <form onSubmit={handleSubmit} className="space-y-4">
          {/* Assunto */}
          <div>
            <Label htmlFor="subject" className="text-sm font-medium">
              Assunto <span className="text-red-500">*</span>
            </Label>
            <Input
              id="subject"
              value={formData.subject}
              onChange={(e) => setFormData(prev => ({ ...prev, subject: e.target.value }))}
              className="mt-1"
              required
            />
          </div>

          {/* Descri√ß√£o */}
          <div>
            <Label htmlFor="description" className="text-sm font-medium">
              Descri√ß√£o <span className="text-red-500">*</span>
            </Label>
            <Textarea
              id="description"
              value={formData.description}
              onChange={(e) => setFormData(prev => ({ ...prev, description: e.target.value }))}
              className="mt-1 min-h-[100px]"
              required
            />
          </div>

          {/* Setor do Solicitante */}
          <div>
            <Label htmlFor="requesterDepartment" className="text-sm font-medium">
              Setor do Solicitante <span className="text-red-500">*</span>
            </Label>
            <Select
              value={formData.requesterDepartment}
              onValueChange={(value) => setFormData(prev => ({ ...prev, requesterDepartment: value }))}
            >
              <SelectTrigger className="mt-1">
                <SelectValue placeholder="Selecione..." />
              </SelectTrigger>
              <SelectContent>
                {departments?.map((dept) => (
                  <SelectItem key={dept.id} value={dept.name}>
                    {dept.name}
                  </SelectItem>
                ))}
              </SelectContent>
            </Select>
          </div>

          {/* Categoria Prim√°ria */}
          <div>
            <Label htmlFor="primaryCategory" className="text-sm font-medium">
              Categoria Prim√°ria <span className="text-red-500">*</span>
            </Label>
            <Select
              value={formData.primaryCategory}
              onValueChange={(value) => setFormData(prev => ({ ...prev, primaryCategory: value }))}
            >
              <SelectTrigger className="mt-1">
                <SelectValue placeholder="Selecione..." />
              </SelectTrigger>
              <SelectContent>
                <div className="p-2">
                  <Input placeholder="Pesquisar" className="mb-2" />
                </div>
                {primaryCategories.map((category) => (
                  <SelectItem key={category} value={category}>
                    {category}
                  </SelectItem>
                ))}
              </SelectContent>
            </Select>
          </div>

          {/* Categoria Secund√°ria */}
          <div>
            <Label htmlFor="secondaryCategory" className="text-sm font-medium">
              Categoria Secund√°ria <span className="text-red-500">*</span>
              <span className="text-red-600 text-xs ml-2">
                (Para abertura do ticket, √© obrigat√≥rio a sele√ß√£o do √∫ltimo n√≠vel das categorias que est√£o sinalizadas na cor verde)
              </span>
            </Label>
            <Select
              value={formData.secondaryCategory}
              onValueChange={(value) => setFormData(prev => ({ ...prev, secondaryCategory: value }))}
              disabled={!formData.primaryCategory}
            >
              <SelectTrigger className="mt-1">
                <SelectValue placeholder={
                  formData.primaryCategory ? "Selecione..." : "Selecione uma categoria prim√°ria antes"
                } />
              </SelectTrigger>
              <SelectContent>
                <div className="p-2">
                  <Input placeholder="Pesquisar" className="mb-2" />
                </div>
                {subcategories.map((subcat) => (
                  <SelectItem key={subcat} value={subcat}>
                    <div className="flex items-center">
                      <span className="w-2 h-2 bg-green-500 rounded-full mr-2"></span>
                      {subcat}
                    </div>
                  </SelectItem>
                ))}
              </SelectContent>
            </Select>
          </div>

          {/* Nome do Solicitante */}
          <div>
            <Label htmlFor="requesterName" className="text-sm font-medium">
              Nome do Solicitante (Completo) <span className="text-red-500">*</span>
            </Label>
            <Input
              id="requesterName"
              value={formData.requesterName}
              onChange={(e) => setFormData(prev => ({ ...prev, requesterName: e.target.value }))}
              className="mt-1"
              placeholder="Felipe"
              required
            />
          </div>

          {/* Contato Email */}
          <div>
            <Label htmlFor="requesterEmail" className="text-sm font-medium">
              Contato Email <span className="text-red-500">*</span>
              <span className="text-red-600 text-xs ml-2">
                (Ap√≥s digitar o e-mail, pressione Enter ou Tab)
              </span>
            </Label>
            <div className="mt-1 flex items-center space-x-2">
              <Badge variant="secondary" className="bg-blue-900 text-white text-xs px-2 py-1">
                FELIPE.LACERDA@GRUPOOPUS.COM
              </Badge>
              <Input
                id="requesterEmail"
                type="email"
                value={formData.requesterEmail}
                onChange={(e) => setFormData(prev => ({ ...prev, requesterEmail: e.target.value }))}
                placeholder="(Email) Pressione Enter ou Tab"
                className="flex-1"
                required
              />
            </div>
          </div>

          {/* Campos Din√¢micos */}
          {getDynamicFields().map((field) => (
            <div key={field.id}>
              <Label htmlFor={field.id} className="text-sm font-medium">
                {field.label} {field.required && <span className="text-red-500">*</span>}
              </Label>
              {field.type === 'select' ? (
                <Select
                  value={formData.dynamicFields[field.id] || ''}
                  onValueChange={(value) => handleDynamicFieldChange(field.id, value)}
                >
                  <SelectTrigger className="mt-1">
                    <SelectValue placeholder="Selecione uma op√ß√£o" />
                  </SelectTrigger>
                  <SelectContent>
                    {field.options?.map((option) => (
                      <SelectItem key={option} value={option}>
                        {option}
                      </SelectItem>
                    ))}
                  </SelectContent>
                </Select>
              ) : field.type === 'textarea' ? (
                <Textarea
                  id={field.id}
                  value={formData.dynamicFields[field.id] || ''}
                  onChange={(e) => handleDynamicFieldChange(field.id, e.target.value)}
                  placeholder={field.placeholder}
                  className="mt-1"
                  required={field.required}
                />
              ) : (
                <Input
                  id={field.id}
                  type={field.type}
                  value={formData.dynamicFields[field.id] || ''}
                  onChange={(e) => handleDynamicFieldChange(field.id, e.target.value)}
                  placeholder={field.placeholder}
                  className="mt-1"
                  required={field.required}
                />
              )}
            </div>
          ))}

          {/* Prioridade */}
          <div>
            <Label htmlFor="priority" className="text-sm font-medium">Prioridade</Label>
            <Select
              value={formData.priority}
              onValueChange={(value: any) => setFormData(prev => ({ ...prev, priority: value }))}
            >
              <SelectTrigger className="mt-1">
                <SelectValue placeholder="Selecione uma op√ß√£o" />
              </SelectTrigger>
              <SelectContent>
                <SelectItem value="low">Baixa</SelectItem>
                <SelectItem value="medium">M√©dia</SelectItem>
                <SelectItem value="high">Alta</SelectItem>
                <SelectItem value="critical">Cr√≠tica</SelectItem>
              </SelectContent>
            </Select>
          </div>

          {/* Anexos */}
          <div>
            <Label className="text-sm font-medium">
              Anexo <Badge variant="outline" className="ml-2">i</Badge>
            </Label>
            <div className="mt-2 border-2 border-dashed border-gray-300 rounded-lg p-6 text-center">
              <input
                type="file"
                multiple
                onChange={handleFileSelect}
                className="hidden"
                id="file-upload"
              />
              <label htmlFor="file-upload" className="cursor-pointer">
                <Upload className="mx-auto h-8 w-8 text-gray-400 mb-2" />
                <p className="text-sm text-gray-600">Clique aqui para carregar seu arquivo.</p>
              </label>
            </div>

            {/* Lista de arquivos selecionados */}
            {selectedFiles.length > 0 && (
              <div className="mt-2 space-y-2">
                {selectedFiles.map((file, index) => (
                  <div key={index} className="flex items-center justify-between bg-gray-50 p-2 rounded">
                    <div className="flex items-center space-x-2">
                      <Paperclip className="h-4 w-4 text-gray-500" />
                      <span className="text-sm">{file.name}</span>
                      <span className="text-xs text-gray-500">
                        ({(file.size / 1024 / 1024).toFixed(1)} MB)
                      </span>
                    </div>
                    <Button
                      type="button"
                      variant="ghost"
                      size="sm"
                      onClick={() => removeFile(index)}
                    >
                      <X className="h-4 w-4" />
                    </Button>
                  </div>
                ))}
              </div>
            )}
          </div>

          {/* Bot√µes */}
          <div className="flex justify-end space-x-2 pt-4">
            <Button type="button" variant="outline" onClick={onClose}>
              Cancelar
            </Button>
            <Button type="submit" disabled={createTicketMutation.isPending}>
              {createTicketMutation.isPending ? 'Criando...' : 'Criar Ticket'}
            </Button>
          </div>
        </form>
      </DialogContent>
    </Dialog>
  );
}

client/src/components/TicketFinalization.content:
import React, { useState, useEffect } from 'react';

interface TicketFinalizationModalProps {
  isOpen: boolean;
  onClose: () => void;
  onConfirm: (finalizationData: FinalizationData) => void;
  ticket: {
    id: string;
    subject: string;
    createdAt?: string;
  };
}

interface FinalizationData {
  comment: string;
  hoursSpent: string;
  equipmentRemoved: string;
  materialsUsed: string;
  extraCharge: boolean;
  chargeType: string;
}

export default function TicketFinalizationModal({ 
  isOpen, 
  onClose, 
  onConfirm, 
  ticket 
}: TicketFinalizationModalProps) {
  const [formData, setFormData] = useState<FinalizationData>({
    comment: '',
    hoursSpent: '04:30',
    equipmentRemoved: '',
    materialsUsed: '',
    extraCharge: false,
    chargeType: 'Selecione'
  });

  const [isSubmitting, setIsSubmitting] = useState(false);

  if (!isOpen) return null;

  const handleSubmit = async (e: React.FormEvent) => {
    e.preventDefault();
    setIsSubmitting(true);

    try {
      await onConfirm(formData);
      onClose();
    } catch (error) {
      console.error('Erro ao finalizar ticket:', error);
    } finally {
      setIsSubmitting(false);
    }
  };

  const updateFormField = (field: keyof FinalizationData, value: string | boolean) => {
    setFormData(prev => ({ ...prev, [field]: value }));
  };

  return (
    <div className="fixed inset-0 bg-black bg-opacity-50 z-50 flex items-center justify-center p-5">
      <div 
        className="bg-white rounded-lg w-full max-w-2xl max-h-[90vh] overflow-hidden shadow-2xl"
        onClick={(e) => e.stopPropagation()}
      >
        {/* Header */}
        <div className="px-6 py-4 border-b border-gray-200 bg-gradient-to-r from-red-50 to-pink-50">
          <div className="flex items-center justify-between">
            <div>
              <h2 className="text-xl font-bold text-red-600 mb-1">
                Finalizar Ticket {ticket.id}
              </h2>
              <p className="text-sm text-gray-600">{ticket.subject}</p>
            </div>
            <button
              onClick={onClose}
              className="p-2 hover:bg-gray-100 rounded-lg transition-colors"
            >
              <svg className="w-5 h-5 text-gray-500" fill="none" stroke="currentColor" viewBox="0 0 24 24">
                <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M6 18L18 6M6 6l12 12" />
              </svg>
            </button>
          </div>
        </div>

        {/* Content */}
        <form onSubmit={handleSubmit} className="overflow-y-auto max-h-[calc(90vh-140px)]">
          <div className="p-6 space-y-6">

            {/* Coment√°rio */}
            <div>
              <label className="block text-sm font-medium text-gray-700 mb-2">
                Coment√°rio de Finaliza√ß√£o *
              </label>
              <textarea
                value={formData.comment}
                onChange={(e) => updateFormField('comment', e.target.value)}
                placeholder="Descreva como o problema foi resolvido..."
                required
                className="w-full p-3 border border-gray-300 rounded-lg resize-vertical min-h-[100px] focus:ring-2 focus:ring-blue-500 focus:border-transparent"
              />
            </div>

            {/* Horas - Travado */}
            <div>
              <label className="block text-sm font-medium text-gray-700 mb-2">
                Apontamento de Horas (Calculado automaticamente)
              </label>
              <div className="flex items-center p-3 bg-gray-50 border border-gray-200 rounded-lg">
                <svg className="w-5 h-5 text-gray-400 mr-3" fill="none" stroke="currentColor" viewBox="0 0 24 24">
                  <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M12 8v4l3 3m6-3a9 9 0 11-18 0 9 9 0 0118 0z" />
                </svg>
                <input
                  type="text"
                  value={formData.hoursSpent}
                  readOnly
                  className="bg-transparent border-none text-gray-600 font-semibold cursor-not-allowed flex-1"
                />
              </div>
            </div>

            {/* Equipamentos */}
            <div>
              <label className="block text-sm font-medium text-gray-700 mb-2">
                Equipamentos Retirados
              </label>
              <textarea
                value={formData.equipmentRemoved}
                onChange={(e) => updateFormField('equipmentRemoved', e.target.value)}
                placeholder="Liste os equipamentos retirados, se houver..."
                className="w-full p-3 border border-gray-300 rounded-lg resize-vertical min-h-[80px] focus:ring-2 focus:ring-blue-500 focus:border-transparent"
              />
            </div>

            {/* Materiais */}
            <div>
              <label className="block text-sm font-medium text-gray-700 mb-2">
                Materiais Utilizados
              </label>
              <textarea
                value={formData.materialsUsed}
                onChange={(e) => updateFormField('materialsUsed', e.target.value)}
                placeholder="Liste os materiais utilizados..."
                className="w-full p-3 border border-gray-300 rounded-lg resize-vertical min-h-[80px] focus:ring-2 focus:ring-blue-500 focus:border-transparent"
              />
            </div>

            {/* Cobran√ßa Avulsa */}
            <div>
              <label className="flex items-center text-sm font-medium text-gray-700 cursor-pointer">
                <input
                  type="checkbox"
                  checked={formData.extraCharge}
                  onChange={(e) => updateFormField('extraCharge', e.target.checked)}
                  className="mr-3 w-4 h-4 text-blue-600 rounded focus:ring-2 focus:ring-blue-500"
                />
                Cobran√ßa Avulsa
              </label>

              {formData.extraCharge && (
                <div className="mt-3">
                  <select
                    value={formData.chargeType}
                    onChange={(e) => updateFormField('chargeType', e.target.value)}
                    className="w-full p-3 border border-gray-300 rounded-lg focus:ring-2 focus:ring-blue-500 focus:border-transparent"
                  >
                    <option value="Selecione">Selecione o tipo de cobran√ßa</option>
                    <option value="Servi√ßo Extra">Servi√ßo Extra</option>
                    <option value="Hora Extra">Hora Extra</option>
                    <option value="Material Adicional">Material Adicional</option>
                    <option value="Deslocamento">Deslocamento</option>
                  </select>
                </div>
              )}
            </div>

          </div>

          {/* Footer */}
          <div className="px-6 py-4 bg-gray-50 border-t border-gray-200 flex justify-end space-x-3">
            <button
              type="button"
              onClick={onClose}
              className="px-4 py-2 border border-gray-300 rounded-lg text-gray-700 hover:bg-gray-100 transition-colors"
            >
              Cancelar
            </button>
            <button
              type="submit"
              disabled={isSubmitting || !formData.comment.trim()}
              className={`px-6 py-2 rounded-lg font-medium transition-colors ${
                isSubmitting || !formData.comment.trim()
                  ? 'bg-gray-400 text-white cursor-not-allowed'
                  : 'bg-red-600 text-white hover:bg-red-700'
              }`}
            >
              {isSubmitting ? 'Finalizando...' : 'Finalizar Ticket'}
            </button>
          </div>
        </form>
      </div>
    </div>
  );
}

client/src/pages/ConfigurationPage.tsx.content:
import React, { useState } from 'react';
import { useQuery, useMutation, useQueryClient } from '@tanstack/react-query';
import { Card, CardContent, CardHeader, CardTitle } from '@/components/ui/card';
import { Button } from '@/components/ui/button';
import { Input } from '@/components/ui/input';
import { Label } from '@/components/ui/label';
import { Badge } from '@/components/ui/badge';
import { Tabs, TabsContent, TabsList, TabsTrigger } from '@/components/ui/tabs';
import { Dialog, DialogContent, DialogHeader, DialogTitle, DialogTrigger } from '@/components/ui/dialog';
import { Select, SelectContent, SelectItem, SelectTrigger, SelectValue } from '@/components/ui/select';
import { Switch } from '@/components/ui/switch';
import { Trash2, Edit, Plus, Save, X, Settings, Palette, Clock } from 'lucide-react';
import { useToast } from '@/hooks/use-toast';
import { apiRequest } from '@/lib/queryClient';

interface StatusConfig {
  id: string;
  name: string;
  value: string;
  color: string;
  order: number;
  isActive: boolean;
  isDefault: boolean;
}

interface PriorityConfig {
  id: string;
  name: string;
  value: string;
  color: string;
  slaHours: number;
  order: number;
  isActive: boolean;
  isDefault: boolean;
}

export default function ConfigurationPage() {
  const [statusEditModal, setStatusEditModal] = useState<{ isOpen: boolean; status: StatusConfig | null }>({
    isOpen: false,
    status: null
  });
  const [priorityEditModal, setPriorityEditModal] = useState<{ isOpen: boolean; priority: PriorityConfig | null }>({
    isOpen: false,
    priority: null
  });

  const { toast } = useToast();
  const queryClient = useQueryClient();

  // Buscar configura√ß√µes
  const { data: statusConfigs = [], isLoading: statusLoading } = useQuery<StatusConfig[]>({
    queryKey: ['/api/config/status'],
  });

  const { data: priorityConfigs = [], isLoading: priorityLoading } = useQuery<PriorityConfig[]>({
    queryKey: ['/api/config/priority'],
  });

  // Mutations para status
  const updateStatusMutation = useMutation({
    mutationFn: async (status: StatusConfig) => {
      const response = await fetch(`/api/config/status/${status.id}`, {
        method: 'PUT',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify(status),
      });
      if (!response.ok) throw new Error('Failed to update status');
      return response.json();
    },
    onSuccess: () => {
      queryClient.invalidateQueries({ queryKey: ['/api/config/status'] });
      setStatusEditModal({ isOpen: false, status: null });
      toast({
        title: 'Status atualizado',
        description: 'Configura√ß√£o de status salva com sucesso.',
      });
    },
    onError: () => {
      toast({
        title: 'Erro',
        description: 'Erro ao atualizar status.',
        variant: 'destructive',
      });
    },
  });

  const createStatusMutation = useMutation({
    mutationFn: async (status: Omit<StatusConfig, 'id'>) => {
      const response = await fetch('/api/config/status', {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify(status),
      });
      if (!response.ok) throw new Error('Failed to create status');
      return response.json();
    },
    onSuccess: () => {
      queryClient.invalidateQueries({ queryKey: ['/api/config/status'] });
      setStatusEditModal({ isOpen: false, status: null });
      toast({
        title: 'Status criado',
        description: 'Novo status criado com sucesso.',
      });
    },
  });

  const deleteStatusMutation = useMutation({
    mutationFn: async (statusId: string) => {
      const response = await fetch(`/api/config/status/${statusId}`, {
        method: 'DELETE',
      });
      if (!response.ok) throw new Error('Failed to delete status');
      return response.json();
    },
    onSuccess: () => {
      queryClient.invalidateQueries({ queryKey: ['/api/config/status'] });
      toast({
        title: 'Status removido',
        description: 'Status removido com sucesso.',
      });
    },
  });

  // Mutations para prioridades
  const updatePriorityMutation = useMutation({
    mutationFn: async (priority: PriorityConfig) => {
      const response = await fetch(`/api/config/priority/${priority.id}`, {
        method: 'PUT',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify(priority),
      });
      if (!response.ok) throw new Error('Failed to update priority');
      return response.json();
    },
    onSuccess: () => {
      queryClient.invalidateQueries({ queryKey: ['/api/config/priority'] });
      setPriorityEditModal({ isOpen: false, priority: null });
      toast({
        title: 'Prioridade atualizada',
        description: 'Configura√ß√£o de prioridade salva com sucesso.',
      });
    },
  });

  const createPriorityMutation = useMutation({
    mutationFn: async (priority: Omit<PriorityConfig, 'id'>) => {
      const response = await fetch('/api/config/priority', {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify(priority),
      });
      if (!response.ok) throw new Error('Failed to create priority');
      return response.json();
    },
    onSuccess: () => {
      queryClient.invalidateQueries({ queryKey: ['/api/config/priority'] });
      setPriorityEditModal({ isOpen: false, priority: null });
      toast({
        title: 'Prioridade criada',
        description: 'Nova prioridade criada com sucesso.',
      });
    },
  });

  const deletePriorityMutation = useMutation({
    mutationFn: async (priorityId: string) => {
      const response = await fetch(`/api/config/priority/${priorityId}`, {
        method: 'DELETE',
      });
      if (!response.ok) throw new Error('Failed to delete priority');
      return response.json();
    },
    onSuccess: () => {
      queryClient.invalidateQueries({ queryKey: ['/api/config/priority'] });
      toast({
        title: 'Prioridade removida',
        description: 'Prioridade removida com sucesso.',
      });
    },
  });

  const getColorPreview = (color: string) => (
    <div
      className="w-6 h-6 rounded-full border border-gray-300"
      style={{ backgroundColor: color }}
    />
  );

  const colorOptions = [
    { value: '#dc2626', label: 'Vermelho', preview: '#dc2626' },
    { value: '#f59e0b', label: 'Laranja', preview: '#f59e0b' },
    { value: '#eab308', label: 'Amarelo', preview: '#eab308' },
    { value: '#10b981', label: 'Verde', preview: '#10b981' },
    { value: '#3b82f6', label: 'Azul', preview: '#3b82f6' },
    { value: '#8b5cf6', label: 'Roxo', preview: '#8b5cf6' },
    { value: '#6b7280', label: 'Cinza', preview: '#6b7280' },
  ];

  return (
    <div className="p-6 max-w-6xl mx-auto">
      <div className="mb-6">
        <h1 className="text-2xl font-bold flex items-center gap-2">
          <Settings className="w-6 h-6" />
          Configura√ß√µes do Sistema
        </h1>
        <p className="text-gray-600 mt-2">
          Gerencie status, prioridades e outras configura√ß√µes do sistema de tickets.
        </p>
      </div>

      <Tabs defaultValue="status" className="w-full">
        <TabsList className="grid w-full grid-cols-2">
          <TabsTrigger value="status">Status de Tickets</TabsTrigger>
          <TabsTrigger value="priority">Prioridades</TabsTrigger>
        </TabsList>

        {/* Status Configuration */}
        <TabsContent value="status" className="space-y-4">
          <Card>
            <CardHeader className="flex flex-row items-center justify-between">
              <CardTitle className="flex items-center gap-2">
                <Palette className="w-5 h-5" />
                Configura√ß√µes de Status
              </CardTitle>
              <Dialog open={statusEditModal.isOpen} onOpenChange={(open) => 
                setStatusEditModal({ isOpen: open, status: open ? statusEditModal.status : null })
              }>
                <DialogTrigger asChild>
                  <Button 
                    onClick={() => setStatusEditModal({ 
                      isOpen: true, 
                      status: {
                        id: '',
                        name: '',
                        value: '',
                        color: '#3b82f6',
                        order: statusConfigs.length + 1,
                        isActive: true,
                        isDefault: false
                      }
                    })}
                  >
                    <Plus className="w-4 h-4 mr-2" />
                    Novo Status
                  </Button>
                </DialogTrigger>
                <StatusEditModal
                  status={statusEditModal.status}
                  onSave={(status) => {
                    if (status.id) {
                      updateStatusMutation.mutate(status);
                    } else {
                      const { id, ...newStatus } = status;
                      createStatusMutation.mutate(newStatus);
                    }
                  }}
                  colorOptions={colorOptions}
                />
              </Dialog>
            </CardHeader>
            <CardContent>
              {statusLoading ? (
                <div>Carregando...</div>
              ) : (
                <div className="space-y-3">
                  {statusConfigs
                    .sort((a, b) => a.order - b.order)
                    .map((status) => (
                    <div key={status.id} className="flex items-center justify-between p-3 border rounded-lg">
                      <div className="flex items-center gap-3">
                        {getColorPreview(status.color)}
                        <div>
                          <div className="font-medium">{status.name}</div>
                          <div className="text-sm text-gray-500">Valor: {status.value}</div>
                        </div>
                        <div className="flex gap-2">
                          {status.isDefault && (
                            <Badge variant="secondary">Padr√£o</Badge>
                          )}
                          {!status.isActive && (
                            <Badge variant="outline">Inativo</Badge>
                          )}
                        </div>
                      </div>
                      <div className="flex gap-2">
                        <Button
                          variant="outline"
                          size="sm"
                          onClick={() => setStatusEditModal({ isOpen: true, status })}
                        >
                          <Edit className="w-4 h-4" />
                        </Button>
                        <Button
                          variant="outline"
                          size="sm"
                          onClick={() => deleteStatusMutation.mutate(status.id)}
                          disabled={status.isDefault}
                        >
                          <Trash2 className="w-4 h-4" />
                        </Button>
                      </div>
                    </div>
                  ))}
                </div>
              )}
            </CardContent>
          </Card>
        </TabsContent>

        {/* Priority Configuration */}
        <TabsContent value="priority" className="space-y-4">
          <Card>
            <CardHeader className="flex flex-row items-center justify-between">
              <CardTitle className="flex items-center gap-2">
                <Clock className="w-5 h-5" />
                Configura√ß√µes de Prioridade
              </CardTitle>
              <Dialog open={priorityEditModal.isOpen} onOpenChange={(open) => 
                setPriorityEditModal({ isOpen: open, priority: open ? priorityEditModal.priority : null })
              }>
                <DialogTrigger asChild>
                  <Button 
                    onClick={() => setPriorityEditModal({ 
                      isOpen: true, 
                      priority: {
                        id: '',
                        name: '',
                        value: '',
                        color: '#3b82f6',
                        slaHours: 24,
                        order: priorityConfigs.length + 1,
                        isActive: true,
                        isDefault: false
                      }
                    })}
                  >
                    <Plus className="w-4 h-4 mr-2" />
                    Nova Prioridade
                  </Button>
                </DialogTrigger>
                <PriorityEditModal
                  priority={priorityEditModal.priority}
                  onSave={(priority) => {
                    if (priority.id) {
                      updatePriorityMutation.mutate(priority);
                    } else {
                      const { id, ...newPriority } = priority;
                      createPriorityMutation.mutate(newPriority);
                    }
                  }}
                  colorOptions={colorOptions}
                />
              </Dialog>
            </CardHeader>
            <CardContent>
              {priorityLoading ? (
                <div>Carregando...</div>
              ) : (
                <div className="space-y-3">
                  {priorityConfigs
                    .sort((a, b) => a.order - b.order)
                    .map((priority) => (
                    <div key={priority.id} className="flex items-center justify-between p-3 border rounded-lg">
                      <div className="flex items-center gap-3">
                        {getColorPreview(priority.color)}
                        <div>
                          <div className="font-medium">{priority.name}</div>
                          <div className="text-sm text-gray-500">
                            Valor: {priority.value} ‚Ä¢ SLA: {priority.slaHours}h
                          </div>
                        </div>
                        <div className="flex gap-2">
                          {priority.isDefault && (
                            <Badge variant="secondary">Padr√£o</Badge>
                          )}
                          {!priority.isActive && (
                            <Badge variant="outline">Inativo</Badge>
                          )}
                        </div>
                      </div>
                      <div className="flex gap-2">
                        <Button
                          variant="outline"
                          size="sm"
                          onClick={() => setPriorityEditModal({ isOpen: true, priority })}
                        >
                          <Edit className="w-4 h-4" />
                        </Button>
                        <Button
                          variant="outline"
                          size="sm"
                          onClick={() => deletePriorityMutation.mutate(priority.id)}
                          disabled={priority.isDefault}
                        >
                          <Trash2 className="w-4 h-4" />
                        </Button>
                      </div>
                    </div>
                  ))}
                </div>
              )}
            </CardContent>
          </Card>
        </TabsContent>
      </Tabs>
    </div>
  );
}

// Modal para edi√ß√£o de status
function StatusEditModal({ 
  status, 
  onSave, 
  colorOptions 
}: { 
  status: StatusConfig | null; 
  onSave: (status: StatusConfig) => void; 
  colorOptions: Array<{ value: string; label: string; preview: string }>;
}) {
  const [editedStatus, setEditedStatus] = useState<StatusConfig | null>(status);

  React.useEffect(() => {
    setEditedStatus(status);
  }, [status]);

  if (!editedStatus) return null;

  return (
    <DialogContent className="max-w-md">
      <DialogHeader>
        <DialogTitle>
          {editedStatus.id ? 'Editar Status' : 'Novo Status'}
        </DialogTitle>
      </DialogHeader>
      <div className="space-y-4">
        <div>
          <Label htmlFor="statusName">Nome</Label>
          <Input
            id="statusName"
            value={editedStatus.name}
            onChange={(e) => setEditedStatus({ ...editedStatus, name: e.target.value })}
            placeholder="Ex: Em Andamento"
          />
        </div>
        <div>
          <Label htmlFor="statusValue">Valor (identificador)</Label>
          <Input
            id="statusValue"
            value={editedStatus.value}
            onChange={(e) => setEditedStatus({ ...editedStatus, value: e.target.value })}
            placeholder="Ex: in_progress"
          />
        </div>
        <div>
          <Label htmlFor="statusColor">Cor</Label>
          <Select
            value={editedStatus.color}
            onValueChange={(value) => setEditedStatus({ ...editedStatus, color: value })}
          >
            <SelectTrigger>
              <SelectValue />
            </SelectTrigger>
            <SelectContent>
              {colorOptions.map((color) => (
                <SelectItem key={color.value} value={color.value}>
                  <div className="flex items-center gap-2">
                    <div
                      className="w-4 h-4 rounded-full"
                      style={{ backgroundColor: color.preview }}
                    />
                    {color.label}
                  </div>
                </SelectItem>
              ))}
            </SelectContent>
          </Select>
        </div>
        <div>
          <Label htmlFor="statusOrder">Ordem</Label>
          <Input
            id="statusOrder"
            type="number"
            value={editedStatus.order}
            onChange={(e) => setEditedStatus({ ...editedStatus, order: parseInt(e.target.value) || 1 })}
          />
        </div>
        <div className="flex items-center space-x-2">
          <Switch
            id="statusActive"
            checked={editedStatus.isActive}
            onCheckedChange={(checked) => setEditedStatus({ ...editedStatus, isActive: checked })}
          />
          <Label htmlFor="statusActive">Ativo</Label>
        </div>
        <div className="flex items-center space-x-2">
          <Switch
            id="statusDefault"
            checked={editedStatus.isDefault}
            onCheckedChange={(checked) => setEditedStatus({ ...editedStatus, isDefault: checked })}
          />
          <Label htmlFor="statusDefault">Padr√£o</Label>
        </div>
        <div className="flex justify-end gap-2">
          <Button
            onClick={() => onSave(editedStatus)}
            disabled={!editedStatus.name || !editedStatus.value}
          >
            <Save className="w-4 h-4 mr-2" />
            Salvar
          </Button>
        </div>
      </div>
    </DialogContent>
  );
}

