  );
}

client/src/components/StatsCard.tsx.content:
import { LucideIcon } from "lucide-react";

interface StatsCardProps {
  title: string;
  value: string | number;
  change: string;
  changeType: "positive" | "negative" | "neutral";
  icon: LucideIcon;
  iconColor: string;
  iconBgColor: string;
  onClick?: () => void;
}

export default function StatsCard({ 
  title, 
  value, 
  change, 
  changeType, 
  icon: Icon, 
  iconColor,
  iconBgColor,
  onClick
}: StatsCardProps) {
  const getChangeColor = () => {
    switch (changeType) {
      case "positive":
        return "text-success";
      case "negative":
        return "text-error";
      default:
        return "text-gray-50";
    }
  };

  const getChangeIcon = () => {
    if (changeType === "positive") return "‚Üó";
    if (changeType === "negative") return "‚Üò";
    return "";
  };

  return (
    <div 
      className={`bg-card border border-border rounded p-6 shadow-enterprise transition-enterprise hover:shadow-enterprise-lg ${onClick ? 'cursor-pointer hover:bg-gray-50' : ''}`}
      onClick={onClick}
    >
      <div className="flex items-center justify-between">
        <div className="flex-1">
          <p className="text-sm font-medium text-muted-foreground mb-1">{title}</p>
          <p className="text-2xl font-semibold text-foreground mb-2">{value}</p>
          <div className={`inline-flex items-center text-xs font-medium ${getChangeColor()}`}>
            <span className="mr-1">{getChangeIcon()}</span>
            {change}
          </div>
        </div>
        <div className={`w-12 h-12 ${iconBgColor} rounded flex items-center justify-center`}>
          <Icon className={iconColor} size={20} />
        </div>
      </div>
    </div>
  );
}

client/src/components/TicketTrendsChart..content:
import { useQuery } from "@tanstack/react-query";
import { AreaChart, Area, XAxis, YAxis, CartesianGrid, Tooltip, ResponsiveContainer, Legend } from "recharts";
import type { TrendData } from "@shared/schema";

interface TicketTrendsChartProps {
  days: number;
  filters?: {
    dateFilter: string;
    priorityFilter: string;
    departmentFilter: string;
  };
}

export default function TicketTrendsChart({ days, filters }: TicketTrendsChartProps) {
  // Build query parameters
  const buildQueryParams = () => {
    const params = new URLSearchParams();
    params.append('days', days.toString());
    if (filters?.dateFilter) params.append('dateFilter', filters.dateFilter);
    if (filters?.priorityFilter && filters.priorityFilter !== 'all') params.append('priority', filters.priorityFilter);
    if (filters?.departmentFilter && filters.departmentFilter !== 'all') params.append('department', filters.departmentFilter);
    return params.toString();
  };

  const queryParams = buildQueryParams();

  const { data: trendData, isLoading } = useQuery<TrendData[]>({
    queryKey: ["/api/dashboard/trends", queryParams],
    queryFn: () => fetch(`/api/dashboard/trends?${queryParams}`).then(res => res.json()),
  });

  if (isLoading) {
    return (
      <div className="h-64 flex items-center justify-center">
        <div className="text-gray-50">Loading chart...</div>
      </div>
    );
  }

  if (!trendData || trendData.length === 0) {
    return (
      <div className="h-64 flex items-center justify-center">
        <div className="text-gray-500">Sem dados dispon√≠veis</div>
      </div>
    );
  }



  return (
    <div className="h-64">
      <ResponsiveContainer width="100%" height="100%">
        <AreaChart data={trendData} margin={{ top: 10, right: 30, left: 0, bottom: 0 }}>
          <defs>
            <linearGradient id="colorCreated" x1="0" y1="0" x2="0" y2="1">
              <stop offset="5%" stopColor="#3b82f6" stopOpacity={0.3}/>
              <stop offset="95%" stopColor="#3b82f6" stopOpacity={0.05}/>
            </linearGradient>
            <linearGradient id="colorResolved" x1="0" y1="0" x2="0" y2="1">
              <stop offset="5%" stopColor="#22c55e" stopOpacity={0.3}/>
              <stop offset="95%" stopColor="#22c55e" stopOpacity={0.05}/>
            </linearGradient>
