import React, { useState, useEffect } from 'react';
import Button from '@/components/Button';
import { Select, SelectContent, SelectItem, SelectTrigger, SelectValue } from '@/components/ui/select';
import Icon from '@/components/AppIcon';

interface User {
  id: string;
  name: string;
  email: string;
  role: string;
  status: string;
  department: string;
  lastLogin: string;
}

interface ActivityEntry {
  id: string;
  userId: string;
  userName: string;
  userRole: string;
  type: string;
  action: string;
  target?: string;
  timestamp: string;
  ipAddress: string;
  userAgent: string;
  location: string;
  severity: 'low' | 'medium' | 'high' | 'critical';
}

interface ActivityMonitorProps {
  onClose: () => void;
  users: User[];
}

export default function ActivityMonitor({
  onClose,
  users
}: ActivityMonitorProps) {
  const [activities, setActivities] = useState<ActivityEntry[]>([]);
  const [filteredActivities, setFilteredActivities] = useState<ActivityEntry[]>([]);
  const [filters, setFilters] = useState({
    user: 'all',
    type: 'all',
    severity: 'all',
    timeRange: 'today'
  });
  const [isLive, setIsLive] = useState(true);
  const [autoRefresh, setAutoRefresh] = useState(true);

  // Mock activity data
  const mockActivities: ActivityEntry[] = [
    {
      id: '1',
      userId: '1',
      userName: 'Jo√£o Silva Santos',
      userRole: 'admin',
      type: 'authentication',
      action: 'Login realizado com sucesso',
      timestamp: new Date().toISOString(),
      ipAddress: '192.168.1.100',
      userAgent: 'Mozilla/5.0 (Windows NT 10.0; Win64; x64) AppleWebKit/537.36',
      location: 'S√£o Paulo, SP',
      severity: 'low'
    },
    {
      id: '2',
      userId: '2',
      userName: 'Maria Santos Oliveira',
      userRole: 'operator',
      type: 'ticket',
      action: 'Ticket #1234 foi resolvido',
      target: 'Ticket #1234',
      timestamp: new Date(Date.now() - 300000).toISOString(),
      ipAddress: '192.168.1.101',
      userAgent: 'Mozilla/5.0 (Windows NT 10.0; Win64; x64) AppleWebKit/537.36',
      location: 'S√£o Paulo, SP',
      severity: 'medium'
    },
    {
      id: '3',
      userId: '1',
      userName: 'Jo√£o Silva Santos',
      userRole: 'admin',
      type: 'user_management',
      action: 'Novo usu√°rio criado: Carlos Lima',
      target: 'Carlos Lima',
      timestamp: new Date(Date.now() - 600000).toISOString(),
      ipAddress: '192.168.1.100',
      userAgent: 'Mozilla/5.0 (Windows NT 10.0; Win64; x64) AppleWebKit/537.36',
      location: 'S√£o Paulo, SP',
      severity: 'high'
    },
    {
      id: '4',
      userId: '3',
      userName: 'Carlos Eduardo Lima',
      userRole: 'user',
      type: 'authentication',
      action: 'Tentativa de login falhada (senha incorreta)',
      timestamp: new Date(Date.now() - 900000).toISOString(),
      ipAddress: '192.168.1.102',
      userAgent: 'Mozilla/5.0 (Windows NT 10.0; Win64; x64) AppleWebKit/537.36',
      location: 'Rio de Janeiro, RJ',
      severity: 'critical'
    },
    {
      id: '5',
      userId: '2',
      userName: 'Maria Santos Oliveira',
      userRole: 'operator',
      type: 'settings',
      action: 'Configura√ß√µes de perfil atualizadas',
      timestamp: new Date(Date.now() - 1200000).toISOString(),
      ipAddress: '192.168.1.101',
      userAgent: 'Mozilla/5.0 (Windows NT 10.0; Win64; x64) AppleWebKit/537.36',
      location: 'S√£o Paulo, SP',
      severity: 'low'
    }
  ];

  useEffect(() => {
    setActivities(mockActivities);
    setFilteredActivities(mockActivities);
  }, []);

  // Auto refresh simulation
  useEffect(() => {
    if (!autoRefresh || !isLive) return;

    const interval = setInterval(() => {
      // Simulate new activity
      const newActivity: ActivityEntry = {
        id: Date.now().toString(),
        userId: users[Math.floor(Math.random() * users.length)].id,
        userName: users[Math.floor(Math.random() * users.length)].name,
        userRole: users[Math.floor(Math.random() * users.length)].role,
        type: ['authentication', 'ticket', 'settings', 'user_management'][Math.floor(Math.random() * 4)],
        action: 'Nova atividade simulada',
        timestamp: new Date().toISOString(),
        ipAddress: `192.168.1.${Math.floor(Math.random() * 255)}`,
        userAgent: 'Mozilla/5.0 (Windows NT 10.0; Win64; x64) AppleWebKit/537.36',
        location: ['S√£o Paulo, SP', 'Rio de Janeiro, RJ', 'Belo Horizonte, MG'][Math.floor(Math.random() * 3)],
        severity: ['low', 'medium', 'high'][Math.floor(Math.random() * 3)] as 'low' | 'medium' | 'high'
      };

      setActivities(prev => [newActivity, ...prev.slice(0, 49)]); // Keep only 50 most recent
    }, 10000); // Every 10 seconds

    return () => clearInterval(interval);
  }, [autoRefresh, isLive, users]);

  // Apply filters
  useEffect(() => {
    let filtered = [...activities];

    if (filters.user) {
      filtered = filtered.filter(activity => activity.userId === filters.user);
    }

    if (filters.type) {
      filtered = filtered.filter(activity => activity.type === filters.type);
    }

    if (filters.severity) {
      filtered = filtered.filter(activity => activity.severity === filters.severity);
    }

    const now = new Date();
    const timeRangeFilter = (activity: ActivityEntry) => {
      const activityTime = new Date(activity.timestamp);
      const diffInMs = now.getTime() - activityTime.getTime();

      switch (filters.timeRange) {
        case 'today':
          return diffInMs <= 24 * 60 * 60 * 1000;
        case 'week':
          return diffInMs <= 7 * 24 * 60 * 60 * 1000;
        case 'month':
          return diffInMs <= 30 * 24 * 60 * 60 * 1000;
        default:
          return true;
      }
    };

    filtered = filtered.filter(timeRangeFilter);
    setFilteredActivities(filtered);
  }, [activities, filters]);

  const formatTimestamp = (timestamp: string) => {
    const date = new Date(timestamp);
    const now = new Date();
    const diffInMs = now.getTime() - date.getTime();
    const diffInMinutes = Math.floor(diffInMs / (1000 * 60));

    if (diffInMinutes < 1) return 'Agora mesmo';
    if (diffInMinutes < 60) return `${diffInMinutes} min atr√°s`;
    if (diffInMinutes < 1440) return `${Math.floor(diffInMinutes / 60)}h atr√°s`;

    return date.toLocaleString('pt-BR', {
      day: '2-digit',
      month: '2-digit',
      hour: '2-digit',
      minute: '2-digit'
    });
  };

  const getActivityIcon = (type: string) => {
    switch (type) {
      case 'authentication': return 'LogIn';
      case 'ticket': return 'Ticket';
      case 'user_management': return 'Users';
      case 'settings': return 'Settings';
      default: return 'Activity';
    }
  };

  const getActivityColor = (severity: string) => {
    switch (severity) {
      case 'critical': return 'text-red-600 bg-red-50 dark:bg-red-900/20';
      case 'high': return 'text-orange-600 bg-orange-50 dark:bg-orange-900/20';
      case 'medium': return 'text-yellow-600 bg-yellow-50 dark:bg-yellow-900/20';
      case 'low': return 'text-green-600 bg-green-50 dark:bg-green-900/20';
      default: return 'text-slate-600 bg-slate-50 dark:bg-slate-700';
    }
  };

  const getSeverityLabel = (severity: string) => {
    switch (severity) {
      case 'critical': return 'Cr√≠tica';
      case 'high': return 'Alta';
      case 'medium': return 'M√©dia';
      case 'low': return 'Baixa';
      default: return severity;
    }
  };

  const getTypeLabel = (type: string) => {
    switch (type) {
      case 'authentication': return 'Autentica√ß√£o';
      case 'ticket': return 'Tickets';
      case 'user_management': return 'Usu√°rios';
      case 'settings': return 'Configura√ß√µes';
      default: return type;
    }
  };

  const exportActivities = () => {
    const csv = [
      ['Timestamp', 'Usu√°rio', 'Fun√ß√£o', 'Tipo', 'A√ß√£o', 'Severidade', 'IP', 'Localiza√ß√£o'].join(','),
      ...filteredActivities.map(activity => [
        activity.timestamp,
        activity.userName,
        activity.userRole,
        getTypeLabel(activity.type),
        activity.action,
        getSeverityLabel(activity.severity),
        activity.ipAddress,
        activity.location
      ].join(','))
    ].join('\n');

    const blob = new Blob([csv], { type: 'text/csv' });
    const url = URL.createObjectURL(blob);
    const a = document.createElement('a');
    a.href = url;
    a.download = `atividades-usuarios-${new Date().toISOString().split('T')[0]}.csv`;
    a.click();
    URL.revokeObjectURL(url);
  };

  return (
    <div className="fixed inset-0 bg-black bg-opacity-50 flex items-center justify-center z-50">
      <div className="bg-white dark:bg-slate-800 rounded-lg shadow-2xl max-w-6xl w-full max-h-[90vh] overflow-hidden">
        {/* Header */}
        <div className="flex items-center justify-between p-6 border-b border-slate-200 dark:border-slate-700">
          <div className="flex items-center space-x-3">
            <div className="flex items-center justify-center w-10 h-10 bg-blue-50 dark:bg-blue-900/20 rounded-lg">
              <Icon name="Activity" size={20} className="text-blue-600 dark:text-blue-400" />
            </div>
            <div>
              <h2 className="text-xl font-bold text-slate-900 dark:text-slate-100">
                Monitor de Atividade dos Usu√°rios
              </h2>
              <p className="text-sm text-slate-600 dark:text-slate-400">
                Acompanhe em tempo real as a√ß√µes realizadas pelos usu√°rios
              </p>
            </div>
          </div>

          <div className="flex items-center space-x-3">
            <div className="flex items-center space-x-2">
              <div className={`w-2 h-2 rounded-full ${isLive ? 'bg-green-500 animate-pulse' : 'bg-red-500'}`}></div>
              <span className="text-sm text-slate-600 dark:text-slate-400">
                {isLive ? 'Ao Vivo' : 'Pausado'}
              </span>
            </div>

            <Button
              variant="outline"
              size="sm"
              onClick={() => setIsLive(!isLive)}
              iconName={isLive ? "Pause" : "Play"}
            />

            <Button
              variant="outline"
              size="sm"
              onClick={exportActivities}
              iconName="Download"
            />

            <Button
              variant="ghost"
              onClick={onClose}
              iconName="X"
            />
          </div>
        </div>

        {/* Filters */}
        <div className="p-4 border-b border-slate-200 dark:border-slate-700 bg-slate-50 dark:bg-slate-700">
          <div className="grid grid-cols-1 md:grid-cols-5 gap-4">
            <div>
              <Select value={filters.user} onValueChange={(value) => setFilters({...filters, user: value})}>
                <SelectTrigger>
                  <SelectValue placeholder="Todos os usu√°rios" />
                </SelectTrigger>
                <SelectContent>
                  <SelectItem value="all">Todos os usu√°rios</SelectItem>
                  {users.map(user => (
                    <SelectItem key={user.id} value={user.id}>
                      {user.name}
                    </SelectItem>
                  ))}
                </SelectContent>
              </Select>
            </div>

            <div>
              <Select value={filters.type} onValueChange={(value) => setFilters({...filters, type: value})}>
                <SelectTrigger>
                  <SelectValue placeholder="Todos os tipos" />
                </SelectTrigger>
                <SelectContent>
                  <SelectItem value="all">Todos os tipos</SelectItem>
                  <SelectItem value="authentication">Autentica√ß√£o</SelectItem>
                  <SelectItem value="ticket">Tickets</SelectItem>
                  <SelectItem value="user_management">Usu√°rios</SelectItem>
                  <SelectItem value="settings">Configura√ß√µes</SelectItem>
                </SelectContent>
              </Select>
            </div>

            <div>
              <Select value={filters.severity} onValueChange={(value) => setFilters({...filters, severity: value})}>
                <SelectTrigger>
                  <SelectValue placeholder="Todas severidades" />
                </SelectTrigger>
                <SelectContent>
                  <SelectItem value="all">Todas severidades</SelectItem>
                  <SelectItem value="critical">Cr√≠tica</SelectItem>
                  <SelectItem value="high">Alta</SelectItem>
                  <SelectItem value="medium">M√©dia</SelectItem>
                  <SelectItem value="low">Baixa</SelectItem>
                </SelectContent>
              </Select>
            </div>

            <div>
              <Select value={filters.timeRange} onValueChange={(value) => setFilters({...filters, timeRange: value})}>
                <SelectTrigger>
                  <SelectValue />
                </SelectTrigger>
                <SelectContent>
                  <SelectItem value="today">Hoje</SelectItem>
                  <SelectItem value="week">Esta semana</SelectItem>
                  <SelectItem value="month">Este m√™s</SelectItem>
                  <SelectItem value="all">Todos</SelectItem>
                </SelectContent>
              </Select>
            </div>

            <div className="flex items-center space-x-2">
              <Button
                variant="outline"
                size="sm"
                onClick={() => setAutoRefresh(!autoRefresh)}
                iconName={autoRefresh ? "Pause" : "Play"}
                className="flex-1"
              >
                {autoRefresh ? 'Pausar' : 'Iniciar'}
              </Button>
            </div>
          </div>
        </div>

        {/* Activity List */}
        <div className="p-6 max-h-[calc(90vh-12rem)] overflow-y-auto">
          <div className="flex items-center justify-between mb-4">
            <h3 className="text-lg font-semibold text-slate-900 dark:text-slate-100">
              Atividades Recentes ({filteredActivities.length})
            </h3>
            <div className="text-sm text-slate-600 dark:text-slate-400">
              Atualizado {formatTimestamp(new Date().toISOString())}
            </div>
          </div>

          {filteredActivities.length === 0 ? (
            <div className="text-center py-12">
              <Icon name="Activity" size={48} className="text-slate-400 dark:text-slate-600 mx-auto mb-4" />
              <h3 className="text-lg font-semibold text-slate-900 dark:text-slate-100 mb-2">
                Nenhuma atividade encontrada
              </h3>
              <p className="text-slate-600 dark:text-slate-400">
                N√£o h√° atividades que correspondam aos filtros aplicados.
              </p>
            </div>
          ) : (
            <div className="space-y-4">
              {filteredActivities.map(activity => (
                <div
                  key={activity.id}
                  className={`flex items-start space-x-4 p-4 rounded-lg border transition-all duration-200 ${getActivityColor(activity.severity)}`}
                >
                  <div className="flex-shrink-0 mt-1">
                    <Icon name={getActivityIcon(activity.type) as any} size={16} />
                  </div>

                  <div className="flex-1 min-w-0">
                    <div className="flex items-center justify-between">
                      <div className="flex items-center space-x-2">
                        <span className="font-medium text-slate-900 dark:text-slate-100">
                          {activity.userName}
                        </span>
                        <span className="text-xs px-2 py-1 bg-white dark:bg-slate-800 rounded text-slate-600 dark:text-slate-400">
                          {activity.userRole}
                        </span>
                      </div>
                      <div className="flex items-center space-x-2 text-xs text-slate-600 dark:text-slate-400">
                        <span>{getSeverityLabel(activity.severity)}</span>
                        <span>‚Ä¢</span>
                        <span>{formatTimestamp(activity.timestamp)}</span>
                      </div>
                    </div>

                    <p className="text-sm text-slate-800 dark:text-slate-200 mt-1">
                      {activity.action}
                    </p>

                    <div className="flex items-center space-x-4 mt-2 text-xs text-slate-600 dark:text-slate-400">
                      <div className="flex items-center space-x-1">
                        <Icon name="MapPin" size={12} />
                        <span>{activity.location}</span>
                      </div>
                      <div className="flex items-center space-x-1">
                        <Icon name="Globe" size={12} />
                        <span>{activity.ipAddress}</span>
                      </div>
                      <div className="flex items-center space-x-1">
                        <Icon name="Tag" size={12} />
                        <span>{getTypeLabel(activity.type)}</span>
                      </div>
                    </div>
                  </div>
                </div>
              ))}
            </div>
          )}
        </div>
      </div>
    </div>
  );
}

client/src/components/NewSidebar.tsx.content:
import React, { useState, useEffect } from 'react';
import { useLocation } from 'wouter';
import Icon from './AppIcon';
import Button from './Button';

interface SidebarProps {
  isOpen?: boolean;
  onClose: () => void;
  isCollapsed?: boolean;
  onToggleCollapse: () => void;
  onOpenTicketModal: () => void;
}

const NewSidebar: React.FC<SidebarProps> = ({ 
  isOpen = false, 
  onClose, 
  isCollapsed = false, 
  onToggleCollapse,
  onOpenTicketModal 
}) => {
  const [location, setLocation] = useLocation();
  const [activeSubmenu, setActiveSubmenu] = useState<string | null>(null);

  const navigate = (path: string) => setLocation(path);

  const navigationItems = [
    {
      id: 'dashboard',
      label: 'Dashboard',
      path: '/',
      icon: 'LayoutDashboard',
      description: 'Vis√£o geral e m√©tricas'
    },
    {
      id: 'tickets',
      label: 'Tickets',
      icon: 'Ticket',
      description: 'Gerenciar tickets',
      submenu: [
        { label: 'Kanban Board', path: '/tickets', icon: 'Kanban' },
        { label: 'Criar Ticket', action: 'modal', icon: 'Plus' }
      ]
    },
    {
      id: 'monitoring',
      label: 'SLA Monitor',
      path: '/sla',
      icon: 'Clock',
      description: 'Acompanhar performance'
    },
    {
      id: 'reports',
      label: 'Relat√≥rios Avan√ßados',
      path: '/reports',
      icon: 'BarChart3',
      description: 'An√°lises e insights'
    },
    {
      id: 'administration',
      label: 'Administra√ß√£o',
      icon: 'Settings',
      description: 'Gerenciar sistema',
      submenu: [
        { label: 'Gerenciar Usu√°rios', path: '/users', icon: 'Users' },
        { label: 'Departamentos', path: '/departments', icon: 'Building2' },
        { label: 'Categorias', path: '/categories', icon: 'FolderTree' },
        { label: 'Formul√°rios', path: '/forms', icon: 'FileText' },
        { label: 'Campos Customiz√°veis', path: '/fields', icon: 'Settings2' },
        { label: 'Sistema de Aprova√ß√µes', path: '/approvals', icon: 'CheckCircle' },
        { label: 'Configura√ß√£o SLA', path: '/sla-config', icon: 'Clock' },
        { label: 'Status e Prioridades', path: '/config', icon: 'Settings' }
      ]
    }
  ];

  const handleNavigation = (path: string) => {
    navigate(path);
    if (window.innerWidth < 1024) {
      onClose();
    }
  };

  const handleSubmenuToggle = (itemId: string) => {
    if (isCollapsed) return;
    setActiveSubmenu(activeSubmenu === itemId ? null : itemId);
  };

  const isActivePath = (path: string) => {
    return location === path;
  };

  const isActiveParent = (item: any) => {
    if (item?.path && isActivePath(item?.path)) return true;
    if (item?.submenu) {
      return item?.submenu?.some((subItem: any) => isActivePath(subItem?.path));
    }
    return false;
  };

  useEffect(() => {
    // Auto-expand active parent menu
    const activeParentItem = navigationItems?.find(item => isActiveParent(item));
    if (activeParentItem && activeParentItem?.submenu && !isCollapsed) {
      setActiveSubmenu(activeParentItem?.id);
    }
  }, [location, isCollapsed]);

  const sidebarWidth = isCollapsed ? 'w-16' : 'w-60';
  const sidebarClasses = `
    fixed top-16 left-0 h-full bg-card border-r border-border shadow-enterprise-lg z-30
    transition-all duration-300 ease-in-out
    ${sidebarWidth}
    lg:translate-x-0
    ${isOpen ? 'translate-x-0' : '-translate-x-full lg:translate-x-0'}
  `;

  return (
    <>
      {/* Mobile Overlay */}
      {isOpen && (
        <div
          className="fixed inset-0 bg-black/50 z-20 lg:hidden"
          onClick={onClose}
        />
      )}

      {/* Collapse Toggle Button - External */}
      <button
        onClick={onToggleCollapse}
        className="fixed bg-white border border-gray-300 rounded-full p-2 shadow-lg hover:shadow-xl transition-all duration-300 z-50 hover:bg-gray-50"
        title={isCollapsed ? 'Expandir sidebar' : 'Recolher sidebar'}
        style={{ 
          top: '5rem',
          left: isCollapsed ? '4.5rem' : '15.5rem',
          transform: 'translateX(-50%)'
        }}
      >
        <Icon name={isCollapsed ? "ChevronRight" : "ChevronLeft"} size={16} />
      </button>

      {/* Sidebar */}
      <aside className={sidebarClasses}>
        <div className="flex flex-col h-full">
          {/* Header */}
          <div className={`flex items-center border-b border-border ${isCollapsed ? 'justify-center p-2' : 'justify-between p-4'}`}>
            <div className="flex items-center space-x-3">
              <div className="flex items-center justify-center w-8 h-8 bg-primary rounded-lg">
                <Icon name="Ticket" size={20} color="white" />
              </div>
              {!isCollapsed && (
                <div>
                  <h1 className="text-lg font-semibold text-foreground">TicketFlow Pro</h1>
                  <p className="text-xs text-muted-foreground">Enterprise Edition</p>
                </div>
              )}
            </div>

            {/* Close Button (Mobile only when not collapsed) */}
            {!isCollapsed && (
              <Button
                variant="ghost"
                size="icon"
                onClick={onClose}
                className="lg:hidden transition-enterprise"
              >
                <Icon name="X" size={16} />
              </Button>
            )}
          </div>

          {/* Navigation */}
          <nav className="flex-1 p-4 space-y-2 overflow-y-auto">
            {navigationItems?.map((item) => (
              <div key={item?.id}>
                {/* Main Navigation Item */}
                <div
                  className={`
                    group relative flex items-center rounded-lg transition-enterprise cursor-pointer
                    ${isActiveParent(item) 
                      ? 'bg-primary text-primary-foreground' 
                      : 'text-foreground hover:bg-muted'
                    }
                    ${isCollapsed ? 'justify-center p-3' : 'p-3'}
                  `}
                  onClick={() => {
                    if (item?.path) {
                      handleNavigation(item?.path);
                    } else if (item?.submenu) {
                      handleSubmenuToggle(item?.id);
                    }
                  }}
                >
                  <div className={`flex items-center justify-center ${isCollapsed ? 'w-6 h-6' : ''}`}>
                    <Icon 
                      name={item?.icon} 
                      size={isCollapsed ? 18 : 20} 
                      className={`
                        ${isCollapsed ? '' : 'mr-3'} 
                        ${isActiveParent(item) ? 'text-primary-foreground' : ''}
                      `}
                    />
                  </div>

                  {!isCollapsed && (
                    <>
                      <div className="flex-1">
                        <div className="font-medium text-sm">{item?.label}</div>
                        {item?.description && (
                          <div className={`text-xs ${
                            isActiveParent(item) ? 'text-primary-foreground/80' : 'text-muted-foreground'
                          }`}>
                            {item?.description}
                          </div>
                        )}
                      </div>

                      {item?.submenu && (
                        <Icon 
                          name={activeSubmenu === item?.id ? "ChevronDown" : "ChevronRight"} 
                          size={16}
                          className={`transition-transform ${
                            isActiveParent(item) ? 'text-primary-foreground' : ''
                          }`}
                        />
                      )}
                    </>
                  )}

                  {/* Tooltip for Collapsed State */}
                  {isCollapsed && (
                    <div className="absolute left-full ml-3 px-3 py-2 bg-gray-900 text-white rounded-md shadow-lg opacity-0 group-hover:opacity-100 transition-opacity pointer-events-none z-50 min-w-48">
                      <div className="text-sm font-medium mb-1">{item?.label}</div>
                      {item?.description && (
                        <div className="text-xs opacity-80 mb-2">{item?.description}</div>
                      )}
                      {/* Submenu items em tooltip */}
                      {item?.submenu && (
                        <div className="space-y-1 border-t border-gray-700 pt-2">
                          {item?.submenu?.map((subItem: any) => (
                            <div 
                              key={subItem?.path || subItem?.action} 
                              className="text-xs opacity-80 cursor-pointer hover:opacity-100 p-1 rounded hover:bg-gray-800"
                              onClick={() => {
                                if (subItem?.action === 'modal') {
                                  onOpenTicketModal();
                                } else {
                                  handleNavigation(subItem?.path);
                                }
                              }}
                            >
                              {subItem?.label}
                            </div>
                          ))}
                        </div>
                      )}
                    </div>
                  )}
                </div>

                {/* Submenu */}
                {item?.submenu && !isCollapsed && activeSubmenu === item?.id && (
                  <div className="ml-6 mt-2 space-y-1 border-l-2 border-border pl-4">
                    {item?.submenu?.map((subItem: any) => (
                      <div
                        key={subItem?.path || subItem?.action}
                        className={`
                          flex items-center p-2 rounded-md text-sm transition-enterprise cursor-pointer
                          ${isActivePath(subItem?.path)
                            ? 'bg-primary/10 text-primary font-medium' 
                            : 'text-muted-foreground hover:text-foreground hover:bg-muted'
                          }
                        `}
                        onClick={() => {
                          if (subItem?.action === 'modal') {
                            onOpenTicketModal();
                          } else {
                            handleNavigation(subItem?.path);
                          }
                        }}
                      >
                        <Icon name={subItem?.icon} size={16} className="mr-3" />
                        {subItem?.label}
                      </div>
                    ))}
                  </div>
                )}
              </div>
            ))}
          </nav>

          {/* Footer */}
          <div className="p-4 border-t border-border">
            {!isCollapsed ? (
              <div className="space-y-2">
                <Button
                  variant="ghost"
                  size="sm"
                  iconName="HelpCircle"
                  iconPosition="left"
                  iconSize={16}
                  className="w-full justify-start transition-enterprise"
                >
                  Ajuda & Suporte
                </Button>
                <Button
                  variant="ghost"
                  size="sm"
                  iconName="LogOut"
                  iconPosition="left"
                  iconSize={16}
                  className="w-full justify-start text-error hover:text-error hover:bg-error/10 transition-enterprise"
                  onClick={() => handleNavigation('/login')}
                >
                  Sair
                </Button>
              </div>
            ) : (
              <div className="space-y-2">
                <Button
                  variant="ghost"
                  size="icon"
                  className="w-full transition-enterprise group relative"
                >
                  <Icon name="HelpCircle" size={16} />
                  <div className="absolute left-full ml-2 px-2 py-1 bg-popover border border-border rounded-md shadow-enterprise-lg opacity-0 group-hover:opacity-100 transition-opacity pointer-events-none whitespace-nowrap z-50">
                    Ajuda & Suporte
                  </div>
                </Button>
                <Button
                  variant="ghost"
                  size="icon"
                  className="w-full text-error hover:text-error hover:bg-error/10 transition-enterprise group relative"
                  onClick={() => handleNavigation('/login')}
                >
                  <Icon name="LogOut" size={16} />
                  <div className="absolute left-full ml-2 px-2 py-1 bg-popover border border-border rounded-md shadow-enterprise-lg opacity-0 group-hover:opacity-100 transition-opacity pointer-events-none whitespace-nowrap z-50">
                    Sair
                  </div>
                </Button>
              </div>
            )}
          </div>
        </div>
      </aside>
    </>
  );
};

export default NewSidebar;

client/src/components/tickets/Assignment.content:
import React from 'react';
import { Select, SelectContent, SelectItem, SelectTrigger, SelectValue } from '@/components/ui/select';
import { Switch } from '@/components/ui/switch';
import Icon from '@/components/AppIcon';

interface Operator {
  id: string;
  name: string;
  department: string;
  specialties: string[];
  currentTickets: number;
  status: 'online' | 'busy' | 'offline';
  serviceDeskId: string;
}

interface ServiceDesk {
  id: string;
  name: string;
  activeOperators: number;
  defaultSla: number;
}

interface AssignmentControlsProps {
  formData: any;
  onFormChange: (field: string, value: any) => void;
  errors: any;
  operators: Operator[];
  serviceDesks: ServiceDesk[];
  userRole: string;
}

export default function AssignmentControls({
  formData,
  onFormChange,
  errors,
  operators,
  serviceDesks,
  userRole
}: AssignmentControlsProps) {
  const handleInputChange = (field: string, value: any) => {
    onFormChange(field, value);
  };

  const filteredOperators = formData?.serviceDesk ? 
    operators?.filter(op => op?.serviceDeskId === formData?.serviceDesk) : [];

  const getStatusColor = (status: string) => {
    switch (status) {
      case 'online': return 'text-green-600';
      case 'busy': return 'text-yellow-600';
      case 'offline': return 'text-red-600';
      default: return 'text-slate-500';
    }
  };

  const getStatusIcon = (status: string) => {
    switch (status) {
      case 'online': return 'CheckCircle';
      case 'busy': return 'Clock';
      case 'offline': return 'XCircle';
      default: return 'Circle';
    }
  };

  return (
    <div className="bg-white dark:bg-slate-800 rounded-lg border border-slate-200 dark:border-slate-700 p-6">
      <div className="flex items-center space-x-3 mb-6">
        <div className="flex items-center justify-center w-10 h-10 bg-indigo-50 dark:bg-indigo-900/20 rounded-lg">
          <Icon name="Users" size={20} className="text-indigo-600 dark:text-indigo-400" />
        </div>
        <div>
          <h2 className="text-lg font-semibold text-slate-900 dark:text-slate-100">
            Atribui√ß√£o e Roteamento
          </h2>
          <p className="text-sm text-slate-600 dark:text-slate-400">
            Configure como o chamado ser√° distribu√≠do e tratado
          </p>
        </div>
      </div>

      <div className="space-y-6">
        {/* Service Desk Selection */}
        <div>
          <label className="text-sm font-medium text-slate-900 dark:text-slate-100 mb-2 block">
            Mesa de Atendimento *
          </label>
          <Select 
            value={formData?.serviceDesk || ''} 
            onValueChange={(value) => {
              handleInputChange('serviceDesk', value);
              handleInputChange('assignedOperator', '');
            }}
          >
            <SelectTrigger className={errors?.serviceDesk ? 'border-red-500' : ''}>
              <SelectValue placeholder="Selecione a mesa de atendimento" />
            </SelectTrigger>
            <SelectContent>
              {serviceDesks?.map(desk => (
                <SelectItem key={desk?.id} value={desk?.id}>
                  <div className="flex items-center justify-between w-full">
                    <div>
                      <div className="font-medium">{desk?.name}</div>
                      <div className="text-xs text-slate-500">
                        {desk?.activeOperators} operadores ativos ‚Ä¢ SLA padr√£o: {desk?.defaultSla}h
                      </div>
                    </div>
                  </div>
                </SelectItem>
              ))}
            </SelectContent>
          </Select>
          {errors?.serviceDesk && (
            <p className="text-sm text-red-600 dark:text-red-400 mt-1">
              {errors?.serviceDesk}
            </p>
          )}
        </div>

        {/* Assignment Options */}
        <div className="space-y-4">
          <div className="flex items-center justify-between">
            <div>
              <label className="text-sm font-medium text-slate-900 dark:text-slate-100">
                Atribui√ß√£o Autom√°tica
              </label>
              <p className="text-xs text-slate-600 dark:text-slate-400">
                Sistema escolhe automaticamente o melhor operador dispon√≠vel
              </p>
            </div>
            <Switch
              checked={formData?.autoAssign || false}
              onCheckedChange={(checked) => {
                handleInputChange('autoAssign', checked);
                if (checked) {
                  handleInputChange('assignedOperator', '');
                }
              }}
            />
          </div>

          {/* Manual Assignment */}
          {!formData?.autoAssign && (
            <div>
              <label className="text-sm font-medium text-slate-900 dark:text-slate-100 mb-2 block">
                Operador Espec√≠fico
              </label>
              <Select 
                value={formData?.assignedOperator || ''} 
                onValueChange={(value) => handleInputChange('assignedOperator', value)}
                disabled={!formData?.serviceDesk || formData?.autoAssign}
              >
                <SelectTrigger>
                  <SelectValue placeholder="Selecione um operador (opcional)" />
                </SelectTrigger>
                <SelectContent>
                  <SelectItem value="auto">
                    <span className="text-slate-500">Deixar para distribui√ß√£o autom√°tica</span>
                  </SelectItem>
                  {filteredOperators?.map(operator => (
                    <SelectItem key={operator?.id} value={operator?.id}>
                      <div className="flex items-center space-x-3 w-full">
                        <Icon 
                          name={getStatusIcon(operator?.status) as any} 
                          size={12} 
                          className={getStatusColor(operator?.status)} 
                        />
                        <div className="flex-1">
                          <div className="flex items-center justify-between">
                            <span className="font-medium">{operator?.name}</span>
                            <span className="text-xs text-slate-500">
                              {operator?.currentTickets} chamados
                            </span>
                          </div>
                          <div className="text-xs text-slate-500">
                            {operator?.department} ‚Ä¢ {operator?.specialties?.join(', ')}
                          </div>
                        </div>
                      </div>
                    </SelectItem>
                  ))}
                </SelectContent>
              </Select>
            </div>
          )}
        </div>

        {/* Advanced Options */}
        <div className="border-t border-slate-200 dark:border-slate-700 pt-4">
          <h4 className="text-sm font-medium text-slate-900 dark:text-slate-100 mb-4">
            Configura√ß√µes Avan√ßadas
          </h4>

          <div className="space-y-4">
            <div className="flex items-center justify-between">
              <div>
                <label className="text-sm font-medium text-slate-900 dark:text-slate-100">
                  Notifica√ß√£o por E-mail
                </label>
                <p className="text-xs text-slate-600 dark:text-slate-400">
                  Enviar e-mail para o solicitante quando o chamado for criado
                </p>
              </div>
              <Switch
                checked={formData?.emailNotification !== false}
                onCheckedChange={(checked) => handleInputChange('emailNotification', checked)}
              />
            </div>

            {userRole === 'admin' && (
              <>
                <div className="flex items-center justify-between">
                  <div>
                    <label className="text-sm font-medium text-slate-900 dark:text-slate-100">
                      Chamado Interno
                    </label>
                    <p className="text-xs text-slate-600 dark:text-slate-400">
                      Marcar como chamado interno da equipe
                    </p>
                  </div>
                  <Switch
                    checked={formData?.internalTicket || false}
                    onCheckedChange={(checked) => handleInputChange('internalTicket', checked)}
                  />
                </div>

                <div className="flex items-center justify-between">
                  <div>
                    <label className="text-sm font-medium text-slate-900 dark:text-slate-100">
                      Pular Valida√ß√µes
                    </label>
                    <p className="text-xs text-slate-600 dark:text-slate-400">
                      Ignorar algumas valida√ß√µes autom√°ticas do sistema
                    </p>
                  </div>
                  <Switch
                    checked={formData?.skipValidations || false}
                    onCheckedChange={(checked) => handleInputChange('skipValidations', checked)}
                  />
                </div>
              </>
            )}
          </div>
        </div>

        {/* Auto Assignment Preview */}
        {formData?.autoAssign && formData?.serviceDesk && (
          <div className="p-4 bg-indigo-50 dark:bg-indigo-900/20 rounded-lg border border-indigo-200 dark:border-indigo-800">
            <div className="flex items-start space-x-3">
              <Icon name="Cpu" size={16} className="text-indigo-600 dark:text-indigo-400 mt-0.5" />
              <div>
                <h4 className="text-sm font-medium text-slate-900 dark:text-slate-100 mb-1">
                  Crit√©rios de Atribui√ß√£o Autom√°tica
                </h4>
                <ul className="text-sm text-slate-600 dark:text-slate-400 space-y-1">
                  <li>‚Ä¢ Especialidade do operador na categoria selecionada</li>
                  <li>‚Ä¢ Carga atual de trabalho (n√∫mero de chamados em aberto)</li>
                  <li>‚Ä¢ Status de disponibilidade (online/ocupado)</li>
                  <li>‚Ä¢ Balanceamento de carga entre a equipe</li>
                </ul>
              </div>
            </div>
          </div>
        )}

        {/* Operator Availability */}
        {formData?.serviceDesk && !formData?.autoAssign && (
          <div className="p-4 bg-slate-50 dark:bg-slate-700 rounded-lg border border-slate-200 dark:border-slate-600">
            <div className="flex items-center space-x-3 mb-3">
              <Icon name="Users" size={16} className="text-slate-600 dark:text-slate-400" />
              <h4 className="text-sm font-medium text-slate-900 dark:text-slate-100">
                Status da Equipe
              </h4>
            </div>

            <div className="grid grid-cols-2 md:grid-cols-4 gap-4 text-center">
              <div>
                <div className="text-lg font-semibold text-green-600 dark:text-green-400">
                  {filteredOperators?.filter(op => op?.status === 'online')?.length}
                </div>
                <div className="text-xs text-slate-600 dark:text-slate-400">Dispon√≠veis</div>
              </div>
              <div>
                <div className="text-lg font-semibold text-yellow-600 dark:text-yellow-400">
                  {filteredOperators?.filter(op => op?.status === 'busy')?.length}
                </div>
                <div className="text-xs text-slate-600 dark:text-slate-400">Ocupados</div>
              </div>
              <div>
                <div className="text-lg font-semibold text-slate-600 dark:text-slate-400">
                  {filteredOperators?.reduce((acc, op) => acc + op?.currentTickets, 0)}
                </div>
                <div className="text-xs text-slate-600 dark:text-slate-400">Chamados Ativos</div>
              </div>
              <div>
                <div className="text-lg font-semibold text-blue-600 dark:text-blue-400">
                  {Math.round(filteredOperators?.reduce((acc, op) => acc + op?.currentTickets, 0) / Math.max(filteredOperators?.length, 1))}
                </div>
                <div className="text-xs text-slate-600 dark:text-slate-400">M√©dia por Operador</div>
              </div>
            </div>
          </div>
        )}
      </div>
    </div>
  );
}

