      {/* User Details Modal */}
      <UserDetailsModal
        userId={selectedUserForDetails}
        isOpen={userDetailsModalOpen}
        onClose={() => {
          setUserDetailsModalOpen(false);
          setSelectedUserForDetails(null);
        }}
      />
    </div>
  );
}

