import React, { useState, useEffect } from 'react';
import { Button } from '@/components/ui/button';
import { Input } from '@/components/ui/input';
import { Select, SelectContent, SelectItem, SelectTrigger, SelectValue } from '@/components/ui/select';
import { Badge } from '@/components/ui/badge';
import { Card, CardContent, CardHeader, CardTitle } from '@/components/ui/card';
import { Tabs, TabsContent, TabsList, TabsTrigger } from '@/components/ui/tabs';
import { Separator } from '@/components/ui/separator';
import { Switch } from '@/components/ui/switch';
import { BarChart, Bar, LineChart, Line, XAxis, YAxis, CartesianGrid, Tooltip, ResponsiveContainer } from 'recharts';
import { 
  User, 
  Mail, 
  Phone, 
  MapPin, 
  Calendar, 
  Shield, 
  Activity, 
  BarChart3, 
  Clock, 
  CheckCircle, 
  Star, 
  Edit, 
  Save, 
  X, 
  Settings,
  Award,
  Target,
  TrendingUp,
  Users,
  Ticket
} from 'lucide-react';

interface UserType {
  id: string;
  name: string;
  email: string;
  role: string;
  status: string;
  department: string;
  lastLogin: string;
  createdAt: string;
  avatar?: string | null;
  phone?: string;
  extension?: string;
  location?: string;
  manager?: string | null;
  permissions?: string[];
  ticketsAssigned?: number;
  ticketsResolved?: number;
  averageResolutionTime?: string;
  satisfactionRating?: number;
}

interface Department {
  id: string;
  name: string;
  userCount: number;
}

interface Role {
  id: string;
  name: string;
  description: string;
}

interface UserDetailsPanelProps {
  userId: string;
  onClose: () => void;
  user?: UserType;
  departments: Department[];
  roles: Role[];
  isEditing?: boolean;
  onUserUpdate?: (updatedUser: any) => void;
}

export default function UserDetailsPanel({
  userId,
  onClose,
  user,
  departments,
  roles,
  isEditing: initialIsEditing = false,
  onUserUpdate
}: UserDetailsPanelProps) {
  const [isEditing, setIsEditing] = useState(initialIsEditing);
  const [activeTab, setActiveTab] = useState('overview');
  const [hasUnsavedChanges, setHasUnsavedChanges] = useState(false);
  const [formData, setFormData] = useState({
    name: user?.name || '',
    email: user?.email || '',
    phone: user?.phone || '',
    extension: user?.extension || '',
    role: user?.role || '',
    department: user?.department || '',
    status: user?.status || 'active',
    location: user?.location || '',
    manager: user?.manager || ''
  });

  useEffect(() => {
    setIsEditing(initialIsEditing);
  }, [initialIsEditing]);

  if (!user) {
    return (
      <div className="fixed inset-0 bg-black/50 backdrop-blur-sm flex items-center justify-center z-50">
        <Card className="w-full max-w-md">
          <CardContent className="p-8 text-center">
            <div className="w-16 h-16 bg-red-100 dark:bg-red-900/20 rounded-full flex items-center justify-center mx-auto mb-4">
              <User className="w-8 h-8 text-red-600" />
            </div>
            <h3 className="text-lg font-semibold mb-2">Usu√°rio n√£o encontrado</h3>
            <p className="text-slate-600 dark:text-slate-400 mb-6">
              O usu√°rio selecionado n√£o p√¥de ser carregado.
            </p>
            <Button onClick={onClose} className="w-full">Fechar</Button>
          </CardContent>
        </Card>
      </div>
    );
  }

  const handleSave = async () => {
    try {
      console.log('Salvando dados do usu√°rio:', formData);

      // Usar userId do prop ao inv√©s de user.id
      const userIdToUpdate = userId || user?.id;

      if (!userIdToUpdate) {
        throw new Error('ID do usu√°rio n√£o encontrado');
      }

      // Fazer chamada para API de atualiza√ß√£o de usu√°rio  
      const response = await fetch(`/api/users/${userIdToUpdate}`, {
        method: 'PATCH',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify({
          name: formData.name,
          email: formData.email,
          role: formData.role,
          departmentId: formData.department,
          phone: formData.phone,
          extension: formData.extension,
          location: formData.location
        }),
      });

      if (!response.ok) {
        const errorData = await response.json();
        throw new Error(errorData.message || `HTTP error! status: ${response.status}`);
      }

      const updatedUser = await response.json();
      console.log('Usu√°rio atualizado com sucesso:', updatedUser);

      setIsEditing(false);
      setHasUnsavedChanges(false);

      // Atualizar formData com os dados salvos
      setFormData({
        name: updatedUser.name,
        email: updatedUser.email,
        phone: updatedUser.phone || '',
        extension: updatedUser.extension || '',
        role: updatedUser.role,
        department: updatedUser.departmentId || '',
        status: updatedUser.status || 'active',
        location: updatedUser.location || '',
        manager: updatedUser.manager || ''
      });

      // Notificar componente pai da atualiza√ß√£o
      if (onUserUpdate) {
        onUserUpdate(updatedUser);
      }

      alert('Dados do usu√°rio salvos com sucesso!');
    } catch (error) {
      console.error('Erro na requisi√ß√£o:', error);
      alert(`Erro ao salvar usu√°rio: ${error instanceof Error ? error.message : 'Erro desconhecido'}`);
    }
  };

  const handleCancel = () => {
    setFormData({
      name: user?.name || '',
      email: user?.email || '',
      phone: user?.phone || '',
      extension: user?.extension || '',
      role: user?.role || '',
      department: user?.department || '',
      status: user?.status || 'active',
      location: user?.location || '',
      manager: user?.manager || ''
    });
    setIsEditing(false);
  };

  const formatDate = (dateString: string) => {
    return new Date(dateString).toLocaleDateString('pt-BR', {
      day: '2-digit',
      month: '2-digit',
      year: 'numeric',
      hour: '2-digit',
      minute: '2-digit'
    });
  };

  const getRoleName = (roleId: string) => {
    switch (roleId) {
      case 'administrador': return 'Administrador';
      case 'supervisor': return 'Supervisor';
      case 'colaborador': return 'Colaborador';
      default: return roleId;
    }
  };

  const getStatusLabel = (status: string) => {
    switch (status) {
      case 'active': return 'Ativo';
      case 'inactive': return 'Inativo';
      case 'pending': return 'Pendente';
      default: return status;
    }
  };

  const getStatusColor = (status: string) => {
    switch (status) {
      case 'active': return 'bg-green-100 text-green-800 dark:bg-green-900/20 dark:text-green-400';
      case 'inactive': return 'bg-red-100 text-red-800 dark:bg-red-900/20 dark:text-red-400';
      case 'pending': return 'bg-yellow-100 text-yellow-800 dark:bg-yellow-900/20 dark:text-yellow-400';
      default: return 'bg-slate-100 text-slate-800 dark:bg-slate-700 dark:text-slate-300';
    }
  };

  const getRoleColor = (role: string) => {
    switch (role) {
      case 'administrador': return 'bg-purple-100 text-purple-800 dark:bg-purple-900/20 dark:text-purple-400';
      case 'supervisor': return 'bg-blue-100 text-blue-800 dark:bg-blue-900/20 dark:text-blue-400';
      case 'colaborador': return 'bg-green-100 text-green-800 dark:bg-green-900/20 dark:text-green-400';
      default: return 'bg-slate-100 text-slate-800 dark:bg-slate-700 dark:text-slate-300';
    }
  };

  const getInitials = (name: string) => {
    return name.split(' ').map(n => n[0]).join('').toUpperCase().slice(0, 2);
  };

  // Mock performance data
  const performanceData = [
    { month: 'Jan', tickets: 45, satisfaction: 4.5 },
    { month: 'Fev', tickets: 52, satisfaction: 4.7 },
    { month: 'Mar', tickets: 48, satisfaction: 4.6 },
    { month: 'Abr', tickets: 61, satisfaction: 4.8 },
    { month: 'Mai', tickets: 55, satisfaction: 4.7 },
    { month: 'Jun', tickets: 67, satisfaction: 4.9 }
  ];

  const weeklyActivity = [
    { day: 'Seg', hours: 8.5 },
    { day: 'Ter', hours: 7.2 },
