          <Button
            variant="ghost"
            size="icon"
            onClick={onSidebarToggle}
            className="lg:hidden"
          >
            <Icon name="Menu" size={20} />
          </Button>

          {/* Logo do Grupo OPUS - apenas a logo */}
          <div className="flex items-center">
            <img 
