import React, { useState } from 'react';
import Icon from '../../../components/AppIcon';
import Button from '../../../components/ui/Button';
import Input from '../../../components/ui/Input';
import Select from '../../../components/ui/Select';
import { Checkbox } from '../../../components/ui/Checkbox';

const FilterPanel = ({ filters, onFiltersChange, onSavePreset, savedPresets, onLoadPreset }) => {
  const [showAdvanced, setShowAdvanced] = useState(false);
  const [presetName, setPresetName] = useState('');
  const [showSavePreset, setShowSavePreset] = useState(false);

  const statusOptions = [
    { value: '', label: 'Todos os Status' },
    { value: 'Ativo', label: 'Ativo' },
    { value: 'Inativo', label: 'Inativo' },
    { value: 'Suspenso', label: 'Suspenso' },
    { value: 'Pendente', label: 'Pendente' }
  ];

  const departmentOptions = [
    { value: '', label: 'Todos os Departamentos' },
    { value: 'TI', label: 'Tecnologia da Informa√ß√£o' },
    { value: 'Suporte', label: 'Suporte T√©cnico' },
    { value: 'Vendas', label: 'Vendas' },
    { value: 'Marketing', label: 'Marketing' },
    { value: 'RH', label: 'Recursos Humanos' },
    { value: 'Financeiro', label: 'Financeiro' }
  ];

  const roleOptions = [
    { value: '', label: 'Todas as Fun√ß√µes' },
    { value: 'Administrador', label: 'Administrador' },
    { value: 'Supervisor', label: 'Supervisor' },
    { value: 'Atendente', label: 'Atendente' },
    { value: 'Usu√°rio', label: 'Usu√°rio' }
  ];

  const activityOptions = [
    { value: '', label: 'Qualquer Per√≠odo' },
    { value: 'today', label: 'Hoje' },
    { value: 'week', label: '√öltima Semana' },
    { value: 'month', label: '√öltimo M√™s' },
    { value: 'quarter', label: '√öltimo Trimestre' },
    { value: 'never', label: 'Nunca Acessou' }
  ];

  const handleFilterChange = (key, value) => {
    onFiltersChange({
      ...filters,
      [key]: value
    });
  };

  const handleClearFilters = () => {
    onFiltersChange({
      search: '',
      status: '',
      department: '',
      role: '',
      activity: '',
      dateFrom: '',
      dateTo: '',
      hasAvatar: false,
      hasSignature: false,
      ldapSync: false
