import React, { useState } from 'react';
import Icon from '../../../components/AppIcon';
import Button from '../../../components/ui/Button';
import Input from '../../../components/ui/Input';

import { Checkbox } from '../../../components/ui/Checkbox';

const FilterSidebar = ({ 
  isOpen, 
  onClose, 
  filters, 
  onFiltersChange, 
  onClearFilters,
  ticketCounts 
}) => {
  const [localFilters, setLocalFilters] = useState(filters);

  const handleFilterChange = (key, value) => {
    const newFilters = { ...localFilters, [key]: value };
    setLocalFilters(newFilters);
    onFiltersChange(newFilters);
  };

  const handleArrayFilterChange = (key, value, checked) => {
    const currentArray = localFilters?.[key] || [];
    const newArray = checked 
      ? [...currentArray, value]
      : currentArray?.filter(item => item !== value);

    handleFilterChange(key, newArray);
  };

  const priorityOptions = [
    { value: 'Critical', label: 'Cr√≠tica', count: ticketCounts?.priority?.Critical || 0 },
    { value: 'High', label: 'Alta', count: ticketCounts?.priority?.High || 0 },
    { value: 'Medium', label: 'M√©dia', count: ticketCounts?.priority?.Medium || 0 },
    { value: 'Low', label: 'Baixa', count: ticketCounts?.priority?.Low || 0 }
  ];

  const statusOptions = [
    { value: 'todo', label: 'A Fazer', count: ticketCounts?.status?.todo || 0 },
