import React, { useState } from 'react';
import Button from '../../../components/ui/Button';
import Icon from '../../../components/AppIcon';

const FormActions = ({ 
  onSubmit, 
  onSaveDraft, 
  onCancel, 
  isSubmitting, 
  isDraftSaving, 
  formData,
  hasUnsavedChanges 
}) => {
  const [showConfirmDialog, setShowConfirmDialog] = useState(false);

  const handleSubmit = (e) => {
    e?.preventDefault();
    onSubmit();
  };

  const handleCancel = () => {
    if (hasUnsavedChanges) {
      setShowConfirmDialog(true);
    } else {
      onCancel();
    }
  };

  const confirmCancel = () => {
    setShowConfirmDialog(false);
    onCancel();
  };

  const getEstimatedSLA = () => {
    if (formData?.customSla) return `${formData?.customSla}h`;

    switch (formData?.priority) {
      case 'critical': return '1h';
      case 'high': return '4h';
      case 'medium': return '8h';
      case 'low': return '24h';
      default: return '8h';
    }
  };

  return (
    <>
      <div className="bg-card rounded-lg border border-border p-6 shadow-enterprise">
        <div className="flex items-center justify-between mb-6">
          <div className="flex items-center space-x-3">
            <div className="flex items-center justify-center w-10 h-10 bg-primary/10 rounded-lg">
              <Icon name="Send" size={20} className="text-primary" />
            </div>
            <div>
              <h2 className="text-lg font-semibold text-foreground">Finalizar Chamado</h2>
              <p className="text-sm text-muted-foreground">Revise as informa√ß√µes e crie o ticket</p>
            </div>
          </div>
        </div>

        {/* Summary Card */}
        <div className="mb-6 p-4 bg-muted/50 rounded-lg border border-border">
          <h4 className="text-sm font-medium text-foreground mb-3">Resumo do Chamado</h4>
          <div className="grid grid-cols-1 md:grid-cols-2 gap-4 text-sm">
            <div>
              <span className="text-muted-foreground">Solicitante:</span>
              <span className="ml-2 text-foreground font-medium">
                {formData?.requesterName || 'N√£o informado'}
              </span>
            </div>
            <div>
              <span className="text-muted-foreground">Prioridade:</span>
              <span className="ml-2 text-foreground font-medium">
                {formData?.priority ? 
                  ['low', 'medium', 'high', 'critical']?.includes(formData?.priority) ? 
                    { low: 'Baixa', medium: 'M√©dia', high: 'Alta', critical: 'Cr√≠tica' }?.[formData?.priority] 
                    : 'M√©dia' :'M√©dia'
                }
              </span>
            </div>
            <div>
              <span className="text-muted-foreground">Categoria:</span>
              <span className="ml-2 text-foreground font-medium">
                {formData?.category || 'N√£o selecionada'}
              </span>
            </div>
