            {/* Left Panel - Filters and User List */}
            <div className="xl:col-span-2 space-y-6">
              {/* Filters */}
              <FilterPanel
                filters={filters}
                onFiltersChange={setFilters}
                onSavePreset={handleSavePreset}
                savedPresets={savedPresets}
                onLoadPreset={handleLoadPreset}
              />

              {/* Toolbar */}
              <div className="flex flex-col sm:flex-row sm:items-center sm:justify-between space-y-4 sm:space-y-0">
                <div className="flex items-center space-x-2">
                  <Button
                    variant={viewMode === 'grid' ? 'default' : 'outline'}
                    size="sm"
                    iconName="Grid3X3"
                    iconSize={16}
                    onClick={() => setViewMode('grid')}
                  >
                    Grade
                  </Button>
                  <Button
                    variant={viewMode === 'cards' ? 'default' : 'outline'}
                    size="sm"
                    iconName="LayoutGrid"
                    iconSize={16}
                    onClick={() => setViewMode('cards')}
                  >
                    Cart√µes
                  </Button>
                </div>

                {selectedUsers?.length > 0 && (
                  <Button
                    variant="outline"
                    size="sm"
                    iconName="Settings"
                    iconPosition="left"
                    iconSize={16}
                    onClick={() => setShowBulkActions(true)}
                  >
                    A√ß√µes em Lote ({selectedUsers?.length})
                  </Button>
                )}
              </div>

              {/* User List */}
              {viewMode === 'grid' ? (
                <UserDataGrid
                  users={filteredUsers}
                  selectedUsers={selectedUsers}
                  onUserSelect={handleUserSelect}
                  onSelectAll={handleSelectAll}
                  onEdit={handleEditUser}
                  onDelete={handleDeleteUser}
                  onToggleStatus={handleToggleUserStatus}
                  sortConfig={sortConfig}
                  onSort={handleSort}
                />
              ) : (
                <div className="space-y-4">
                  <div className="flex items-center justify-between">
                    <h3 className="text-sm font-semibold text-foreground">
                      Usu√°rios ({filteredUsers?.length})
                    </h3>
                  </div>
                  <div className="grid grid-cols-1 md:grid-cols-2 gap-4">
                    {filteredUsers?.map((user) => (
                      <UserCard
                        key={user?.id}
                        user={user}
                        onEdit={handleEditUser}
                        onDelete={handleDeleteUser}
                        onToggleStatus={handleToggleUserStatus}
                        isSelected={selectedUsers?.includes(user?.id)}
                        onSelect={handleUserSelect}
                      />
                    ))}
                  </div>
                </div>
              )}
            </div>

            {/* Right Panel - Activity Monitor */}
            <div className="space-y-6">
              <ActivityMonitor />
            </div>
          </div>
        </div>
      </main>
      {/* User Details Panel */}
      {showUserDetails && (
        <div className="fixed inset-0 bg-black/50 z-50 flex items-center justify-center p-4">
          <div className="w-full max-w-4xl h-full max-h-[90vh]">
            <UserDetailsPanel
              user={selectedUser}
              onSave={handleSaveUser}
              onClose={() => {
                setShowUserDetails(false);
                setSelectedUser(null);
              }}
            />
          </div>
        </div>
      )}
      {/* Bulk Actions Panel */}
      {showBulkActions && (
        <div className="fixed inset-0 bg-black/50 z-50 flex items-center justify-center p-4">
          <div className="w-full max-w-2xl">
            <BulkActionsPanel
              selectedUsers={selectedUsers}
              onBulkAction={handleBulkAction}
              onClose={() => setShowBulkActions(false)}
            />
          </div>
        </div>
      )}
    </div>
  );
};

export default UserManagementConsole;

