interface DynamicField {
  id: string;
  label: string;
  type: 'text' | 'textarea' | 'select' | 'number' | 'email' | 'tel';
  placeholder?: string;
  required?: boolean;
  options?: string[];
}

// Configura√ß√µes de campos din√¢micos por categoria (baseado nas categorias reais do banco)
const DYNAMIC_FIELDS_CONFIG: Record<string, DynamicField[]> = {
  'Suporte T√©cnico': [
    { id: 'tipo_problema', label: 'Tipo de Problema', type: 'select', required: true, 
      options: ['Instala√ß√£o de Software', 'Problemas de Hardware', 'Problemas de Rede', 'Acesso ao Sistema', 'Outros'] },
