    { id: 'urgencia_negocio', label: 'Impacto no Neg√≥cio', type: 'select', required: true,
      options: ['Baixo - N√£o impacta opera√ß√£o', 'M√©dio - Impacta algumas fun√ß√µes', 'Alto - Impacta opera√ß√£o cr√≠tica', 'Cr√≠tico - Para toda opera√ß√£o'] }
  ],
  'Problemas de Hardware': [
    { id: 'equipamento_problema', label: 'Equipamento com Problema', type: 'text', placeholder: 'Ex: Notebook HP, Impressora Xerox', required: true },
    { id: 'numero_patrimonio', label: 'N√∫mero do Patrim√¥nio', type: 'text', placeholder: 'Se dispon√≠vel' },
    { id: 'descricao_problema', label: 'Descri√ß√£o Detalhada do Problema', type: 'textarea', required: true },
    { id: 'local_equipamento', label: 'Localiza√ß√£o do Equipamento', type: 'text', required: true }
  ],
  'Folha de Pagamento': [
    { id: 'cpf_funcionario', label: 'CPF do Funcion√°rio', type: 'text', placeholder: '000.000.000-00', required: true },
    { id: 'tipo_solicitacao', label: 'Tipo de Solicita√ß√£o', type: 'select', required: true,
      options: ['Corre√ß√£o de Dados', 'Inclus√£o de Benef√≠cio', 'Exclus√£o de Benef√≠cio', 'F√©rias', 'Licen√ßa', 'Outros'] },
    { id: 'periodo_referencia', label: 'Per√≠odo de Refer√™ncia', type: 'text', placeholder: 'Ex: Janeiro/2025', required: true },
    { id: 'centro_custo', label: 'Centro de Custo', type: 'text', required: true }
  ],
  'Contas a Pagar': [
    { id: 'fornecedor', label: 'Nome do Fornecedor', type: 'text', required: true },
    { id: 'numero_nota_fiscal', label: 'N√∫mero da Nota Fiscal', type: 'text', required: true },
    { id: 'valor_pagamento', label: 'Valor do Pagamento (R$)', type: 'number', required: true },
    { id: 'data_vencimento', label: 'Data de Vencimento', type: 'text', placeholder: 'DD/MM/AAAA', required: true }
  ]
};

interface CreateTicketPopupProps {
  isOpen: boolean;
  onClose: () => void;
}

export default function CreateTicketPopup({ isOpen, onClose }: CreateTicketPopupProps) {
  const { toast } = useToast();
  const queryClient = useQueryClient();

  const [formData, setFormData] = useState({
    subject: '',
    description: '',
    requesterDepartment: '',
    primaryCategory: '',
    secondaryCategory: '',
    assignedTo: '',
    priority: 'medium' as 'low' | 'medium' | 'high' | 'critical',
    dynamicFields: {} as Record<string, string>
  });

  const [selectedFiles, setSelectedFiles] = useState<File[]>([]);
  const [availableCategories, setAvailableCategories] = useState<any[]>([]);

  // Buscar dados necess√°rios
  const { data: users } = useQuery<any[]>({
    queryKey: ['/api/users'],
    enabled: isOpen,
  });

  const { data: departments } = useQuery<any[]>({
    queryKey: ['/api/departments'],
    enabled: isOpen,
  });

  const { data: allCategories } = useQuery<any[]>({
    queryKey: ['/api/categories'],
    enabled: isOpen,
  });

  // Usu√°rio atual
  const currentUser = users?.find(u => u.role === 'admin') || users?.[0];

  // Atualizar categorias dispon√≠veis quando departamento prim√°rio muda
  useEffect(() => {
    if (formData.primaryCategory && allCategories) {
      const selectedDept = departments?.find(d => d.name === formData.primaryCategory);
      if (selectedDept) {
        const deptCategories = allCategories.filter(cat => cat.departmentId === selectedDept.id);
        setAvailableCategories(deptCategories);
      } else {
        setAvailableCategories([]);
      }

      setFormData(prev => ({ 
        ...prev, 
        secondaryCategory: '',
        dynamicFields: {}
      }));
    }
  }, [formData.primaryCategory, allCategories, departments]);

  // Mutation para criar ticket
  const createTicketMutation = useMutation({
    mutationFn: async (ticketData: any) => {
      return apiRequest('/api/tickets', 'POST', ticketData);
    },
    onSuccess: () => {
      toast({
        title: 'Ticket criado com sucesso!',
        description: 'O ticket foi criado e ser√° processado em breve.',
      });
      queryClient.invalidateQueries({ queryKey: ['/api/tickets'] });
      onClose();
      // Reset form
      setFormData({
        subject: '',
        description: '',
        requesterDepartment: '',
        primaryCategory: '',
        secondaryCategory: '',
        assignedTo: '',
        priority: 'medium',
        dynamicFields: {}
      });
      setSelectedFiles([]);
    },
    onError: (error: any) => {
      toast({
        title: 'Erro ao criar ticket',
        description: error.message || 'Ocorreu um erro inesperado.',
        variant: 'destructive',
      });
    },
  });

  const handleSubmit = (e: React.FormEvent) => {
    e.preventDefault();

    const selectedDept = departments?.find(d => d.name === formData.primaryCategory);
    const selectedCategory = availableCategories.find(c => c.name === formData.secondaryCategory);

    const ticketData = {
      title: formData.subject,
      description: formData.description,
      departmentId: selectedDept?.id,
      categoryId: selectedCategory?.id,
      priority: formData.priority,
      requesterId: currentUser?.id,
      assignedToId: formData.assignedTo || null,
      requesterDepartment: formData.requesterDepartment,
      customFields: formData.dynamicFields,
      status: 'open'
    };

    createTicketMutation.mutate(ticketData);
  };

  const handleFileSelect = (e: React.ChangeEvent<HTMLInputElement>) => {
    const files = Array.from(e.target.files || []);
    setSelectedFiles(prev => [...prev, ...files]);
  };

  const removeFile = (index: number) => {
    setSelectedFiles(prev => prev.filter((_, i) => i !== index));
  };

  const handleDynamicFieldChange = (fieldId: string, value: string) => {
    setFormData(prev => ({
      ...prev,
      dynamicFields: {
        ...prev.dynamicFields,
        [fieldId]: value
      }
    }));
  };

  const primaryCategories = departments?.map(d => d.name) || [];
  const dynamicFields = DYNAMIC_FIELDS_CONFIG[formData.secondaryCategory] || [];

  // Se n√£o estiver aberto, n√£o renderizar nada
  if (!isOpen) return null;

  return (
    <div 
      style={{ 
        position: 'fixed',
        top: 0,
        left: 0,
        right: 0,
        bottom: 0,
        backgroundColor: 'rgba(0, 0, 0, 0.6)',
        zIndex: 999999,
        display: 'flex',
        alignItems: 'center',
        justifyContent: 'center',
        padding: '16px'
      }}
      onClick={onClose}
    >
      <div 
        style={{
          backgroundColor: 'white',
          borderRadius: '12px',
          boxShadow: '0 25px 50px -12px rgba(0, 0, 0, 0.5)',
          width: '100%',
          maxWidth: '900px',
          maxHeight: '90vh',
          overflow: 'hidden'
        }}
        onClick={(e) => e.stopPropagation()}
      >
        {/* Header */}
        <div style={{
          display: 'flex',
          alignItems: 'center',
          justifyContent: 'space-between',
          padding: '24px',
          borderBottom: '1px solid #e5e7eb',
          background: 'linear-gradient(to right, #eff6ff, #eef2ff)'
        }}>
          <div>
            <h2 style={{
              fontSize: '24px',
              fontWeight: 'bold',
              color: '#111827',
              margin: 0
            }}>Novo Ticket</h2>
            <p style={{
              fontSize: '14px',
              color: '#6b7280',
              margin: '4px 0 0 0'
            }}>Preencha os campos abaixo para criar seu ticket</p>
          </div>
          <button
            onClick={onClose}
            style={{
              padding: '8px',
              borderRadius: '50%',
              border: 'none',
              backgroundColor: 'transparent',
              cursor: 'pointer',
              display: 'flex',
              alignItems: 'center',
              justifyContent: 'center'
            }}
          >
            <X style={{ width: '20px', height: '20px', color: '#6b7280' }} />
          </button>
        </div>

        {/* Content */}
        <div style={{
          overflowY: 'auto',
          maxHeight: 'calc(90vh - 120px)'
        }}>
          <form onSubmit={handleSubmit} style={{ padding: '24px' }}>
            {/* Assunto */}
            <div style={{ marginBottom: '20px' }}>
              <label style={{
                display: 'block',
                fontSize: '14px',
                fontWeight: '500',
                color: '#374151',
                marginBottom: '4px'
              }}>
                Assunto <span style={{ color: '#ef4444' }}>*</span>
              </label>
              <input
                type="text"
                value={formData.subject}
                onChange={(e) => setFormData(prev => ({ ...prev, subject: e.target.value }))}
                style={{
                  width: '100%',
                  padding: '8px 12px',
                  border: '1px solid #d1d5db',
                  borderRadius: '8px',
                  fontSize: '14px',
                  outline: 'none',
                  transition: 'all 0.2s'
                }}
                onFocus={(e) => e.target.style.borderColor = '#3b82f6'}
                onBlur={(e) => e.target.style.borderColor = '#d1d5db'}
                required
              />
            </div>

            {/* Descri√ß√£o */}
            <div>
              <label className="block text-sm font-medium text-gray-700 mb-1">
                Descri√ß√£o <span className="text-red-500">*</span>
              </label>
              <textarea
                value={formData.description}
                onChange={(e) => setFormData(prev => ({ ...prev, description: e.target.value }))}
                rows={4}
                className="w-full px-3 py-2 border border-gray-300 rounded-lg focus:ring-2 focus:ring-blue-500 focus:border-transparent resize-none"
                required
              />
            </div>

            {/* Setor do Solicitante */}
            <div>
              <label className="block text-sm font-medium text-gray-700 mb-1">
                Setor do Solicitante <span className="text-red-500">*</span>
              </label>
              <Select
                value={formData.requesterDepartment}
                onValueChange={(value) => setFormData(prev => ({ ...prev, requesterDepartment: value }))}
              >
                <SelectTrigger>
                  <SelectValue placeholder="Selecione o setor..." />
                </SelectTrigger>
                <SelectContent>
                  {primaryCategories.map((dept) => (
                    <SelectItem key={dept} value={dept}>
                      {dept}
                    </SelectItem>
                  ))}
                </SelectContent>
              </Select>
            </div>

            {/* Departamento Respons√°vel */}
            <div>
              <label className="block text-sm font-medium text-gray-700 mb-1">
                Departamento Respons√°vel <span className="text-red-500">*</span>
              </label>
              <Select
                value={formData.primaryCategory}
                onValueChange={(value) => setFormData(prev => ({ ...prev, primaryCategory: value }))}
              >
                <SelectTrigger>
                  <SelectValue placeholder="Selecione o departamento..." />
                </SelectTrigger>
                <SelectContent>
                  {primaryCategories.map((dept) => (
                    <SelectItem key={dept} value={dept}>
                      {dept}
                    </SelectItem>
                  ))}
                </SelectContent>
              </Select>
            </div>

            {/* Categoria */}
            <div>
              <label className="block text-sm font-medium text-gray-700 mb-1">
                Categoria <span className="text-red-500">*</span>
              </label>
              <Select
                value={formData.secondaryCategory}
                onValueChange={(value) => setFormData(prev => ({ ...prev, secondaryCategory: value }))}
                disabled={!formData.primaryCategory || availableCategories.length === 0}
              >
                <SelectTrigger>
                  <SelectValue placeholder={
                    !formData.primaryCategory ? "Selecione um departamento primeiro" :
                    availableCategories.length === 0 ? "Nenhuma categoria dispon√≠vel" : "Selecione..."
                  } />
                </SelectTrigger>
                <SelectContent>
                  {availableCategories.map((category) => (
                    <SelectItem key={category.id} value={category.name}>
                      {category.name}
                    </SelectItem>
                  ))}
                </SelectContent>
              </Select>
            </div>

            {/* Campos Din√¢micos */}
            {dynamicFields.length > 0 && (
              <div className="space-y-4 p-4 bg-gray-50 rounded-lg">
                <h3 className="text-lg font-medium text-gray-900">Informa√ß√µes Espec√≠ficas</h3>
                {dynamicFields.map((field) => (
                  <div key={field.id}>
                    <label className="block text-sm font-medium text-gray-700 mb-1">
                      {field.label}
                      {field.required && <span className="text-red-500">*</span>}
                    </label>
                    {field.type === 'select' ? (
                      <Select
                        value={formData.dynamicFields[field.id] || ''}
                        onValueChange={(value) => handleDynamicFieldChange(field.id, value)}
                      >
                        <SelectTrigger>
                          <SelectValue placeholder="Selecione..." />
                        </SelectTrigger>
                        <SelectContent>
                          {field.options?.map((option) => (
                            <SelectItem key={option} value={option}>
                              {option}
                            </SelectItem>
                          ))}
                        </SelectContent>
                      </Select>
                    ) : field.type === 'textarea' ? (
                      <textarea
                        value={formData.dynamicFields[field.id] || ''}
                        onChange={(e) => handleDynamicFieldChange(field.id, e.target.value)}
                        placeholder={field.placeholder}
                        required={field.required}
                        rows={3}
                        className="w-full px-3 py-2 border border-gray-300 rounded-lg focus:ring-2 focus:ring-blue-500 focus:border-transparent resize-none"
                      />
                    ) : (
                      <input
                        type={field.type}
                        value={formData.dynamicFields[field.id] || ''}
                        onChange={(e) => handleDynamicFieldChange(field.id, e.target.value)}
                        placeholder={field.placeholder}
                        required={field.required}
                        className="w-full px-3 py-2 border border-gray-300 rounded-lg focus:ring-2 focus:ring-blue-500 focus:border-transparent"
                      />
                    )}
                  </div>
                ))}
              </div>
            )}

            {/* Prioridade */}
            <div>
              <label className="block text-sm font-medium text-gray-700 mb-1">
                Prioridade
              </label>
              <Select
                value={formData.priority}
                onValueChange={(value: 'low' | 'medium' | 'high' | 'critical') => 
                  setFormData(prev => ({ ...prev, priority: value }))
                }
              >
                <SelectTrigger>
                  <SelectValue />
                </SelectTrigger>
                <SelectContent>
                  <SelectItem value="low">Baixa</SelectItem>
                  <SelectItem value="medium">M√©dia</SelectItem>
                  <SelectItem value="high">Alta</SelectItem>
                  <SelectItem value="critical">Cr√≠tica</SelectItem>
                </SelectContent>
              </Select>
            </div>

            {/* Atribu√≠do a */}
            <div>
              <label className="block text-sm font-medium text-gray-700 mb-1">
                Atribu√≠do a
              </label>
              <Select
                value={formData.assignedTo}
                onValueChange={(value) => setFormData(prev => ({ ...prev, assignedTo: value }))}
              >
                <SelectTrigger>
                  <SelectValue placeholder="Selecionar respons√°vel..." />
                </SelectTrigger>
                <SelectContent>
                  {users?.filter(u => u.canHandleTickets).map((user) => (
                    <SelectItem key={user.id} value={user.id}>
                      {user.name} ({user.email})
                    </SelectItem>
                  ))}
                </SelectContent>
              </Select>
            </div>

            {/* Arquivos */}
            <div>
              <label className="block text-sm font-medium text-gray-700 mb-2">
                Anexos
              </label>
              <div className="border-2 border-dashed border-gray-300 rounded-lg p-4 text-center">
                <input
                  type="file"
                  id="file-upload"
                  multiple
                  onChange={handleFileSelect}
                  className="hidden"
                />
                <label 
                  htmlFor="file-upload"
                  className="cursor-pointer inline-flex items-center space-x-2 text-blue-600 hover:text-blue-700"
                >
                  <Paperclip className="h-5 w-5" />
                  <span>Clique para anexar arquivos</span>
                </label>
                <p className="text-xs text-gray-500 mt-1">
                  M√°ximo 10MB por arquivo
                </p>
              </div>

              {selectedFiles.length > 0 && (
                <div className="mt-2 space-y-2">
                  {selectedFiles.map((file, index) => (
                    <div key={index} className="flex items-center justify-between p-2 bg-gray-100 rounded">
                      <span className="text-sm text-gray-700">{file.name}</span>
                      <button
                        type="button"
                        onClick={() => removeFile(index)}
                        className="text-red-500 hover:text-red-700"
                      >
                        <X className="h-4 w-4" />
                      </button>
                    </div>
                  ))}
                </div>
              )}
            </div>

            {/* Bot√µes */}
            <div className="flex justify-end space-x-3 pt-6 border-t">
              <button
                type="button"
                onClick={onClose}
                className="px-4 py-2 text-sm font-medium text-gray-700 bg-white border border-gray-300 rounded-lg hover:bg-gray-50 focus:outline-none focus:ring-2 focus:ring-offset-2 focus:ring-gray-500"
              >
                Cancelar
              </button>
              <button
                type="submit"
                disabled={createTicketMutation.isPending}
                className="px-6 py-2 text-sm font-medium text-white bg-blue-600 hover:bg-blue-700 disabled:bg-blue-400 rounded-lg focus:outline-none focus:ring-2 focus:ring-offset-2 focus:ring-blue-500 disabled:cursor-not-allowed flex items-center space-x-2"
              >
                {createTicketMutation.isPending ? (
                  <>
                    <div className="w-4 h-4 border-2 border-white border-t-transparent rounded-full animate-spin"></div>
                    <span>Criando...</span>
                  </>
                ) : (
                  <span>Criar Ticket</span>
                )}
              </button>
            </div>
          </form>
        </div>
      </div>
    </div>
  );
}

