import React, { useState } from 'react';
import { Button } from '@/components/ui/button';
import { Badge } from '@/components/ui/badge';
import { 
  LayoutDashboard, 
  Ticket, 
  Users, 
  Settings, 
  BarChart3, 
  UserCheck,
  FileText,
  Building2,
  ChevronLeft,
  ChevronRight,
  CheckCircle2
} from 'lucide-react';

interface SidebarItem {
  icon: React.ReactNode;
  label: string;
  href: string;
  badge?: number;
}

interface ExpandableSidebarProps {
  currentPath: string;
  onNavigate: (path: string) => void;
}

export function ExpandableSidebar({ currentPath, onNavigate }: ExpandableSidebarProps) {
  const [isExpanded, setIsExpanded] = useState(true);

  const menuItems: SidebarItem[] = [
    { icon: <LayoutDashboard className="w-5 h-5" />, label: 'Dashboard', href: '/' },
    { icon: <Ticket className="w-5 h-5" />, label: 'Tickets', href: '/tickets', badge: 12 },
    { icon: <CheckCircle2 className="w-5 h-5" />, label: 'Aprova√ß√µes', href: '/approvals', badge: 3 },
    { icon: <Users className="w-5 h-5" />, label: 'Usu√°rios', href: '/users' },
    { icon: <Building2 className="w-5 h-5" />, label: 'Departamentos', href: '/departments' },
    { icon: <FileText className="w-5 h-5" />, label: 'Categorias', href: '/categories' },
    { icon: <BarChart3 className="w-5 h-5" />, label: 'Relat√≥rios', href: '/reports' },
    { icon: <UserCheck className="w-5 h-5" />, label: 'Perfil', href: '/profile' },
    { icon: <Settings className="w-5 h-5" />, label: 'Configura√ß√µes', href: '/settings' },
  ];

  const isActive = (href: string) => {
    if (href === '/') return currentPath === '/';
    return currentPath.startsWith(href);
  };

  return (
    <div 
      className={`bg-white border-r border-gray-200 transition-all duration-300 ease-in-out flex flex-col relative ${
        isExpanded ? 'w-64' : 'w-16'
      }`}
      style={{ 
        minWidth: isExpanded ? '256px' : '64px',
        maxWidth: isExpanded ? '256px' : '64px'
      }}
    >
      {/* Header */}
      <div className={`border-b border-gray-200 ${isExpanded ? 'p-4' : 'p-2'}`}>
        <div className={`flex items-center ${isExpanded ? 'justify-between' : 'flex-col space-y-2'}`}>
          {isExpanded ? (
            <div className="flex-1">
              <h2 className="text-lg font-bold text-gray-900">TicketFlow Pro</h2>
              <p className="text-xs text-gray-500">Sistema de Gest√£o</p>
            </div>
          ) : (
            <div className="w-8 h-8 bg-blue-600 rounded-lg flex items-center justify-center">
              <span className="text-white font-bold text-sm">T</span>
            </div>
          )}
          <Button
            variant="ghost"
            size="sm"
            onClick={() => setIsExpanded(!isExpanded)}
            className="p-2 hover:bg-gray-100 rounded-lg"
            title={isExpanded ? 'Recolher sidebar' : 'Expandir sidebar'}
          >
            {isExpanded ? <ChevronLeft className="w-4 h-4" /> : <ChevronRight className="w-4 h-4" />}
          </Button>
        </div>
      </div>

      {/* Navigation */}
      <nav className="flex-1 p-2 space-y-1">
        {menuItems.map((item) => {
          const active = isActive(item.href);
          return (
            <div key={item.href} className="relative group">
              <Button
                variant="ghost"
                onClick={() => onNavigate(item.href)}
                className={`w-full ${isExpanded ? 'justify-start' : 'justify-center'} p-3 ${
                  active 
                    ? 'bg-blue-600 text-white hover:bg-blue-700' 
                    : 'text-gray-700 hover:bg-gray-100'
                }`}
              >
                <div className={`flex items-center ${isExpanded ? 'space-x-3' : ''} w-full`}>
                  <div className="flex-shrink-0">
                    {item.icon}
                  </div>
                  {isExpanded && (
                    <>
                      <span className="text-sm font-medium flex-1 text-left">{item.label}</span>
                      {item.badge && item.badge > 0 && (
                        <Badge 
                          variant="secondary" 
                          className={`text-xs ${
                            active 
                              ? 'bg-white/20 text-white' 
                              : 'bg-gray-200 text-gray-700'
                          }`}
                        >
                          {item.badge}
                        </Badge>
                      )}
                    </>
                  )}
                </div>
              </Button>

              {/* Tooltip para sidebar colapsada */}
              {!isExpanded && (
                <div className="absolute left-full ml-2 top-1/2 transform -translate-y-1/2 bg-gray-900 text-white px-2 py-1 rounded text-xs whitespace-nowrap opacity-0 group-hover:opacity-100 transition-opacity duration-200 z-50">
                  {item.label}
                  {item.badge && item.badge > 0 && (
                    <span className="ml-2 bg-red-500 text-white rounded-full px-1 text-xs">
                      {item.badge}
                    </span>
                  )}
                </div>
              )}
            </div>
          );
        })}
      </nav>

      {/* Footer */}
      <div className={`border-t border-gray-200 ${isExpanded ? 'p-4' : 'p-2'}`}>
        {isExpanded ? (
          <div className="text-xs text-gray-500 text-center">
            <p>Vers√£o 2.1.0</p>
            <p className="mt-1">¬© 2024 TicketFlow Pro</p>
          </div>
        ) : (
          <div className="flex justify-center">
            <div className="w-8 h-8 bg-gray-100 rounded-lg flex items-center justify-center">
              <span className="text-gray-600 text-xs font-bold">v2</span>
            </div>
          </div>
        )}
      </div>
    </div>
  );
}

client/src/components/SimpleTicketModal..content:
import React, { useState, useEffect } from 'react';
import { useQuery } from '@tanstack/react-query';
import { X, AlertCircle, Paperclip, Upload } from 'lucide-react';

interface SimpleTicketModalProps {
  isOpen: boolean;
  onClose: () => void;
}

interface Department {
  id: string;
  name: string;
  categories: Category[];
}

interface Category {
  id: string;
  name: string;
  customFields: CustomField[];
}

interface CustomField {
  id: string;
  name: string;
  type: string;
  required: boolean;
  options?: string[];
}

export default function SimpleTicketModal({ isOpen, onClose }: SimpleTicketModalProps) {
  const [formData, setFormData] = useState({
    subject: '',
    requesterName: '',
    requesterEmail: '',
    requesterPhone: '',
    requesterDepartment: '',
    responsibleDepartment: '',
    category: '',
    priority: 'M√©dia',
    description: '',
    attachments: [] as File[],
    customFields: {} as Record<string, string>
  });

  const [selectedDepartment, setSelectedDepartment] = useState<Department | null>(null);
  const [selectedCategory, setSelectedCategory] = useState<Category | null>(null);
  const [selectedCategoryId, setSelectedCategoryId] = useState<string>("");
  const [isSubmitting, setIsSubmitting] = useState(false);

  const { data: departments = [] } = useQuery<Department[]>({
    queryKey: ['/api/departments'],
    enabled: isOpen
  });

  // Buscar categorias filtradas por departamento respons√°vel
  const { data: categories = [] } = useQuery<Category[]>({
    queryKey: ['/api/categories/department', formData.responsibleDepartment],
    enabled: isOpen && !!formData.responsibleDepartment
  });

  // Buscar campos customizados para categoria selecionada E departamento
  const { data: customFields = [] } = useQuery<CustomField[]>({
    queryKey: ["/api/custom-fields/category", selectedCategoryId, formData.responsibleDepartment],
    queryFn: async () => {
      if (!selectedCategoryId || !formData.responsibleDepartment) return [];

      const response = await fetch(
        `/api/custom-fields/category/${selectedCategoryId}?departmentId=${formData.responsibleDepartment}`
      );

      if (!response.ok) {
        console.error('Error fetching custom fields:', response.statusText);
        return [];
      }

      return response.json();
    },
    enabled: isOpen && !!selectedCategoryId && !!formData.responsibleDepartment,
  });

  // Buscar dados do usu√°rio logado
  const { data: currentUser } = useQuery({
    queryKey: ['/api/auth/user'],
    enabled: isOpen
  });

  // Auto-preencher dados do usu√°rio quando o modal abrir
  useEffect(() => {
    if (isOpen && currentUser) {
      const fullName = currentUser.firstName && currentUser.lastName 
        ? `${currentUser.firstName} ${currentUser.lastName}`.trim()
        : currentUser.name || '';

      setFormData(prev => ({
        ...prev,
        requesterName: fullName,
        requesterEmail: currentUser.email || '',
        requesterDepartment: currentUser.departmentId || '' // Auto-preenchido e travado
      }));
    }
  }, [isOpen, currentUser]);

  if (!isOpen) return null;

  const handleDepartmentChange = (departmentId: string) => {
    setFormData(prev => ({ 
      ...prev, 
      responsibleDepartment: departmentId,
      category: '',
      customFields: {}
    }));
    setSelectedCategory(null);
    setSelectedCategoryId('');
  };

  const handleCategoryChange = (categoryId: string) => {
    const category = categories.find(c => c.id === categoryId);
    setSelectedCategory(category || null);
    setSelectedCategoryId(categoryId);
    setFormData(prev => ({ 
      ...prev, 
      category: categoryId,
      customFields: {} // Limpar campos customizados ao trocar categoria
    }));
  };

  const handleResponsibleDepartmentChange = (departmentId: string) => {
    setFormData(prev => ({ 
      ...prev, 
      responsibleDepartment: departmentId,
      category: '', // Limpar categoria ao trocar departamento respons√°vel
      customFields: {} // Limpar campos customizados
    }));
    setSelectedCategory(null);
    setSelectedCategoryId('');
  };

  const handleCustomFieldChange = (fieldId: string, value: string) => {
    setFormData(prev => ({
      ...prev,
      customFields: {
        ...prev.customFields,
        [fieldId]: value
      }
    }));
  };

  const handleFileChange = (e: React.ChangeEvent<HTMLInputElement>) => {
    const files = Array.from(e.target.files || []);
    setFormData(prev => ({
      ...prev,
      attachments: [...prev.attachments, ...files]
    }));
  };

  const removeFile = (index: number) => {
    setFormData(prev => ({
      ...prev,
      attachments: prev.attachments.filter((_, i) => i !== index)
    }));
  };

  const handleSubmit = async (e: React.FormEvent) => {
    e.preventDefault();
    setIsSubmitting(true);

    try {
      const response = await fetch('/api/tickets', {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify(formData)
      });

      if (response.ok) {
        alert('Ticket criado com sucesso!');
        onClose();
        setFormData({
          subject: '',
          requesterName: '',
          requesterEmail: '',
          requesterPhone: '',
          requesterDepartment: '',
          responsibleDepartment: '',
          category: '',
          priority: 'M√©dia',
          description: '',
          attachments: [],
          customFields: {}
        });
      } else {
        alert('Erro ao criar ticket');
      }
    } catch (error) {
      alert('Erro ao criar ticket');
    } finally {
      setIsSubmitting(false);
    }
  };

  return (
    <div
      style={{
        position: 'fixed',
        top: 0,
        left: 0,
        right: 0,
        bottom: 0,
        backgroundColor: 'rgba(0, 0, 0, 0.6)',
        zIndex: 999999,
        display: 'flex',
        alignItems: 'center',
        justifyContent: 'center',
        padding: '20px'
      }}
      onClick={onClose}
    >
      <div
        style={{
          backgroundColor: 'white',
          borderRadius: '12px',
          width: '100%',
          maxWidth: '900px',
          maxHeight: '90vh',
          overflow: 'hidden',
          boxShadow: '0 25px 50px -12px rgba(0, 0, 0, 0.5)'
        }}
        onClick={(e) => e.stopPropagation()}
      >
        {/* Header */}
        <div style={{
          display: 'flex',
          alignItems: 'center',
          justifyContent: 'space-between',
          padding: '24px',
          borderBottom: '1px solid #e5e7eb',
          background: 'linear-gradient(to right, #eff6ff, #eef2ff)'
        }}>
          <div>
            <h2 style={{
              fontSize: '24px',
              fontWeight: 'bold',
              color: '#111827',
              margin: 0
            }}>Novo Ticket</h2>
            <p style={{
              fontSize: '14px',
              color: '#6b7280',
              margin: '4px 0 0 0'
            }}>Preencha os campos abaixo para criar seu ticket</p>
          </div>
          <button
            onClick={onClose}
            style={{
              padding: '8px',
              borderRadius: '50%',
              border: 'none',
              backgroundColor: 'transparent',
              cursor: 'pointer',
              display: 'flex',
              alignItems: 'center',
              justifyContent: 'center'
            }}
          >
            <X style={{ width: '20px', height: '20px', color: '#6b7280' }} />
          </button>
        </div>

        {/* Content */}
        <div style={{ overflowY: 'auto', maxHeight: 'calc(90vh - 120px)' }}>
          <form onSubmit={handleSubmit} style={{ padding: '24px' }}>
            {/* Grid de campos principais */}
            <div style={{ 
              display: 'grid', 
              gridTemplateColumns: 'repeat(auto-fit, minmax(300px, 1fr))',
              gap: '20px',
              marginBottom: '24px'
            }}>
              {/* Assunto */}
              <div style={{ gridColumn: 'span 2' }}>
                <label style={{
                  display: 'block',
                  fontSize: '14px',
                  fontWeight: '500',
                  color: '#374151',
                  marginBottom: '6px'
                }}>
                  Assunto <span style={{ color: '#ef4444' }}>*</span>
                </label>
                <input
                  type="text"
                  value={formData.subject}
