            <TableBody>
              {users?.map((user) => (
                <TableRow key={user.id}>
                  <TableCell className="font-medium">{user.name}</TableCell>
                  <TableCell className="text-gray-600">{user.email}</TableCell>
                  <TableCell>
                    {editingUser === user.id ? (
                      <Select
                        defaultValue={user.role}
                        onValueChange={(value) => {
                          updateUserMutation.mutate({
                            userId: user.id,
                            role: value,
                          });
                        }}
                      >
                        <SelectTrigger className="w-40">
                          <SelectValue />
                        </SelectTrigger>
                        <SelectContent>
                          <SelectItem value="colaborador">Colaborador</SelectItem>
                          <SelectItem value="supervisor">Supervisor</SelectItem>
                          <SelectItem value="admin">Administrador</SelectItem>
                        </SelectContent>
                      </Select>
                    ) : (
                      <Badge variant={getRoleBadgeVariant(user.role)}>
                        {getRoleLabel(user.role)}
                      </Badge>
                    )}
                  </TableCell>
                  <TableCell className="text-sm text-gray-600">
                    {getRolePermissions(user.role)}
                  </TableCell>
                  <TableCell>
                    {editingUser === user.id ? (
                      <Button
                        variant="ghost"
                        size="sm"
                        onClick={() => setEditingUser(null)}
                        disabled={updateUserMutation.isPending}
                      >
                        Cancelar
                      </Button>
                    ) : (
                      <Button
                        variant="ghost"
                        size="sm"
                        onClick={() => setEditingUser(user.id)}
                      >
                        <Edit2 className="w-4 h-4" />
                      </Button>
                    )}
                  </TableCell>
                </TableRow>
              ))}
            </TableBody>
          </Table>
        </CardContent>
      </Card>

      {/* Permission Info */}
      <Card>
        <CardHeader>
          <CardTitle className="text-lg">Explica√ß√£o dos Perfis</CardTitle>
        </CardHeader>
        <CardContent className="space-y-4">
          <div className="grid grid-cols-1 md:grid-cols-3 gap-4">
            <div className="border rounded-lg p-4">
              <div className="flex items-center gap-2 mb-2">
                <Badge variant="destructive">Administrador</Badge>
              </div>
              <p className="text-sm text-gray-600">
                ‚Ä¢ Pode abrir tickets<br/>
                ‚Ä¢ Pode atender qualquer ticket<br/>
                ‚Ä¢ Acesso total ao sistema<br/>
                ‚Ä¢ Gerencia usu√°rios e configura√ß√µes
              </p>
            </div>

            <div className="border rounded-lg p-4">
              <div className="flex items-center gap-2 mb-2">
                <Badge variant="default">Supervisor</Badge>
              </div>
              <p className="text-sm text-gray-600">
                ‚Ä¢ Pode abrir tickets<br/>
                ‚Ä¢ Pode atender tickets do seu departamento<br/>
                ‚Ä¢ Gerencia tickets do departamento<br/>
                ‚Ä¢ Acesso limitado √†s configura√ß√µes
              </p>
            </div>

            <div className="border rounded-lg p-4">
              <div className="flex items-center gap-2 mb-2">
                <Badge variant="secondary">Colaborador</Badge>
              </div>
              <p className="text-sm text-gray-600">
                ‚Ä¢ Pode apenas abrir tickets<br/>
                ‚Ä¢ N√£o pode atender tickets<br/>
                ‚Ä¢ Acesso limitado ao sistema<br/>
