import React from 'react';
import { Select, SelectContent, SelectItem, SelectTrigger, SelectValue } from '@/components/ui/select';
import Icon from '../AppIcon';

interface SLAFiltersState {
  priority: string;
  status: string;
  department: string;
  timeRange: string;
}

interface SLAFiltersProps {
  filters: SLAFiltersState;
  onFiltersChange: (filters: SLAFiltersState) => void;
}

export default function SLAFilters({ filters, onFiltersChange }: SLAFiltersProps) {
  const priorityOptions = [
    { value: 'all', label: 'Todas as Prioridades' },
    { value: 'critical', label: 'Cr√≠tica' },
    { value: 'high', label: 'Alta' },
    { value: 'medium', label: 'M√©dia' },
    { value: 'low', label: 'Baixa' }
  ];

  const statusOptions = [
    { value: 'all', label: 'Todos os Status' },
    { value: 'open', label: 'Aberto' },
    { value: 'in_progress', label: 'Em Andamento' },
    { value: 'pending', label: 'Pendente' },
    { value: 'resolved', label: 'Resolvido' }
  ];

  const departmentOptions = [
    { value: 'all', label: 'Todos os Departamentos' },
    { value: 'TI', label: 'TI' },
    { value: 'RH', label: 'Recursos Humanos' },
    { value: 'Financeiro', label: 'Financeiro' },
    { value: 'Vendas', label: 'Vendas' },
    { value: 'Suporte', label: 'Suporte' }
  ];

  const timeRangeOptions = [
    { value: '1h', label: '√öltima Hora' },
    { value: '24h', label: '√öltimas 24 Horas' },
    { value: '7d', label: '√öltimos 7 Dias' },
    { value: '30d', label: '√öltimos 30 Dias' },
    { value: 'all', label: 'Todos os Per√≠odos' }
  ];

  const handleFilterChange = (key: keyof SLAFiltersState, value: string) => {
    onFiltersChange({
      ...filters,
      [key]: value
    });
  };

  const clearFilters = () => {
    onFiltersChange({
      priority: 'all',
      status: 'all',
      department: 'all',
      timeRange: '24h'
    });
  };

  const hasActiveFilters = filters.priority !== 'all' || 
                          filters.status !== 'all' || 
                          filters.department !== 'all' || 
                          filters.timeRange !== '24h';

  return (
    <div className="bg-white dark:bg-slate-800 rounded-lg border border-slate-200 dark:border-slate-700 p-4">
