import { useState, useEffect } from 'react';
import { useQuery, useMutation, useQueryClient } from '@tanstack/react-query';
import { Plus, Edit2, Trash2, Building2 } from 'lucide-react';
import { Button } from '@/components/ui/button';
import { Card, CardContent, CardHeader, CardTitle } from '@/components/ui/card';
import { Badge } from '@/components/ui/badge';
import { apiRequest } from '@/lib/queryClient';

interface Department {
  id: string;
  name: string;
  description?: string;
  isRequester: boolean;
  isResponsible: boolean;
  createdAt: string;
}

interface DepartmentFormData {
  name: string;
  description: string;
  isRequester: boolean;
  isResponsible: boolean;
}

export default function DepartmentManager() {
  const [isModalOpen, setIsModalOpen] = useState(false);
  const [editingDepartment, setEditingDepartment] = useState<Department | null>(null);
  const [formData, setFormData] = useState<DepartmentFormData>({
    name: '',
    description: '',
    isRequester: true,
    isResponsible: true
  });
  const [isSubmitting, setIsSubmitting] = useState(false);

  const queryClient = useQueryClient();

  // Buscar departamentos
  const { data: departments = [], isLoading } = useQuery({
    queryKey: ['/api/departments']
  });

  // Mutation para criar/editar departamento
  const createDepartmentMutation = useMutation({
    mutationFn: async (data: DepartmentFormData) => {
      const url = editingDepartment ? `/api/departments/${editingDepartment.id}` : '/api/departments';
      const method = editingDepartment ? 'PUT' : 'POST';

      return apiRequest(url, {
        method,
        body: JSON.stringify(data)
      });
    },
    onSuccess: () => {
      queryClient.invalidateQueries({ queryKey: ['/api/departments'] });
      setIsModalOpen(false);
      resetForm();
    }
  });

  // Mutation para deletar departamento
  const deleteDepartmentMutation = useMutation({
    mutationFn: async (id: string) => {
      return apiRequest(`/api/departments/${id}`, {
        method: 'DELETE'
      });
    },
    onSuccess: () => {
      queryClient.invalidateQueries({ queryKey: ['/api/departments'] });
    }
  });

  const resetForm = () => {
    setFormData({ 
      name: '', 
      description: '', 
      isRequester: true, 
      isResponsible: true 
    });
    setEditingDepartment(null);
  };

  const handleEdit = (department: Department) => {
    setEditingDepartment(department);
    setFormData({
      name: department.name,
      description: department.description || '',
      isRequester: department.isRequester || true,
      isResponsible: department.isResponsible || true
    });
    setIsModalOpen(true);
  };

  const handleSubmit = async (e: React.FormEvent) => {
    e.preventDefault();
    setIsSubmitting(true);

    try {
      if (editingDepartment) {
        // Editando departamento existente
        const response = await fetch(`/api/departments/${editingDepartment.id}`, {
          method: 'PUT',
          headers: { 'Content-Type': 'application/json' },
          body: JSON.stringify(formData)
        });

        if (!response.ok) throw new Error('Erro ao atualizar departamento');
      } else {
        // Criando novo departamento
        const response = await fetch('/api/departments', {
          method: 'POST',
          headers: { 'Content-Type': 'application/json' },
          body: JSON.stringify(formData)
        });

        if (!response.ok) throw new Error('Erro ao criar departamento');
      }

      // Recarregar dados e fechar modal
      queryClient.invalidateQueries({ queryKey: ['/api/departments'] });
      setIsModalOpen(false);
      resetForm();
    } catch (error) {
      console.error('Erro ao salvar departamento:', error);
      alert('Erro ao salvar departamento. Tente novamente.');
    } finally {
      setIsSubmitting(false);
    }
  };

  if (isLoading) {
    return (
      <div className="p-6">
        <div className="animate-pulse">
          <div className="h-8 bg-gray-200 rounded w-1/4 mb-6"></div>
          <div className="space-y-4">
            {[...Array(3)].map((_, i) => (
              <div key={i} className="h-24 bg-gray-200 rounded"></div>
            ))}
          </div>
        </div>
      </div>
    );
  }

  return (
    <div className="p-6 space-y-6">
      {/* Header */}
