import { useQuery } from '@tanstack/react-query';
import { User } from '@shared/schema';

// Definir hierarquia de roles
const ROLE_HIERARCHY = {
  colaborador: 0,
  supervisor: 1,
  administrador: 2
} as const;

// Definir permiss√µes por role
const ROLE_PERMISSIONS = {
  colaborador: {
    canManageUsers: false,
    canViewAllTickets: false,
    canViewDepartmentTickets: false, // S√≥ v√™ seus pr√≥prios tickets
    canManageTickets: false, // S√≥ pode criar, n√£o gerenciar
    canViewReports: false,
    canManageSystem: false,
    canManageCategories: false,
    canManageDepartments: false,
  },
  supervisor: {
    canManageUsers: true, // S√≥ do seu departamento
    canViewAllTickets: false,
    canViewDepartmentTickets: true, // Todos do departamento
    canManageTickets: true, // Atender/resolver tickets
    canViewReports: true, // Relat√≥rios do departamento
    canManageSystem: false,
    canManageCategories: true, // S√≥ do departamento
    canManageDepartments: false,
  },
  administrador: {
    canManageUsers: true, // Todos os usu√°rios
    canViewAllTickets: true, // Todos os tickets
    canViewDepartmentTickets: true,
    canManageTickets: true,
    canViewReports: true, // Todos os relat√≥rios
    canManageSystem: true, // Configura√ß√µes do sistema
    canManageCategories: true,
    canManageDepartments: true,
  }
} as const;

export type UserRole = keyof typeof ROLE_HIERARCHY;
export type PermissionKey = keyof typeof ROLE_PERMISSIONS.colaborador;

export interface UserPermissions {
  // Verificar se usu√°rio tem permiss√£o espec√≠fica
  hasPermission: (permission: PermissionKey) => boolean;

  // Verificar hierarquia - se role atual √© igual ou superior ao m√≠nimo requerido
  hasRoleLevel: (minRole: UserRole) => boolean;

  // Verificar se pode gerenciar outro usu√°rio (mesmo departamento + hierarquia)
  canManageUser: (targetUser: User) => boolean;

  // Verificar se pode ver tickets de outro usu√°rio
  canViewUserTickets: (targetUser: User) => boolean;

  // Informa√ß√µes do usu√°rio atual
  user: User | null;
  userRole: UserRole;
  isLoading: boolean;
}

export function usePermissions(): UserPermissions {
  const { data: user, isLoading } = useQuery<User>({ 
    queryKey: ['/api/auth/user'], 
    retry: false 
  });

  const userRole: UserRole = (user?.role as UserRole) || 'colaborador';

  const hasPermission = (permission: PermissionKey): boolean => {
    if (!user) return false;
    return ROLE_PERMISSIONS[userRole][permission];
  };

  const hasRoleLevel = (minRole: UserRole): boolean => {
    if (!user) return false;
    return ROLE_HIERARCHY[userRole] >= ROLE_HIERARCHY[minRole];
  };

  const canManageUser = (targetUser: User): boolean => {
    if (!user) return false;

    // Administrador pode gerenciar todos
    if (userRole === 'administrador') return true;

    // Supervisor pode gerenciar usu√°rios do mesmo departamento que n√£o sejam administradores
    if (userRole === 'supervisor' && user.departmentId === targetUser.departmentId) {
      return targetUser.role !== 'administrador';
    }

    return false;
  };

  const canViewUserTickets = (targetUser: User): boolean => {
    if (!user) return false;

    // Usu√°rio sempre pode ver seus pr√≥prios tickets
    if (user.id === targetUser.id) return true;

    // Administrador v√™ todos
    if (userRole === 'administrador') return true;

    // Supervisor v√™ todos do departamento
    if (userRole === 'supervisor' && user.departmentId === targetUser.departmentId) {
      return true;
    }

    return false;
  };

  return {
    hasPermission,
    hasRoleLevel,
    canManageUser,
    canViewUserTickets,
    user,
    userRole,
    isLoading
  };
}

