import { Request, Response, NextFunction } from 'express';
import { storage } from '../storage';

// Definir hierarquia de roles
const ROLE_HIERARCHY = {
  colaborador: 0,
  supervisor: 1,
  administrador: 2
} as const;

// Definir permiss√µes por role
const ROLE_PERMISSIONS = {
  colaborador: {
    canManageUsers: false,
    canViewAllTickets: false,
    canViewDepartmentTickets: false,
    canManageTickets: false,
    canViewReports: false,
    canManageSystem: false,
    canManageCategories: false,
    canManageDepartments: false,
  },
  supervisor: {
    canManageUsers: true, // S√≥ do seu departamento
    canViewAllTickets: false,
    canViewDepartmentTickets: true, // Todos do departamento
    canManageTickets: true,
    canViewReports: true,
    canManageSystem: false,
    canManageCategories: true, // S√≥ do departamento
    canManageDepartments: false,
  },
  administrador: {
    canManageUsers: true, // Todos os usu√°rios
    canViewAllTickets: true, // Todos os tickets
    canViewDepartmentTickets: true,
    canManageTickets: true,
    canViewReports: true, // Todos os relat√≥rios
    canManageSystem: true, // Configura√ß√µes do sistema
    canManageCategories: true,
    canManageDepartments: true,
  }
} as const;

type UserRole = keyof typeof ROLE_HIERARCHY;
type PermissionKey = keyof typeof ROLE_PERMISSIONS.colaborador;

interface AuthenticatedRequest extends Request {
  user: {
    id: string;
    role: UserRole;
    departmentId?: string;
  };
}

export function requirePermission(permission: PermissionKey) {
  return async (req: Request, res: Response, next: NextFunction) => {
    try {
      const authReq = req as AuthenticatedRequest;

      if (!authReq.user) {
        return res.status(401).json({ message: 'Usu√°rio n√£o autenticado' });
      }

      const userRole = authReq.user.role;
      const hasPermission = ROLE_PERMISSIONS[userRole]?.[permission];

      if (!hasPermission) {
        return res.status(403).json({ 
          message: 'Acesso negado. Permiss√£o insuficiente.',
          required: permission,
          userRole: userRole
        });
      }

      next();
    } catch (error) {
      console.error('Erro no middleware de permiss√£o:', error);
      res.status(500).json({ message: 'Erro interno do servidor' });
    }
  };
}

export function requireRole(minRole: UserRole) {
  return async (req: Request, res: Response, next: NextFunction) => {
    try {
      const authReq = req as AuthenticatedRequest;

      if (!authReq.user) {
        return res.status(401).json({ message: 'Usu√°rio n√£o autenticado' });
      }

      const userRole = authReq.user.role;
      const userLevel = ROLE_HIERARCHY[userRole] || 0;
      const requiredLevel = ROLE_HIERARCHY[minRole] || 0;

      if (userLevel < requiredLevel) {
        return res.status(403).json({ 
          message: 'Acesso negado. N√≠vel hier√°rquico insuficiente.',
          required: minRole,
          userRole: userRole
        });
      }

      next();
    } catch (error) {
      console.error('Erro no middleware de role:', error);
      res.status(500).json({ message: 'Erro interno do servidor' });
    }
  };
}

// Middleware para filtrar tickets baseado na hierarquia
export async function filterTicketsByHierarchy(req: Request, res: Response, next: NextFunction) {
  try {
    const authReq = req as AuthenticatedRequest;

    if (!authReq.user) {
      return res.status(401).json({ message: 'Usu√°rio n√£o autenticado' });
    }

    const userRole = authReq.user.role;
    const userId = authReq.user.id;
    const userDepartmentId = authReq.user.departmentId;

    // Adicionar par√¢metros de filtro baseado na hierarquia
    switch (userRole) {
      case 'colaborador':
        // Colaboradores s√≥ veem seus pr√≥prios tickets
        req.query.createdBy = userId;
        break;

      case 'supervisor':
        // Supervisores veem todos do departamento
        if (userDepartmentId) {
          req.query.departmentId = userDepartmentId;
        }
        break;

      case 'administrador':
        // Administradores veem tudo (sem filtro adicional)
        break;

      default:
        // Fallback para colaborador
        req.query.createdBy = userId;
        break;
    }

    next();
  } catch (error) {
    console.error('Erro no middleware de filtro de tickets:', error);
    res.status(500).json({ message: 'Erro interno do servidor' });
  }
}

// Middleware para autentica√ß√£o simulada (ser√° substitu√≠do pelo sistema real de auth)
export function mockAuth(req: Request, res: Response, next: NextFunction) {
  // Simula√ß√£o tempor√°ria - em produ√ß√£o ser√° substitu√≠do pelo sistema de autentica√ß√£o real
  const authReq = req as AuthenticatedRequest;
  authReq.user = {
    id: 'user-1',
    role: 'administrador', // Mudar para testar diferentes roles
    departmentId: 'dept-1'
  };

  next();
}

export { AuthenticatedRequest };

