// Servi√ßo para gerenciar informa√ß√µes do usu√°rio atual
// Em um sistema real, isso viria de um contexto de autentica√ß√£o

interface User {
  id: string;
  name: string;
  email: string;
  role: 'colaborador' | 'supervisor' | 'administrador';
  department: string;
  avatar?: string;
}

// Usu√°rio simulado - em produ√ß√£o isso viria do contexto de autentica√ß√£o
const mockUser: User = {
  id: '8cd6d843-31a9-48f1-a99c-a6789e592a9f',
  name: 'Jo√£o Silva',
  email: 'joao.silva@empresa.com',
  role: 'administrador', // Para demonstra√ß√£o, vamos usar administrador
  department: 'Tecnologia da Informa√ß√£o',
  avatar: null
};

export function getCurrentUser(): User {
  // Em um sistema real, isso obteria dados do contexto de autentica√ß√£o
  // ou de um cookie/token JWT
  return mockUser;
}

export function isAdmin(): boolean {
  return getCurrentUser().role === 'administrador';
}

export function isSupervisor(): boolean {
  const role = getCurrentUser().role;
  return role === 'supervisor' || role === 'administrador';
}

export function hasPermission(permission: string): boolean {
  const user = getCurrentUser();

  // Administradores t√™m todas as permiss√µes
  if (user.role === 'administrador') {
    return true;
  }

  // Supervisores t√™m permiss√µes limitadas
  if (user.role === 'supervisor') {
    const supervisorPermissions = [
      'canViewOwnTickets', 
      'canCreateTickets', 
      'canEditTickets', 
      'canAssignTickets',
      'canViewUsers', 
      'canManageUsers', 
      'canViewDepartments', 
      'canViewReports', 
      'canViewDeptReports', 
      'canManageCategories'
    ];
    return supervisorPermissions.includes(permission);
  }

  // Colaboradores t√™m permiss√µes b√°sicas
  if (user.role === 'colaborador') {
    const colaboradorPermissions = ['canViewOwnTickets', 'canCreateTickets'];
    return colaboradorPermissions.includes(permission);
  }

  return false;
}

