import { useQuery, useMutation, useQueryClient } from "@tanstack/react-query";
import { Plus, Edit, Trash2, Shield, Eye, Settings, Users, Building2, FileText, BarChart3, Cog } from "lucide-react";
import { Button } from "@/components/ui/button";
import { Card, CardContent, CardDescription, CardHeader, CardTitle } from "@/components/ui/card";
import { Badge } from "@/components/ui/badge";
import { Dialog, DialogContent, DialogDescription, DialogFooter, DialogHeader, DialogTitle } from "@/components/ui/dialog";
import { Input } from "@/components/ui/input";
import { Label } from "@/components/ui/label";
import { Textarea } from "@/components/ui/textarea";
import { Checkbox } from "@/components/ui/checkbox";
import { useToast } from "@/hooks/use-toast";
import { AdminOnly } from "@/components/PermissionGuard";
import { apiRequest } from "@/lib/queryClient";

interface Permission {
  id: string;
  name: string;
  description: string;
  category: string;
  icon: any;
}

interface Role {
  id: string;
  name: string;
  description: string;
  color: string;
  permissions: string[];
  userCount?: number;
  isSystem?: boolean; // Roles do sistema n√£o podem ser deletadas
}

const AVAILABLE_PERMISSIONS: Permission[] = [
  // Tickets
  { id: 'tickets.create', name: 'Criar Tickets', description: 'Pode criar novos tickets', category: 'Tickets', icon: Plus },
  { id: 'tickets.view_own', name: 'Ver Pr√≥prios Tickets', description: 'Pode ver seus pr√≥prios tickets', category: 'Tickets', icon: Eye },
  { id: 'tickets.view_all', name: 'Ver Todos os Tickets', description: 'Pode ver todos os tickets do sistema', category: 'Tickets', icon: FileText },
  { id: 'tickets.edit', name: 'Editar Tickets', description: 'Pode editar tickets', category: 'Tickets', icon: Edit },
  { id: 'tickets.delete', name: 'Deletar Tickets', description: 'Pode deletar tickets', category: 'Tickets', icon: Trash2 },
  { id: 'tickets.assign', name: 'Atribuir Tickets', description: 'Pode atribuir tickets para outros usu√°rios', category: 'Tickets', icon: Users },

  // Usu√°rios
  { id: 'users.view', name: 'Ver Usu√°rios', description: 'Pode visualizar lista de usu√°rios', category: 'Usu√°rios', icon: Users },
  { id: 'users.create', name: 'Criar Usu√°rios', description: 'Pode criar novos usu√°rios', category: 'Usu√°rios', icon: Plus },
  { id: 'users.edit', name: 'Editar Usu√°rios', description: 'Pode editar dados de usu√°rios', category: 'Usu√°rios', icon: Edit },
  { id: 'users.delete', name: 'Deletar Usu√°rios', description: 'Pode deletar usu√°rios', category: 'Usu√°rios', icon: Trash2 },

  // Departamentos
  { id: 'departments.view', name: 'Ver Departamentos', description: 'Pode visualizar departamentos', category: 'Departamentos', icon: Building2 },
  { id: 'departments.manage', name: 'Gerenciar Departamentos', description: 'Pode criar, editar e deletar departamentos', category: 'Departamentos', icon: Settings },

  // Relat√≥rios
  { id: 'reports.view', name: 'Ver Relat√≥rios', description: 'Pode visualizar relat√≥rios b√°sicos', category: 'Relat√≥rios', icon: BarChart3 },
  { id: 'reports.advanced', name: 'Relat√≥rios Avan√ßados', description: 'Pode visualizar todos os relat√≥rios', category: 'Relat√≥rios', icon: BarChart3 },

  // Sistema
  { id: 'system.admin', name: 'Administra√ß√£o Sistema', description: 'Acesso completo √†s configura√ß√µes do sistema', category: 'Sistema', icon: Cog },
  { id: 'system.roles', name: 'Gerenciar Fun√ß√µes', description: 'Pode gerenciar fun√ß√µes e permiss√µes', category: 'Sistema', icon: Shield },
];

const DEFAULT_ROLES: Role[] = [
  {
    id: 'administrador',
    name: 'Administrador',
    description: 'Acesso completo ao sistema com todas as permiss√µes',
    color: 'bg-purple-100 text-purple-800',
    permissions: AVAILABLE_PERMISSIONS.map(p => p.id),
    isSystem: true
  },
  {
    id: 'supervisor',
    name: 'Supervisor',
    description: 'Gerencia equipes e tem acesso a relat√≥rios departamentais',
    color: 'bg-blue-100 text-blue-800',
    permissions: [
      'tickets.create', 'tickets.view_all', 'tickets.edit', 'tickets.assign',
      'users.view', 'users.edit', 'departments.view', 'reports.view', 'reports.advanced'
    ],
    isSystem: true
  },
  {
    id: 'colaborador',
    name: 'Colaborador',
    description: 'Acesso b√°sico para cria√ß√£o e atendimento de tickets',
    color: 'bg-green-100 text-green-800',
    permissions: [
      'tickets.create', 'tickets.view_own', 'tickets.edit'
    ],
    isSystem: true
  }
];

export default function RolesManagement() {
  const [roles, setRoles] = useState<Role[]>(DEFAULT_ROLES);
  const [isCreateModalOpen, setIsCreateModalOpen] = useState(false);
  const [isEditModalOpen, setIsEditModalOpen] = useState(false);
  const [editingRole, setEditingRole] = useState<Role | null>(null);
  const [newRole, setNewRole] = useState<Partial<Role>>({
    name: '',
    description: '',
    color: 'bg-slate-100 text-slate-800',
    permissions: []
  });

  const { toast } = useToast();
  const queryClient = useQueryClient();

  // Buscar usu√°rios para contagem por fun√ß√£o
  const { data: users = [] } = useQuery({
    queryKey: ['/api/users'],
    refetchInterval: 30000,
  }) as { data: any[] };

  useEffect(() => {
    setRoles(prevRoles => 
      prevRoles.map(role => ({
        ...role,
        userCount: users.filter((user: any) => user.role === role.id).length
      }))
    );
  }, [users]);

  const handleCreateRole = async () => {
    if (!newRole.name || !newRole.description) {
      toast({
        title: "Erro de Valida√ß√£o",
        description: "Nome e descri√ß√£o s√£o obrigat√≥rios",
        variant: "destructive",
      });
      return;
    }

    const roleToCreate: Role = {
      id: newRole.name!.toLowerCase().replace(/\s+/g, '_'),
      name: newRole.name!,
      description: newRole.description!,
      color: newRole.color || 'bg-slate-100 text-slate-800',
      permissions: newRole.permissions || [],
      userCount: 0,
      isSystem: false
    };

    setRoles(prev => [...prev, roleToCreate]);
    setIsCreateModalOpen(false);
    setNewRole({ name: '', description: '', color: 'bg-slate-100 text-slate-800', permissions: [] });

    toast({
      title: "Sucesso",
      description: "Fun√ß√£o criada com sucesso",
    });
  };

  const handleEditRole = (role: Role) => {
    setEditingRole({ ...role });
    setIsEditModalOpen(true);
  };

  const handleUpdateRole = async () => {
    if (!editingRole) return;

    setRoles(prev => prev.map(role => 
      role.id === editingRole.id ? editingRole : role
    ));

    setIsEditModalOpen(false);
    setEditingRole(null);

    toast({
      title: "Sucesso",
      description: "Fun√ß√£o atualizada com sucesso",
    });
  };

  const handleDeleteRole = async (roleId: string) => {
    const role = roles.find(r => r.id === roleId);
    if (!role) return;

    if (role.isSystem) {
      toast({
        title: "Opera√ß√£o N√£o Permitida",
        description: "Fun√ß√µes do sistema n√£o podem ser deletadas",
        variant: "destructive",
      });
      return;
    }

    const userCount = users.filter((user: any) => user.role === roleId).length;
    if (userCount > 0) {
      toast({
        title: "Opera√ß√£o N√£o Permitida",
        description: `N√£o √© poss√≠vel deletar uma fun√ß√£o com ${userCount} usu√°rio(s) associado(s)`,
        variant: "destructive",
      });
      return;
    }

    const confirmDelete = window.confirm(
      `Tem certeza que deseja deletar a fun√ß√£o "${role.name}"? Esta a√ß√£o n√£o pode ser desfeita.`
    );

    if (!confirmDelete) return;

    setRoles(prev => prev.filter(r => r.id !== roleId));

    toast({
      title: "Sucesso",
      description: "Fun√ß√£o deletada com sucesso",
    });
  };

  const handlePermissionToggle = (permissionId: string, isCreate = false) => {
    const updatePermissions = (current: string[]) => {
      const hasPermission = current.includes(permissionId);
      return hasPermission 
        ? current.filter(p => p !== permissionId)
        : [...current, permissionId];
    };

    if (isCreate) {
      setNewRole(prev => ({
        ...prev,
        permissions: updatePermissions(prev.permissions || [])
      }));
    } else if (editingRole) {
      setEditingRole(prev => prev ? ({
        ...prev,
        permissions: updatePermissions(prev.permissions)
      }) : null);
    }
  };

  const getPermissionsByCategory = () => {
    const grouped: { [key: string]: Permission[] } = {};
    AVAILABLE_PERMISSIONS.forEach(permission => {
      if (!grouped[permission.category]) {
        grouped[permission.category] = [];
      }
      grouped[permission.category].push(permission);
    });
    return grouped;
  };

  const getCategoryIcon = (category: string) => {
    switch (category) {
      case 'Tickets': return FileText;
      case 'Usu√°rios': return Users;
      case 'Departamentos': return Building2;
      case 'Relat√≥rios': return BarChart3;
      case 'Sistema': return Cog;
      default: return Settings;
    }
  };

  return (
    <AdminOnly>
      <div className="p-6 space-y-6">
        {/* Header */}
        <div className="flex items-center justify-between">
          <div>
            <h1 className="text-3xl font-bold text-opus-blue-dark">Configura√ß√£o de Fun√ß√µes</h1>
            <p className="text-gray-600 dark:text-gray-400 mt-1">
              Gerencie fun√ß√µes e permiss√µes do sistema de usu√°rios
            </p>
          </div>
          <Button 
            onClick={() => setIsCreateModalOpen(true)}
            className="bg-opus-blue-dark hover:bg-opus-blue-light"
          >
            <Plus className="w-4 h-4 mr-2" />
            Nova Fun√ß√£o
          </Button>
        </div>

        {/* Lista de Fun√ß√µes */}
        <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-6">
          {roles.map((role) => (
            <Card key={role.id} className="relative">
              <CardHeader>
                <div className="flex items-center justify-between">
                  <div className="flex items-center gap-3">
                    <div className="p-2 rounded-lg bg-gray-100 dark:bg-gray-800">
                      <Shield className="w-5 h-5 text-opus-blue-dark" />
                    </div>
                    <div>
                      <CardTitle className="text-lg">{role.name}</CardTitle>
                      <Badge className={role.color}>
                        {role.userCount || 0} usu√°rio(s)
                      </Badge>
                    </div>
                  </div>
                  {!role.isSystem && (
                    <div className="flex gap-1">
                      <Button
                        size="sm"
                        variant="ghost"
                        onClick={() => handleEditRole(role)}
                      >
                        <Edit className="w-4 h-4" />
                      </Button>
                      <Button
                        size="sm"
                        variant="ghost"
                        onClick={() => handleDeleteRole(role.id)}
                      >
                        <Trash2 className="w-4 h-4" />
                      </Button>
                    </div>
                  )}
                </div>
              </CardHeader>
              <CardContent>
                <CardDescription className="mb-4">
                  {role.description}
                </CardDescription>
                <div className="space-y-2">
                  <p className="text-sm font-medium text-gray-700 dark:text-gray-300">
                    Permiss√µes: {role.permissions.length}
                  </p>
                  <div className="flex flex-wrap gap-1">
                    {role.permissions.slice(0, 3).map(permissionId => {
                      const permission = AVAILABLE_PERMISSIONS.find(p => p.id === permissionId);
                      return permission ? (
                        <Badge key={permissionId} variant="secondary" className="text-xs">
                          {permission.name}
                        </Badge>
                      ) : null;
                    })}
                    {role.permissions.length > 3 && (
                      <Badge variant="secondary" className="text-xs">
                        +{role.permissions.length - 3} mais
                      </Badge>
                    )}
                  </div>
                </div>
                {role.isSystem && (
                  <Badge variant="outline" className="mt-2">
                    Fun√ß√£o do Sistema
                  </Badge>
                )}
              </CardContent>
            </Card>
          ))}
        </div>

        {/* Modal de Cria√ß√£o */}
        <Dialog open={isCreateModalOpen} onOpenChange={setIsCreateModalOpen}>
          <DialogContent className="max-w-4xl max-h-[90vh] overflow-y-auto">
            <DialogHeader>
              <DialogTitle>Criar Nova Fun√ß√£o</DialogTitle>
              <DialogDescription>
                Defina o nome, descri√ß√£o e permiss√µes para a nova fun√ß√£o
              </DialogDescription>
            </DialogHeader>

            <div className="space-y-6">
              <div className="grid grid-cols-2 gap-4">
                <div>
                  <Label htmlFor="name">Nome da Fun√ß√£o</Label>
                  <Input
                    id="name"
                    value={newRole.name || ''}
                    onChange={(e) => setNewRole(prev => ({ ...prev, name: e.target.value }))}
                    placeholder="Ex: Analista, Operador..."
                  />
                </div>
                <div>
                  <Label htmlFor="color">Cor</Label>
                  <select
                    id="color"
                    value={newRole.color}
                    onChange={(e) => setNewRole(prev => ({ ...prev, color: e.target.value }))}
                    className="w-full px-3 py-2 border rounded-md"
                  >
                    <option value="bg-slate-100 text-slate-800">Cinza</option>
                    <option value="bg-blue-100 text-blue-800">Azul</option>
                    <option value="bg-green-100 text-green-800">Verde</option>
                    <option value="bg-yellow-100 text-yellow-800">Amarelo</option>
                    <option value="bg-purple-100 text-purple-800">Roxo</option>
                    <option value="bg-red-100 text-red-800">Vermelho</option>
                  </select>
                </div>
              </div>

              <div>
                <Label htmlFor="description">Descri√ß√£o</Label>
                <Textarea
                  id="description"
                  value={newRole.description || ''}
                  onChange={(e) => setNewRole(prev => ({ ...prev, description: e.target.value }))}
                  placeholder="Descreva as responsabilidades desta fun√ß√£o..."
                />
              </div>

              <div>
                <Label>Permiss√µes</Label>
                <div className="mt-2 space-y-4">
                  {Object.entries(getPermissionsByCategory()).map(([category, permissions]) => {
                    const Icon = getCategoryIcon(category);
                    return (
                      <div key={category} className="border rounded-lg p-4">
                        <div className="flex items-center gap-2 mb-3">
                          <Icon className="w-5 h-5 text-opus-blue-dark" />
                          <h4 className="font-semibold">{category}</h4>
                        </div>
                        <div className="grid grid-cols-1 md:grid-cols-2 gap-3">
                          {permissions.map((permission) => (
                            <div key={permission.id} className="flex items-center space-x-2">
                              <Checkbox
                                id={`new-${permission.id}`}
                                checked={newRole.permissions?.includes(permission.id)}
                                onCheckedChange={() => handlePermissionToggle(permission.id, true)}
                              />
                              <Label htmlFor={`new-${permission.id}`} className="text-sm">
                                {permission.name}
                                <p className="text-xs text-gray-500">{permission.description}</p>
                              </Label>
                            </div>
                          ))}
                        </div>
                      </div>
                    );
                  })}
                </div>
              </div>
            </div>

            <DialogFooter>
              <Button variant="outline" onClick={() => setIsCreateModalOpen(false)}>
                Cancelar
              </Button>
              <Button onClick={handleCreateRole} className="bg-opus-blue-dark hover:bg-opus-blue-light">
                Criar Fun√ß√£o
              </Button>
            </DialogFooter>
          </DialogContent>
        </Dialog>

        {/* Modal de Edi√ß√£o */}
        <Dialog open={isEditModalOpen} onOpenChange={setIsEditModalOpen}>
          <DialogContent className="max-w-4xl max-h-[90vh] overflow-y-auto">
            <DialogHeader>
              <DialogTitle>Editar Fun√ß√£o: {editingRole?.name}</DialogTitle>
              <DialogDescription>
                Modifique as permiss√µes e configura√ß√µes da fun√ß√£o
              </DialogDescription>
            </DialogHeader>

            {editingRole && (
              <div className="space-y-6">
                <div className="grid grid-cols-2 gap-4">
                  <div>
                    <Label htmlFor="edit-name">Nome da Fun√ß√£o</Label>
                    <Input
                      id="edit-name"
                      value={editingRole.name}
                      onChange={(e) => setEditingRole(prev => prev ? ({ ...prev, name: e.target.value }) : null)}
                      disabled={editingRole.isSystem}
                    />
                  </div>
                  <div>
                    <Label htmlFor="edit-color">Cor</Label>
                    <select
                      id="edit-color"
                      value={editingRole.color}
                      onChange={(e) => setEditingRole(prev => prev ? ({ ...prev, color: e.target.value }) : null)}
                      className="w-full px-3 py-2 border rounded-md"
                      disabled={editingRole.isSystem}
                    >
                      <option value="bg-slate-100 text-slate-800">Cinza</option>
                      <option value="bg-blue-100 text-blue-800">Azul</option>
                      <option value="bg-green-100 text-green-800">Verde</option>
                      <option value="bg-yellow-100 text-yellow-800">Amarelo</option>
                      <option value="bg-purple-100 text-purple-800">Roxo</option>
                      <option value="bg-red-100 text-red-800">Vermelho</option>
                    </select>
                  </div>
                </div>

                <div>
                  <Label htmlFor="edit-description">Descri√ß√£o</Label>
                  <Textarea
                    id="edit-description"
                    value={editingRole.description}
                    onChange={(e) => setEditingRole(prev => prev ? ({ ...prev, description: e.target.value }) : null)}
                    disabled={editingRole.isSystem}
                  />
                </div>

                <div>
                  <Label>Permiss√µes</Label>
                  <div className="mt-2 space-y-4">
                    {Object.entries(getPermissionsByCategory()).map(([category, permissions]) => {
                      const Icon = getCategoryIcon(category);
                      return (
                        <div key={category} className="border rounded-lg p-4">
                          <div className="flex items-center gap-2 mb-3">
                            <Icon className="w-5 h-5 text-opus-blue-dark" />
                            <h4 className="font-semibold">{category}</h4>
                          </div>
                          <div className="grid grid-cols-1 md:grid-cols-2 gap-3">
                            {permissions.map((permission) => (
                              <div key={permission.id} className="flex items-center space-x-2">
                                <Checkbox
                                  id={`edit-${permission.id}`}
                                  checked={editingRole.permissions.includes(permission.id)}
                                  onCheckedChange={() => handlePermissionToggle(permission.id, false)}
                                />
                                <Label htmlFor={`edit-${permission.id}`} className="text-sm">
                                  {permission.name}
                                  <p className="text-xs text-gray-500">{permission.description}</p>
                                </Label>
                              </div>
                            ))}
                          </div>
                        </div>
                      );
                    })}
                  </div>
                </div>
              </div>
            )}

            <DialogFooter>
              <Button variant="outline" onClick={() => setIsEditModalOpen(false)}>
                Cancelar
              </Button>
              <Button onClick={handleUpdateRole} className="bg-opus-blue-dark hover:bg-opus-blue-light">
                Salvar Altera√ß√µes
              </Button>
            </DialogFooter>
          </DialogContent>
        </Dialog>
      </div>
    </AdminOnly>
  );
}

