interface UserSecurityModalProps {
  isOpen: boolean;
  onClose: () => void;
  user: any;
  onUpdate: (userId: string, securityData: any) => void;
}

const UserSecurityModal: React.FC<UserSecurityModalProps> = ({
  isOpen,
