  const [showConfirmPassword, setShowConfirmPassword] = useState(false);
  const [isLoading, setIsLoading] = useState(false);
  const [forcePasswordChange, setForcePasswordChange] = useState(false);
  const { toast } = useToast();

  const handleSubmit = async (e: React.FormEvent) => {
    e.preventDefault();

    if (newPassword !== confirmPassword) {
      toast({
        title: "Erro de Valida√ß√£o",
        description: "As senhas n√£o coincidem",
        variant: "destructive",
      });
      return;
    }

    if (newPassword.length < 6) {
      toast({
        title: "Erro de Valida√ß√£o", 
        description: "A senha deve ter pelo menos 6 caracteres",
        variant: "destructive",
      });
      return;
    }

    setIsLoading(true);

    try {
      const securityData = {
        newPassword,
        forcePasswordChange
      };

      await onUpdate(user.id, securityData);

      toast({
        title: "Sucesso",
        description: "Configura√ß√µes de seguran√ßa atualizadas com sucesso",
      });

      setNewPassword('');
      setConfirmPassword('');
      setForcePasswordChange(false);
      onClose();

    } catch (error) {
      toast({
        title: "Erro",
        description: "Erro ao atualizar configura√ß√µes de seguran√ßa",
        variant: "destructive",
      });
    } finally {
      setIsLoading(false);
    }
  };

  const generateRandomPassword = () => {
    const chars = 'ABCDEFGHIJKLMNOPQRSTUVWXYZabcdefghijklmnopqrstuvwxyz0123456789!@#$%^&*';
    let password = '';
    for (let i = 0; i < 12; i++) {
      password += chars.charAt(Math.floor(Math.random() * chars.length));
    }
    setNewPassword(password);
    setConfirmPassword(password);

    // Copy to clipboard
    navigator.clipboard.writeText(password).then(() => {
      toast({
        title: "Senha Gerada",
        description: "Nova senha gerada e copiada para a √°rea de transfer√™ncia",
      });
    });
  };

  return (
    <Dialog open={isOpen} onOpenChange={onClose}>
      <DialogContent className="max-w-2xl max-h-[90vh] overflow-y-auto">
