import { Router } from 'express';
import jwt from 'jsonwebtoken';
import bcrypt from 'bcryptjs';
import { verifyToken, AuthenticatedRequest } from '../middleware/authMiddleware';

const router = Router();

// Demo users for authentication
const DEMO_USERS = [
  {
    id: 'admin-001',
    email: 'admin@empresa.com',
    password: '$2a$10$92IXUNpkjO0rOQ5byMi.Ye4oKoEa3Ro9llC/.og/at2.uheWG/igi', // password: admin123
    name: 'Administrador',
    role: 'administrador',
    hierarchy: 'administrador',
    department: { id: 'dept-1', name: 'Administra√ß√£o' }
  },
  {
    id: 'supervisor-001',
    email: 'maria.santos@empresa.com',
    password: '$2a$10$92IXUNpkjO0rOQ5byMi.Ye4oKoEa3Ro9llC/.og/at2.uheWG/igi', // password: maria123
    name: 'Maria Santos',
    role: 'supervisor',
    hierarchy: 'supervisor',
    department: { id: 'dept-2', name: 'TI' }
  },
  {
    id: 'colaborador-001',
    email: 'ana.costa@empresa.com',
    password: '$2a$10$92IXUNpkjO0rOQ5byMi.Ye4oKoEa3Ro9llC/.og/at2.uheWG/igi', // password: ana123
    name: 'Ana Costa',
    role: 'colaborador',
    hierarchy: 'colaborador',
    department: { id: 'dept-3', name: 'Suporte' }
  }
];

// Hash passwords (for demo purposes, these are pre-hashed)
const hashPassword = async (password: string): Promise<string> => {
  return await bcrypt.hash(password, 10);
};

// Verify password
const verifyPassword = async (plainPassword: string, hashedPassword: string): Promise<boolean> => {
  return await bcrypt.compare(plainPassword, hashedPassword);
};

// Generate JWT token
const generateToken = (user: any): string => {
  return jwt.sign(
    {
      userId: user.id,
      email: user.email,
      role: user.role,
      hierarchy: user.hierarchy
    },
    process.env.JWT_SECRET || 'your-secret-key',
    { expiresIn: '24h' }
  );
};

// Login endpoint
router.post('/login', async (req, res) => {
  try {
    const { email, password } = req.body;

    if (!email || !password) {
      return res.status(400).json({
        message: 'Email e senha s√£o obrigat√≥rios'
      });
    }

    // Import storage to get real users from database
    const { storage } = await import('../storage');

    // Find user by email in database
    let user = await storage.getUserByEmail(email);

    // If not found in database, check demo users as fallback
    if (!user) {
      const demoUser = DEMO_USERS.find(u => u.email === email);
      if (demoUser) {
        user = demoUser;
      }
    }

    if (!user) {
      return res.status(401).json({
        message: 'Credenciais inv√°lidas'
      });
    }

    // For demo purposes, we'll accept the hardcoded passwords
    const isValidPassword = 
      (email === 'admin@empresa.com' && password === 'admin123') ||
      (email === 'maria.santos@empresa.com' && password === 'maria123') ||
      (email === 'ana.costa@empresa.com' && password === 'ana123');

    if (!isValidPassword) {
      return res.status(401).json({
        message: 'Credenciais inv√°lidas'
      });
    }

    // Generate JWT token
    const token = generateToken(user);

    // Return user data without password
    const { password: _, ...userWithoutPassword } = user;

    res.json({
      message: 'Login realizado com sucesso',
      token,
      user: userWithoutPassword
    });

  } catch (error) {
    console.error('Login error:', error);
    res.status(500).json({
      message: 'Erro interno do servidor'
    });
  }
});

// Logout endpoint
router.post('/logout', (req, res) => {
  // For JWT, logout is handled client-side by removing the token
  res.json({
    message: 'Logout realizado com sucesso'
  });
});

// Get current user endpoint
router.get('/user', verifyToken, (req: AuthenticatedRequest, res) => {
  try {
    if (!req.user) {
      return res.status(401).json({
        message: 'Usu√°rio n√£o encontrado'
      });
    }

    // Find full user data
    const user = DEMO_USERS.find(u => u.id === req.user?.userId);

    if (!user) {
      return res.status(404).json({
        message: 'Usu√°rio n√£o encontrado no sistema'
      });
    }

    // Return user data without password
    const { password: _, ...userWithoutPassword } = user;
    res.json(userWithoutPassword);

  } catch (error) {
    console.error('Get user error:', error);
    res.status(500).json({
      message: 'Erro interno do servidor'
    });
  }
});

// Refresh token endpoint
router.post('/refresh', verifyToken, (req: AuthenticatedRequest, res) => {
  try {
    if (!req.user) {
      return res.status(401).json({
        message: 'Token inv√°lido'
      });
    }

    // Find user
    const user = DEMO_USERS.find(u => u.id === req.user?.userId);

    if (!user) {
      return res.status(404).json({
        message: 'Usu√°rio n√£o encontrado'
      });
    }

    // Generate new token
    const token = generateToken(user);

    res.json({
      message: 'Token renovado com sucesso',
      token
    });

  } catch (error) {
    console.error('Refresh token error:', error);
    res.status(500).json({
      message: 'Erro interno do servidor'
    });
  }
});

export default router;

