  onClose: () => void;
}

export default function UserDetailsModal({ userId, isOpen, onClose }: UserDetailsModalProps) {
  const { toast } = useToast();
  const queryClient = useQueryClient();
  const [isEditing, setIsEditing] = useState(false);
  const [activeTab, setActiveTab] = useState('informacoes');

  // Fetch user details
  const { data: user, isLoading } = useQuery({
    queryKey: [`/api/users/${userId}`],
    enabled: !!userId && isOpen,
  });

  // Fetch departments for dropdown
  const { data: departments = [] } = useQuery({
    queryKey: ["/api/departments"],
  });

  // Fetch user performance data
  const { data: performance } = useQuery({
    queryKey: [`/api/users/${userId}/performance`],
    enabled: !!userId && isOpen,
  });

  // Fetch user activity logs
  const { data: activities = [] } = useQuery({
    queryKey: [`/api/users/${userId}/activities`],
    enabled: !!userId && isOpen,
  });

  // Fetch user permissions
  const { data: userPermissions } = useQuery({
    queryKey: [`/api/users/${userId}/permissions`],
    enabled: !!userId && isOpen,
  });

  const [formData, setFormData] = useState({
    name: '',
    email: '',
    cpf: '',
    phone: '',
    role: '',
    departmentId: '',
    location: '',
    extension: ''
  });

  React.useEffect(() => {
    if (user) {
      setFormData({
        name: user.name || '',
        email: user.email || '',
        cpf: user.cpf || '',
        phone: user.phone || '',
        role: user.role || '',
        departmentId: user.departmentId || '',
        location: user.location || '',
        extension: user.extension || ''
      });
    }
  }, [user]);

  // Update user mutation
  const updateUserMutation = useMutation({
    mutationFn: async (data: any) => {
      return apiRequest(`/api/users/${userId}`, "PATCH", data);
    },
    onSuccess: () => {
      queryClient.invalidateQueries({ queryKey: ["/api/users"] });
      queryClient.invalidateQueries({ queryKey: [`/api/users/${userId}`] });
      setIsEditing(false);
      toast({
        title: "Usu√°rio atualizado",
        description: "Os dados do usu√°rio foram atualizados com sucesso.",
      });
    },
    onError: (error: any) => {
      toast({
        title: "Erro",
        description: error.message || "Erro ao atualizar usu√°rio",
        variant: "destructive",
      });
    },
  });

  const handleSave = () => {
    updateUserMutation.mutate(formData);
  };

  const handleCancel = () => {
    if (user) {
      setFormData({
        name: user.name || '',
        email: user.email || '',
        cpf: user.cpf || '',
        phone: user.phone || '',
        role: user.role || '',
        departmentId: user.departmentId || '',
        location: user.location || '',
        extension: user.extension || ''
      });
    }
    setIsEditing(false);
  };

  const getRoleLabel = (role: string) => {
    switch (role) {
      case 'admin': return 'Administrador';
      case 'supervisor': return 'Supervisor';
      case 'colaborador': return 'Colaborador';
      default: return role;
    }
  };

  const getRoleBadgeVariant = (role: string): "default" | "secondary" | "destructive" | "outline" => {
    switch (role) {
      case 'admin': return 'destructive';
      case 'supervisor': return 'default';
      case 'colaborador': return 'secondary';
      default: return 'outline';
    }
  };

  const formatDate = (dateString: string | null) => {
    if (!dateString) return 'Nunca';
    return new Date(dateString).toLocaleDateString('pt-BR', {
      day: '2-digit',
      month: '2-digit',
      year: 'numeric',
      hour: '2-digit',
      minute: '2-digit'
    });
  };

  const getDepartmentName = (departmentId: string) => {
    const dept = departments.find((d: any) => d.id === departmentId);
    return dept?.name || 'N√£o definido';
  };

  if (!userId || !isOpen) return null;

  if (isLoading) {
    return (
      <Dialog open={isOpen} onOpenChange={onClose}>
        <DialogContent className="max-w-4xl max-h-[90vh] overflow-y-auto">
          <div className="flex items-center justify-center h-64">
            <div className="animate-spin rounded-full h-8 w-8 border-b-2 border-primary"></div>
          </div>
        </DialogContent>
      </Dialog>
    );
  }

  return (
    <Dialog open={isOpen} onOpenChange={onClose}>
      <DialogContent className="max-w-5xl max-h-[90vh] overflow-y-auto">
        <DialogHeader className="pb-6">
          <div className="flex items-center justify-between">
            <div className="flex items-center space-x-4">
              <Avatar className="w-16 h-16">
                <AvatarFallback className="bg-gradient-to-br from-[#2c4257] to-[#6b8fb0] text-white text-xl">
                  {user?.name?.split(' ').map((n: string) => n[0]).join('') || 'U'}
                </AvatarFallback>
              </Avatar>
              <div>
                <DialogTitle className="text-2xl">{user?.name}</DialogTitle>
                <div className="flex items-center space-x-3 mt-2">
                  <Badge variant={getRoleBadgeVariant(user?.role)}>
                    {getRoleLabel(user?.role)}
                  </Badge>
                  {user?.isBlocked ? (
                    <Badge variant="outline" className="text-red-600 border-red-200">
                      <UserX className="w-3 h-3 mr-1" />
                      Bloqueado
                    </Badge>
                  ) : user?.isActive ? (
                    <Badge variant="outline" className="text-green-600 border-green-200">
                      <UserCheck className="w-3 h-3 mr-1" />
                      Ativo
                    </Badge>
                  ) : (
