                <CardContent>
                  <div className="text-2xl font-bold text-blue-600">
                    {performance?.resolutionRate || 0}%
                  </div>
                  <p className="text-xs text-muted-foreground">Efici√™ncia</p>
                </CardContent>
              </Card>
            </div>

            <Card>
              <CardHeader>
                <CardTitle>M√©tricas de Performance</CardTitle>
              </CardHeader>
              <CardContent>
                <div className="space-y-4">
                  <div className="flex justify-between items-center">
                    <span className="text-sm font-medium">Tempo M√©dio de Resolu√ß√£o</span>
                    <span className="text-sm">{performance?.averageResolutionTime || 'N/A'}</span>
                  </div>
                  <div className="flex justify-between items-center">
                    <span className="text-sm font-medium">Avalia√ß√£o de Satisfa√ß√£o</span>
                    <span className="text-sm">{performance?.satisfactionRating || 'N/A'}</span>
                  </div>
                  <div className="flex justify-between items-center">
                    <span className="text-sm font-medium">Tickets em Andamento</span>
                    <span className="text-sm">{performance?.openTickets || 0}</span>
                  </div>
                </div>
              </CardContent>
            </Card>
          </TabsContent>

          {/* Atividade Tab */}
          <TabsContent value="atividade" className="space-y-4">
            <Card>
              <CardHeader>
                <CardTitle>Hist√≥rico de Atividades</CardTitle>
              </CardHeader>
              <CardContent>
                <div className="space-y-4">
                  {activities.length > 0 ? (
                    activities.map((activity: any, index: number) => (
                      <div key={index} className="flex items-start space-x-3 p-3 border rounded-lg">
                        <Activity className="w-5 h-5 text-muted-foreground mt-0.5" />
