          </TabsContent>

          {/* Permiss√µes Tab */}
          <TabsContent value="permissoes" className="space-y-4">
            <Card>
              <CardHeader>
                <CardTitle>Permiss√µes do Sistema</CardTitle>
                <p className="text-sm text-muted-foreground">
                  As permiss√µes s√£o definidas pela fun√ß√£o do usu√°rio: <strong>{getRoleLabel(user?.role)}</strong>
                </p>
              </CardHeader>
              <CardContent>
                {userPermissions ? (
                  <div className="space-y-4">
                    {Object.entries(userPermissions).map(([category, permissions]: [string, any]) => (
                      <Card key={category}>
                        <CardHeader className="pb-3">
                          <CardTitle className="text-base">{category}</CardTitle>
                        </CardHeader>
                        <CardContent className="pt-0">
                          <div className="grid grid-cols-2 gap-3">
                            {Object.entries(permissions).map(([permission, hasPermission]: [string, any]) => (
                              <div key={permission} className="flex items-center space-x-3">
                                <div className={`w-3 h-3 rounded-full ${hasPermission ? 'bg-green-500' : 'bg-gray-300'}`} />
                                <span className="text-sm">{permission}</span>
                              </div>
                            ))}
                          </div>
                        </CardContent>
                      </Card>
                    ))}
                  </div>
                ) : (
                  <div className="text-center py-8">
                    <Shield className="w-12 h-12 mx-auto text-gray-400 mb-4" />
                    <p className="text-gray-500">Carregando permiss√µes...</p>
                  </div>
                )}
              </CardContent>
            </Card>
          </TabsContent>
        </Tabs>
      </DialogContent>
    </Dialog>
  );
}

